export default function SupersetComponent() {

    const [accessToken, setAccessToken] = useState(null);

    useEffect(() => {
        fetchSupersetGusetToken().then(res => {
            console.log(res.data);
            setAccessToken(res.data);
        })
    }, []);

    /* const fetchGuestTokenFromBackend = () => {
