import React from "react";
import './styles.css';
import { useParams } from "react-router-dom";
import { useHistory } from "react-router";
import TransporterPersonalInfoDisplay from './TransporterPersonalInfoDisplay';
import ClientPersonalInfoDisplay from './ClientPersonalInfoDisplay';

export default function CustomerInfo() {

    const history = useHistory();

    const { id, type } = useParams();

    return (
        <div style={{ marginTop: "50px", height: "95%" }}>
            <div onClick={() => history.goBack()} className="d-flex justify-content-start backBtn">
                <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="currentColor" className="bi bi-caret-left-fill" viewBox="0 0 16 16" >
                    <path d="m3.86 8.753 5.482 4.796c.646.566 1.658.106 1.658-.753V3.204a1 1 0 0 0-1.659-.753l-5.48 4.796a1 1 0 0 0 0 1.506z" />
                </svg>
                <span>Back</span>
            </div>

            {/* upper background */}
            <div className="upperBackground">
            </div>

            <div className="mainContainer">
                {type === "1" ? <TransporterPersonalInfoDisplay id={id} /> : <ClientPersonalInfoDisplay id={id} />}
            </div>
        </div>
    )
}
