import React, {Component, useState} from "react";
import Address from "../Address/Address";
import Accordion from "react-bootstrap/Accordion";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Table from "react-bootstrap/Table";

import {FinalizeOrder} from "../OrdersManager/FinalizeOrder";
import {CancelOrder} from "../OrdersManager/CancelOrder";
import {AcceptOrder} from "../OrdersManager/AcceptOrder";
import {DeleteTripCost} from "../OrdersManager/DeleteTripCost";
import {EditTripCost} from "../OrdersManager/EditTripCost";
import Loader from "../Loader/Loader";
import "./OrderDetails.css";
import {getOrderDetails, checkTripCost} from "../APIs/OrdersAPIs";
import translate from "../../i18n/translate";

import "leaflet/dist/leaflet.css";
import { MapContainer, Marker, Popup, TileLayer } from "react-leaflet";
import L from 'leaflet';
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';
import iconc from "./map-marker (1).png"
import { Alert, AlertDescription, AlertIcon, AlertTitle } from "@chakra-ui/alert";
import { Box } from "@chakra-ui/layout";
import { deliverFormatter, packageFormatter } from "../Orders/OrdersTabularView";
import { isTransporterMaster } from "../../Util";
import AssignToTransporter from "../AssigneToTransporter";
let DefaultIcon = L.icon({
    iconUrl: icon,
    shadowUrl: iconShadow
});

let custome = L.icon({iconUrl: iconc})

L.Marker.prototype.options.icon = DefaultIcon;

class OrderDetails extends Component {
    state = {
        orderId: this.props.match.params.id,
        orderDetails: {},
        previousPage: null,
        tripCost: null,
        costSet: null,
    };

    componentDidMount = () => {
        checkTripCost(this.state.orderId).then((tripCost) => {
            if (tripCost && tripCost !== "CostNotSend") {
                this.setState({tripCost: tripCost.server_response[0].CostDelivery});
                this.setState({costSet: true});
            } else {
                this.setState({costSet: false});
            }
        });
        getOrderDetails(this.state.orderId).then((orderDetails) => {
            this.setState({orderDetails});
        });
        // this.setState({ orderDetails });
        this.setState({previousPage: this.props.location.state?.currentPage});
    };

    render() {
        if (typeof this.state.orderDetails === "object" && JSON.stringify(this.state.orderDetails) !== '{}') {
            let {
                deliveryWay,
                HeightLoad,
                DetailsLoad,
                LengthLoad,
                WidthLoad,
                IdCitySource,
                IdCityDes,
                NameNeighborhood,
                NameStreet,
                NameBuilding,
                FloorNumbers,
                ApartmentNumber,
                OtherDetails,
                TypeLoad,
                NameNeighborhoodDes,
                NameStreetDes,
                NameBuildingDes,
                FloorNumbersDes,
                ApartmentNumberDes,
                OtherDetailsDes,
                village,
                villageDes,
                PhoneCustomer,
                ReceiverAddressNum,
                LongReciver, LongSender, LatReciver, LatSender
            } = this.state.orderDetails;

            let {costSet, previousPage, orderId, tripCost} = this.state;

            let senderAddress = {
                city: IdCitySource,
                village: village,
                neighborhood: NameNeighborhood,
                street: NameStreet,
                buildingName: NameBuilding,
                floor: FloorNumbers,
                apartmentNumber: ApartmentNumber,
                otherDetails: OtherDetails,
                phoneCustomer: PhoneCustomer
            };

            let receiverAddress = {
                city: IdCityDes,
                village: villageDes,
                neighborhood: NameNeighborhoodDes,
                street: NameStreetDes,
                buildingName: NameBuildingDes,
                floor: FloorNumbersDes,
                apartmentNumber: ApartmentNumberDes,
                otherDetails: OtherDetailsDes,
                phoneCustomer: ReceiverAddressNum
            };

            return (
                <div style={{marginRight: "10%", marginLeft: "10%", marginTop:"5%", overflowY: "auto"}}>
                    
                    {this.state.orderDetails && (
                        <div>
                            {LatSender !== undefined && 
                            <MapContainer center={[LatSender, LongSender]} 
                            zoom={15} 
                            style={{height: "250px"}}
                            onClick={(e)=>{
                                console.log(e)
                            }}
                            >
                                <TileLayer
                                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                                />
                                <Marker position={L.latLng(LatSender, LongSender)} icon={custome}>
                                    <Popup>
                                    {OtherDetails}
                                    </Popup>
                                </Marker>
                                <Marker position={L.latLng(LatReciver, LongReciver)} >
                                    <Popup>
                                        {OtherDetailsDes}    
                                    </Popup>
                                </Marker>
                                
                            </MapContainer>}
                            <Table bordered>
                                <tbody>
                                <tr>
                                    <td>{translate("ORDER_DETAILS.ORDER_NUMBER")}</td>
                                    <td>{orderId}</td>
                                </tr>
                                <tr>
                                    <td>{translate("ORDER_DETAILS.DELIVERY_TYPE")}</td>
                                    <td>{deliverFormatter({DeliveryWays: deliveryWay})}</td>
                                </tr>
                                <tr>
                                    <td>{translate("ORDER_DETAILS.PACKAGE_TYPE")}</td>
                                    <td>{packageFormatter({PackageType: TypeLoad})}</td>
                                </tr>
                                {TypeLoad === "2" && (
                                    <React.Fragment>
                                        <tr colSpan="3">
                                            <td>{translate("ORDER_DETAILS.LOAD_HEIGHT")}</td>
                                            <td>{HeightLoad}</td>
                                        </tr>
                                        <tr>
                                            <td>{translate("ORDER_DETAILS.LOAD_WIDTH")}</td>
                                            <td>{WidthLoad}</td>
                                        </tr>
                                        <tr>
                                            <td>{translate("ORDER_DETAILS.LOAD_LENGTH")}</td>
                                            <td>{LengthLoad}</td>
                                        </tr>
                                        <tr>
                                            <td>{translate("ORDER_DETAILS.LOAD_DETAILS")}</td>
                                            <td>{DetailsLoad}</td>
                                        </tr>
                                    </React.Fragment>
                                )}
                                <tr>
                                    <td colSpan="2" style={{padding: 0}}>
                                        <Accordion defaultActiveKey="0">
                                            <Card bg="light" className="rounded-0 border-0">
                                                <Card.Header className="p-0">
                                                    <Accordion.Toggle
                                                        as={Button}
                                                        variant="link"
                                                        eventKey="0"
                                                        className="w-100 shadow-none rounded-0"
                                                    >
                                                        {translate("ORDER_DETAILS.SENDER_ADDRESS")}
                                                    </Accordion.Toggle>
                                                </Card.Header>
                                                <Accordion.Collapse eventKey="0">
                                                    <Card.Body>
                                                        <Address address={senderAddress}/>
                                                    </Card.Body>
                                                </Accordion.Collapse>
                                            </Card>
                                        </Accordion>
                                        <Accordion defaultActiveKey="1">
                                            <Card bg="light" className="rounded-0 border-0">
                                                <Card.Header className="p-0">
                                                    <Accordion.Toggle
                                                        as={Button}
                                                        variant="link"
                                                        eventKey="1"
                                                        className="w-100 shadow-none rounded-0"
                                                    >
                                                        {translate("ORDER_DETAILS.RECEIVER_ADDRESS")}
                                                    </Accordion.Toggle>
                                                </Card.Header>
                                                <Accordion.Collapse eventKey="1">
                                                    <Card.Body>
                                                        <Address address={receiverAddress} style/>
                                                    </Card.Body>
                                                </Accordion.Collapse>
                                            </Card>
                                        </Accordion>
                                    </td>
                                </tr>
                                </tbody>
                            </Table>
                            <div>
                                {previousPage === "all-orders" && costSet && (
                                    <div>
                                        <Card
                                            bg={"Danger"}
                                            text={"black"}
                                        >
                                            <Card.Header>{translate("ORDER_DETAILS.BID_PRICE")}</Card.Header>
                                            <Card.Body>
                                                <Card.Title>{tripCost}</Card.Title>
                                                {/* <Card.Text></Card.Text> */}
                                            </Card.Body>
                                        </Card>
                                    </div>
                                )}
                                {previousPage === "all-orders" && costSet !== null && costSet && (
                                    <div className="ActionButtons">
                                        <DeleteTripCost orderId={orderId}/>
                                        <EditTripCost orderId={orderId} tripCost={tripCost}/>
                                    </div>
                                )}
                                {/* {previousPage === "all-orders" && costSet && <EditCost />} */}

                                {/* {previousPage === "all-orders" && costSet && <EditCost />} */}

                                {previousPage === "all-orders" &&
                                costSet !== null &&
                                !costSet && <AcceptOrder orderId={orderId}/>}

                                {previousPage === "current-orders" && (
                                    <Box display="flex" alignItems="center" justifyContent="end">
                                        {isTransporterMaster() && <AssignToTransporter orderId={orderId} />}
                                        {!isTransporterMaster() && 
                                        <>
                                            <FinalizeOrder orderId={orderId} />
                                            <CancelOrder orderId={orderId} className="ml-1" />
                                        </>
                                        }
                                    </Box>
                                )}
                            </div>
                        </div>
                    )}
                </div>
            );
        } else if(typeof this.state.orderDetails === "string") {
            return (
                <Alert status="error" w="80%" m="10px auto">
                    <AlertIcon />
                    <AlertTitle mr={2}>{this.state.orderId}</AlertTitle>
                    <AlertDescription>{this.state.orderDetails}</AlertDescription>
                    
                </Alert>
            );
        } else {
            return <Box height="400px"><Loader /></Box>
        }
    }
}

export default OrderDetails;
