import React, { useEffect, useState } from "react";
import { getTransporterCitiesPrices, updateTransporterCitiesPrices } from '../../APIs/ProfileAPIs';
import { Container, Row, Col, Form, Table, Card } from 'react-bootstrap';

export default function CitiesPrices() {

    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1.5rem"
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1.2rem"
        },
    }

    const [pricesArr, setPricesArr] = useState([]);

    useEffect(() => {
        getTransporterCitiesPrices().then((res) => {
            // console.log(res.data);
            setPricesArr(res.data.response);
        })
    }, [])

    return (
        <Container fluid className="p-5">
            <Row>
                <Col>
                    <Card className="shadow rounded-22">
                       {/*  <Card.Header style={styles.cardHeaderLg}>
                            Prices
                        </Card.Header> */}

                        <Card.Body /* className="mt-5" */>
                            <Table hover>
                                <thead>
                                    <tr>
                                        <th scope="col" style={{ width: "30%" }}>from</th>
                                        <th scope="col" style={{ width: "30%" }}>to</th>
                                        <th scope="col" style={{ width: "30%" }}>price</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {
                                        pricesArr.map((item, index) => {
                                            return <tr key={index}>
                                                <td>{item.fromName}</td>
                                                <td>{item.toName}</td>
                                                <td>
                                                    <Form.Group className="d-flex justify-content-center" controlId={index}>
                                                        <Form.Control style={{ width: "100px" }} type="number" placeholder={item.price} onChange={(e) => {
                                                            handleChange(item.id, e.target.value)
                                                        }} />
                                                    </Form.Group>
                                                </td>
                                            </tr>
                                        })
                                    }
                                </tbody>
                            </Table>
                        </Card.Body>
                    </Card>
                </Col>
            </Row>
        </Container>
    )
}
