import { motion } from "framer-motion";
import React from "react";
import { PackageTypesIcons } from "./CreateNewOrder"

export default function OrdersCard(props){
    return (
        <div style={{flex: 1}} className="d-flex flex-column bg-white me-3 mt-3 p-3 rounded-22 shadow">
            <p className="text-black">{props.title}</p>
            <div className="h-100 overflow-auto" style={{fontFamily: "system-ui"}}>
            {
                props?.list?.map((order) => {
                    return (
                        <motion.div 
                        initial={{ backgroundColor: "#fff" }}
                        whileHover={{backgroundColor: "#f0fff0" }} 
                        whileTap={{ scale: 0.95 }}
                        className="d-flex gap-3 align-items-center d-flex gap-3 text-nowrap mb-2">
                            <div 
                                style={{width: "45px", height: "45px", backgroundColor: "#A0F2CC"}} 
                                className="rounded-circle d-flex"
                            >
                                {React.createElement(PackageTypesIcons[order.TypeLoad], 
                                    {style: {width: "30px", height: "30px", margin: "auto"}})
                                }
                            </div>
                            <div style={{width: "35%"}}>
                                <div>From:{order.fromAddress}</div>
                                <div>ID {order.id}</div>
                            </div>
                            <div style={{width: "35%"}}>
                                <div>To {order.toAddress}</div>
                                <div>{order.DateLoad}</div>
                            </div>
                            <div>
                                {!order.isAcceptDelivery &&order.bidsCount == "0" && 
                                <div className="me-2 p-1 rounded" 
                                    style={{
                                        color: "#F2695C",
                                        backgroundColor: "#F4DDD8"
                                    }}>
                                        Waiting
                                    </div>
                                }
                                {!order.isAcceptDelivery && order.bidsCount != "0" && 
                                    <div 
                                    style={{
                                        color: "#6a5acd",
                                        backgroundColor: "#e6e6fa"
                                    }}
                                    className="me-2 p-1 rounded">
                                        {order.bidsCount} Bids
                                    </div>
                                }
                                
                                {!order.orderfinished && !order.pickup_date && props.title.indexOf("Active") != -1 &&
                                    <div 
                                    style={{
                                        color: "#20b2aa",
                                        backgroundColor: "#e7feff"
                                    }}
                                    className="me-2 p-1 rounded">
                                        Accepted
                                    </div>
                                }
                                
                                {!order.orderfinished && order.pickup_date && props.title.indexOf("Active") != -1 &&
                                    <div 
                                    style={{
                                        color: "#987456",
                                        backgroundColor: "#e3dac9"
                                    }}
                                    className="me-2 p-1 rounded">
                                        OnGoing
                                    </div>
                                }
                                
                            </div>
                        </motion.div>
                    )
                })
            }
            </div>
        </div>
    )
}
