import React from "react";
import "./AdminPanel.css"
import {
    GetAllActiveOrders,
    GetAllFinishedOrders,
    GetAllDeletedOrders,
    GetAllReturnedOrders,
    GetAllNewOrders,
    getTotalBalance,
    GetAllClientsNum,
    GetAllTransportersNum,
} from "../../APIs/AdminPanelApis";
import { useEffect, useState } from "react";
import OrdersCard from "./OrdersCard";
import 'bootstrap-icons/font/bootstrap-icons.css';

export default function AdminPanel() {

    const [newOrders, setNewOrders] = useState([]); // new orders
    const [activeOrders, setActiveOrders] = useState([]); // in-process orders
    const [finishedOrders, setFinishedOrders] = useState([]); // finished orders
    const [deletedOrders, setDeletedOrders] = useState([]); // deleted orders
    const [exceptionOrders, setExceptionOrders] = useState([]); // returned/stuck orders
    const [creditAmount, setCreditAmount] = useState(0); // total escrow
    const [totalEscrowBalance, setTotalEscrowBalance] = useState(0); // total in escrow (active money)
    const [totalClients, setTotalClinets] = useState(); // number of clients
    const [totalTransporters, setTotalTransporters] = useState(); // number of transporters

    // to display loading spinner
    const [newOrdersLoading, setNewOrdersLoading] = useState(false);
    const [activeOrdersLoading, setActiveOrdersLoading] = useState(false);
    const [finishedOrdersLoading, setFinishedOrdersLoading] = useState(false);
    const [exceptionOrdersLoading, setExceptionOrdersLoading] = useState(false);
    const [deletedOrdersLoading, setDeletedOrdersLoading] = useState(false);

    /* get total balance */
    useEffect(() => {
        getTotalBalance().then((res) => {

            setTotalEscrowBalance(res.data?.server_response?.data?.result.differ_balance);
            setCreditAmount(res.data?.server_response?.data?.result.credit_amount);
        });
    }, []);

    /* get all orders */
    useEffect(() => {
        setNewOrdersLoading(true);
        setActiveOrdersLoading(true);
        setFinishedOrdersLoading(true);
        setExceptionOrdersLoading(true);
        setDeletedOrdersLoading(true);

        // get all new orders
        GetAllNewOrders().then((res) => {
            setNewOrders(res.data.orders_list);

            setNewOrdersLoading(false);
        });

        // get all in-process orders
        GetAllActiveOrders().then((res) => {
            setActiveOrders(res.data.orders_list);

            setActiveOrdersLoading(false);
        });

        // get all stuck/returned orders
        GetAllReturnedOrders().then((res) => {
            setExceptionOrders(res.data.orders_list);

            setExceptionOrdersLoading(false);
        });

        // get all finished orders
        GetAllFinishedOrders().then((res) => {
            setFinishedOrders(res.data.orders_list)

            setFinishedOrdersLoading(false);
        })

        // get all deleted orders
        GetAllDeletedOrders().then((res) => {
            setDeletedOrders(res.data.orders_list)

            setDeletedOrdersLoading(false);
        })
    }, []);

    useEffect(() => {
        // get all transporters count
        GetAllTransportersNum().then((res) => {
            setTotalTransporters(res.data.NumberOfTransporters);
        });

        // get all clients count
        GetAllClientsNum().then((res) => {
            setTotalClinets(res.data.NumberOfClients);
        });
    }, []);

    return (
        <>
            <div className="statDiv">
                <div className="container-fluid h-100">
                    <div className='row h-100 mt-1'>
                        <div className='col rightBorder'>
                            <div className='row h-50'>
                                <div className='col'>Total Clients:</div>
                                <div className='col togo-dark-green'>{totalClients}</div>
                            </div>
                            <div className='row h-50'>
                                <div className='col'>Total Transporters:</div>
                                <div className='col togo-dark-green'>{totalTransporters}</div>
                            </div>
                        </div>
                        <div className='col'>
                            <div className='row h-50'>
                                <div className='col'>Total in Escrow:</div>
                                <div className='col togo-dark-green'>{totalEscrowBalance}{" NIS"}</div>
                            </div>
                            <div className='row h-50'>
                                <div className='col'>Total Escrow:</div>
                                <div className='col togo-dark-green'>{creditAmount}{" NIS"}</div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <div className="d-flex w-100 panel-wrapper" style={{ backgroundColor: "#eaeced" }}>
                <div className="m-3" style={{ width: "25%" }}>
                    <OrdersCard title="New Orders" list={newOrders} loading={newOrdersLoading} />
                </div>
                <div className="m-3" style={{ width: "25%" }}>
                    <OrdersCard title="Active Orders" list={activeOrders} loading={activeOrdersLoading} />
                </div>
                <div className="m-3" style={{ width: "25%" }}>
                    <OrdersCard title="Finished Orders" list={finishedOrders} loading={finishedOrdersLoading} />
                </div>
                <div className="m-3" style={{ width: "25%" }}>
                    <OrdersCard exception={true} title="Returned/Stuck Orders" list={exceptionOrders} loading={exceptionOrdersLoading} />
                    <OrdersCard exception={true} title="Deleted Orders" list={deletedOrders} loading={deletedOrdersLoading} />
                </div>
            </div>
        </>
    )
}
