import React, { useEffect, useRef, useState } from "react";
import { Form, Row, Col, Container, Button, ListGroup } from 'react-bootstrap';
import { tempAddCity, tempGetCities } from '../APIs/LoginAPIs';

export default function TestComponent() {

    /* const textRef = useRef();

    const [listAreas, setListAreas] = useState([]);

    const cleanHandler = () => {
        let tempStr = textRef.current.value;

        let tempArr = tempStr.split("<option value=\"\">")[1].split("</option>");

        let resArr = [];

        for (let i = 1; i < tempArr.length - 1; i++) {
            let tempNumber = tempArr[i].split("\">")[0].split("\"")[1];
            let tempName = tempArr[i].split("\">")[1];
            resArr.push({ number: tempNumber, name: tempName });

            tempAddCity(tempNumber, tempName).then((res) => {
                if (res.data != "enable editing!")
                    console.log(res.data);
            })
        }

        setListAreas(resArr);
    } */

    const [language, setLanguage] = useState(1);

    const [provinces, setProvinces] = useState([]);
    const [governorates, setGovernorates] = useState([]);
    const [cities, setCities] = useState([]);
    const [areas, setAreas] = useState([]);

    useEffect(() => {
        // provinces, governorates, cities, areas
        tempGetCities("provinces", -1, language).then((res) => {
            // console.log(res.data.server_response);
            setProvinces(res.data.server_response);
        })
    }, [])

    const updateSubLevel = (type, superId) => {
        tempGetCities(type, superId, language).then((res) => {
            // console.log(res.data.server_response)

            if (type === "governorates") {
                setGovernorates([]);
                setCities([]);
                setAreas([]);
                setGovernorates(res.data.server_response);
            } else if (type === "cities") {
                setCities([]);
                setAreas([]);
                setCities(res.data.server_response);
            } else {
                setAreas([]);
                setAreas(res.data.server_response);
            }
        })
    }

    return (
        <Container fluid className="p-5">
            {/* <Row>
                <Col className="d-flex justify-content-center"><span className="h1">This is a test component!</span></Col>
            </Row>

            <Row>
                <Col lg={6}>
                    <Form.Group controlId="exampleForm.ControlTextarea1">
                        <Form.Label>Enter options to clear:</Form.Label>
                        <Form.Control style={{ height: "300px", overflowY: "scroll" }} as="textarea" rows={100} ref={textRef} />
                    </Form.Group>
                </Col>

                <Col lg={1}>
                    <Button className="mt-5" onClick={cleanHandler}>
                        clean
                    </Button>
                </Col>

                <Col lg={5}>
                    <ListGroup className="mt-4">
                        {
                            listAreas.map((item, index) => {
                                return <ListGroup.Item key={index}>{item.number + " - " + item.name}</ListGroup.Item>
                            })
                        }
                    </ListGroup>
                </Col>
            </Row> */}

            <Row>
                <Col className="mb-5 d-flex justify-content-end">
                    <Button>
                        EN
                    </Button>
                    <Button className="ms-1">
                        AR
                    </Button>
                </Col>
            </Row>
            <Row>
                <Col lg={3} className="mb-4">
                    <Form.Label>Provinces:</Form.Label>
                    <Form.Select aria-label="Default select example" onChange={(e) => {
                        updateSubLevel("governorates", e.target.value)
                    }}>
                        <option style={{ color: "lightgray" }}>Select Provice</option>
                        {
                            provinces.map((item, index) => {
                                return <option key={index} value={item.id}>{item.id + " - " + item.name}</option>
                            })
                        }
                    </Form.Select>
                </Col>
                <Col lg={3} className="mb-4">
                    <Form.Label>Governorates:</Form.Label>
                    <Form.Select aria-label="Default select example" onChange={(e) => {
                        updateSubLevel("cities", e.target.value)
                    }}>
                        <option style={{ color: "lightgray" }}>Select Governorate</option>
                        {
                            governorates.map((item, index) => {
                                return <option key={index} value={item.id}>{item.id + " - " + item.name}</option>
                            })
                        }
                    </Form.Select>
                </Col>
                <Col lg={3} className="mb-4">
                    <Form.Label>Cities:</Form.Label>
                    <Form.Select aria-label="Default select example" onChange={(e) => {
                        updateSubLevel("areas", e.target.value)
                    }}>
                        <option style={{ color: "lightgray" }}>Select City</option>
                        {
                            cities.map((item, index) => {
                                return <option key={index} value={item.id}>{item.id + " - " + item.name}</option>
                            })
                        }
                    </Form.Select>
                </Col>
                <Col lg={3} className="mb-4">
                    <Form.Label>Areas:</Form.Label>
                    <Form.Select aria-label="Default select example">
                        <option style={{ color: "lightgray" }}>Select Area</option>
                        {
                            areas.map((item, index) => {
                                return <option key={index} value={item.id}>{item.id + " - " + item.name}</option>
                            })
                        }
                    </Form.Select>
                </Col>
            </Row>
        </Container>
    )
}
