import React, {useState} from 'react';
import { Button, Modal } from "react-bootstrap";
import translate from "../i18n/translate";

export default function AssignToDialog({ ordersIds }) {
    const [openAssignModal, setOpenAssignModal] = useState(false);
    const handleAssignModalClose = () => { setOpenAssignModal(false); }

    return <>
        <Button className={"btn-grad text-white ms-2 me-2"} onClick={() => {ordersIds.length > 0 ? setOpenAssignModal(true) : alert(translate("TEMP.SELECT_FIRST"))}}>{translate("TEMP.ASSIGN_TO")}</Button>
        <Modal size="lg" show={openAssignModal} onHide={handleAssignModalClose} centered animation={true} backdrop="static">

            <Modal.Header closeButton>
                <Modal.Title>{translate("TEMP.ASSIGN_TO")}</Modal.Title>
            </Modal.Header>

            <Modal.Body>

                

            </Modal.Body>
            <Modal.Footer>
                  <Button onClick={handleAssignModalClose}>{translate("GENERAL.CLOSE")}</Button>
            </Modal.Footer>

        </Modal>
    </>
}
