import React, { useCallback, useEffect, useState } from "react";
import './OrdersTabularView.css';
import translate from "../../i18n/translate";
import { Link } from "react-router-dom";
import { CancelOrder } from "../OrdersManager/CancelOrder";
import DynamicTable from "../DynamicTable/DynamicTable";
import { isTransporter, isTransporterMaster } from "../../Util";

import { getOrderDetails } from "../../APIs/OrdersAPIs"; /* edited (getOrderDetails imported) */

export const PackageTypes = {
    "1": "FOOD",
    "2": "SMALL_PACKAGE_AND_ENVELOPS",
    "3": "MEDIUM_PACKAGE",
    "4": "LARGE_PACKAGE"
};

export const DeliveryTypes = {
    "1": "DELIVERY",
    "2": "COD",
    "3": "PICKUP",
    "4": "PAP"
};

export const deliverFormatterClient = (data) => {
    if (DeliveryTypes[parseInt(data.DeliveryWays)]) {
        return translate("ORDERS." + DeliveryTypes[parseInt(data.DeliveryWays)])
    }
    return "";
}

export const packageFormatterClient = (data) => {
    if (PackageTypes[parseInt(data.PackageType)]) {
        return translate("ORDERS." + PackageTypes[parseInt(data.PackageType)])
    }
    return "";
}

/* edited (deliverFormatter added) */
export const deliverFormatter = (data) => {
    if (DeliveryTypes[parseInt(data.deliveryWay)]) { // edited (old => data.DeliveryWays)
        return translate("ORDERS." + DeliveryTypes[parseInt(data.deliveryWay)])
    }
    return "";
}

/* edited (packageFormatter added) */
export const packageFormatter = (data) => {
    if (PackageTypes[parseInt(data.TypeLoad)]) { // edited (old => data.PackageType)
        return translate("ORDERS." + PackageTypes[parseInt(data.TypeLoad)])
    }
    return "";
}

/* edited (timeFormatter added) */
export const timeFormatter = (data) => {
    if (data.DateLoad) {
        return data.DateLoad.split(" ")[1];
    }
    return "";
}

/* edited (dateFormatter added) */
export const dateFormatter = (data) => {
    if (data.DateLoad) {
        return data.DateLoad.split(" ")[0];
    }
    return "";
}

/* edited (bidsCountFormat added) */
export const bidsCountFormat = (data) => {
    if (data.bidsCount) {
        if (data.bidsCount !== "0") {
            return data.bidsCount;
        }

        return "--"
    }
    return "";
}

/* edited (statusFormat added) */
export const statusFormat = (data) => {
    if (data.isAcceptDelivery === null) {
        return "Waiting for Bid"
    } else if (data.pickup_date === null && data.orderfinished === null) {
        return "Bid Accepted"
    } else if (data.pickup_date !== null && data.orderfinished === null) {
        return "Out for Delivery"
    } else if (data.orderfinished === "1" && data.IsDeleted === "1") {
        return "Deleted"
    } else if (data.orderfinished === "1" && data.deliveryWay === "2") {
        return "Delivered and Collected"
    } else if (data.orderfinished === "1" && data.deliveryWay === "1") {
        return "Deliveredd"
    }/*  else if (data.orderfinished === "1" && data.DeliveryId !== data.OriginalDeliveryId) {
        return "Assigned and Delivered"
    } */
    return "";
}

export const OrdersTabularView = ({ orders, currentPage, update }) => {

    // console.log(orders)

    const [bidPrice, setBidPrice] = useState("-");


    /*const test = getOrderDetails(431).then((orderDetailsRes) => {
            setBidPrice(orderDetailsRes.CostLoad);
    })


    console.log(test);*/


    /*getOrderDetails(orderId).then((orderDetailsRes) => {
            setOrderDetails(orderDetailsRes);

            setReceiverAddress({
                otherDetails: orderDetailsRes.OtherDetailsDes,
                phoneCustomer: orderDetailsRes.ReceiverAddressNum,
                long: orderDetailsRes.LongReciver,
                lat: orderDetailsRes.LatReciver
            });
    });*/

    // console.log(orders);

    const [columns, setColumns] = useState([]);

    const showDetailsButton = useCallback((orderId) =>
        <Link
            to={{
                pathname: `/account/Order/${orderId}`,
                state: { currentPage: currentPage },
            }}
            style={{
                paddingRight: "20%",
                paddingLeft: "20%",
                border: "none",
                width: "100%",
                textAlign: "center",
            }}
            className="btn btn-primary btn-rounded btn-grad"
        >
            {translate("ORDERS.SHOW")}
        </Link>, [currentPage]);

    useEffect(() => {

        let newColumns = [];

        /* edited (differ between transporter and client columns) */
        if (isTransporter()) {

            newColumns = [
                {
                    label: translate("ORDERS.ORDER_NUM"),
                    key: "id"
                },
                {
                    label: translate("ORDERS.DELIVERY_TYPE"),
                    key: "deliveryWay",
                    format: deliverFormatter
                },
                {
                    label: translate("ORDERS.PACKAGE_TYPE"),
                    key: "TypeLoad",
                    format: packageFormatter
                },
                {
                    label: translate("ORDERS.ORDER_DATE"),
                    key: "DateLoad",
                    format: dateFormatter
                },
                {
                    label: translate("ORDERS.ORDER_TIME"),
                    key: "DateLoad",
                    format: timeFormatter
                },
                {
                    label: translate("ORDERS.FROM_CITY"),
                    key: "fromAddress"
                },
                {
                    label: translate("ORDERS.TO_CITY"),
                    key: "toAddress"
                },
                {
                    label: translate("ORDERS.BIDS_COUNT"),
                    key: "bidsCount",
                    format: bidsCountFormat
                },
                {
                    label: translate("ORDERS.ORDER_STATUS"),
                    key: "isAcceptDelivery",
                    format: statusFormat
                },
                {
                    label: translate("ORDERS.FULL_DETAILS"),
                    key: "id",
                    format: ({ id }) => showDetailsButton(id)
                }
            ];
        } else {
            newColumns = [
                {
                    label: translate("ORDERS.ORDER_NUM"),
                    key: "idOrder"
                },
                {
                    label: translate("ORDERS.DELIVERY_TYPE"),
                    key: "DeliveryWays",
                    format: deliverFormatterClient
                },
                {
                    label: translate("ORDERS.PACKAGE_TYPE"),
                    key: "PackageType",
                    format: packageFormatterClient
                },
                {
                    label: translate("ORDERS.ORDER_DATE"),
                    key: "DateOrder"
                },
                {
                    label: translate("ORDERS.ORDER_TIME"),
                    key: "TimeOrder"
                },
                {
                    label: translate("ORDERS.FROM_CITY"),
                    key: "FromAddress"
                },
                {
                    label: translate("ORDERS.TO_CITY"),
                    key: "ToAddress"
                },
                {
                    label: translate("ORDERS.ORDER_STATUS"),
                    key: "OrderStatus"
                },
                {
                    label: translate("ORDERS.FULL_DETAILS"),
                    key: "idOrder",
                    format: ({ idOrder }) => showDetailsButton(idOrder)
                }
            ];
        }

        /* edited (comment bidPrice) */
        /*if (isTransporter()) {
            newColumns.splice(3, 0, {
                label: translate("ORDER_DETAILS.BID_PRICE"),
                key: "bidPrice",
                format: ({idOrder}) => <p>{bidPrice}</p>
            });
        }*/

        if (!isTransporter() && currentPage === "all-orders") {
            newColumns.push({
                label: "",
                key: "",
                format: ({ idOrder }) => <CancelOrder className="w-100" orderId={idOrder} onSuccess={update} />
            });
        }

        /* edited (add bidscount) */
        /* if (currentPage === "all-orders" && !isTransporter()) {
            newColumns.splice(7, 0, {
                label: translate("ORDERS.BIDS_COUNT"),
                key: "bidsCount"
            });
        } */

        /* edited (to display my bid as transporter) */
        /* if (currentPage === "all-orders" && isTransporter()) {
            newColumns.splice(7, 0, {
                label: "",
                key: ""
            });
        } */

        /* edited (commented) */
        /* if (currentPage === "current-orders") {
            //TODO:: key to be changed

            newColumns.splice(3, 0, {
                label: translate("ORDERS.PRICE"),
                key: "CostLoad"
            });
            
            if(isTransporterMaster()){
                newColumns.splice(newColumns.length - 1, 0,{
                    label: translate("ORDER_DETAILS.ASSIGNED"),
                    key: "AssignedMemberName",
                });
            }
        } */ /* else if (currentPage === "previous-orders") {
            newColumns.push({
                label: translate("ORDERS.ORDER_STATUS"),
                key: "idOrder",
                format: ({IsFinished, IsDeleted}) => IsFinished ? translate("ORDERS.COMPLETED") : IsDeleted ? translate("ORDERS.CANCELED") : null
            })
        } */

        setColumns(newColumns);
    }, [currentPage, showDetailsButton]);

    return <DynamicTable columns={columns} data={orders} />;
};
