import { motion } from "framer-motion";
import React from "react";

export default function UsersCard(props){
    return (
        <div /* style={{flex: 1}} */ 
        
        className="h-100 d-flex flex-column bg-white  p-3 rounded-22 shadow">
            <p className="text-black">{props.title}</p>
            <div style={{overflowY: "auto",overflowX: "hidden",fontFamily: "system-ui"}}>
            {
                props?.list?.map((user)=>{
                    return (
                        <motion.div 
                        variants={{ 
                            mount: { opacity: 0.8 },
                            rest: { opacity: 1 }
                          }}
                        initial='mount'
                        whileHover={{ opacity: 1 }}
                        className="d-flex gap-3 align-items-center d-flex gap-3 text-nowrap mb-2">
                            <div 
                                style={{width: "45px", height: "45px", backgroundColor: "#A0F2CC"}} 
                                className="rounded-circle d-flex"
                            >
                                <img 
                                    style={{    
                                        margin: "auto",
                                        width: "100%",
                                        height: "100%",
                                        objectFit: "cover",
                                        borderRadius: "50%",
                                        border: "1px solid"
                                    }}
                                    src={user.PersonalImgPath ? "http://46.253.93.4/togo/MobileAPi/" + user.PersonalImgPath: "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}
                                />
                            </div>
                            <div style={{width: "40%"}}>
                                <div>{user.FullName}</div>
                                <div>{user.PhoneNumber}</div>
                            </div>
                            
                        </motion.div>
                    )
                })
            }
            </div>
        </div>
    )
}
