import React, { useEffect, useState } from "react";
import Address from "../Address/Address";
import Map from "../Address/Map"; /* edited (Map imported) */
import { Accordion, Card, Button, Badge, Dropdown, Form, Modal, Spinner, Col, Row, Container } from "react-bootstrap"; /* edited (new importd) */
import { MdOutlineFastfood } from 'react-icons/md'; /* edited (new import) */

import { FinalizeOrder } from "../OrdersManager/FinalizeOrder";
import { CancelOrder } from "../OrdersManager/CancelOrder";
import { BidOrder } from "../OrdersManager/AcceptOrder";
import { DeleteTripCost } from "../OrdersManager/DeleteTripCost";
import { EditTripCost } from "../OrdersManager/EditTripCost";
import Loader from "../Loader/Loader";
import "./OrderDetails.css";
import { getOrderDetails, checkTripCost, AcceptOfferReq, GetTransporterLocation } from "../../APIs/OrdersAPIs";
import translate from "../../i18n/translate";

import { Alert, AlertDescription, AlertIcon, AlertTitle } from "@chakra-ui/alert";
import { Box } from "@chakra-ui/layout";
import { deliverFormatter, packageFormatter } from "../Orders/OrdersTabularView";
import { isTeamMember, isTransporter, isTransporterMaster } from "../../Util";
import AssignToTransporter from "../AssigneToTransporter";

import { useLocation, useParams, useHistory } from "react-router";

import AssignToMemberOnNetworkDialog from "../AssginToMemberOnNetworkDialog";
import { refreshPage } from "../../Functions/CommonFunctions";

import Timeline from "../TimeLine/TimeLine"; /* edited (TimeLine imported) */

import Table from "react-bootstrap/Table"; /* edited (Table imported) */

import Rating from '@material-ui/lab/Rating'; /* edited (Rating imported) */

// var Barcode = require('react-barcode');

const OrderDetails = () => {

    const [show, setShow] = useState(false);
    const [cost, setCost] = useState("");

    const handleClose = () => {
        setShow(false);
    };

    const handleShow = () => setShow(true);

    const { id: orderId } = useParams();
    const location = useLocation();
    const history = useHistory();
    const [orderDetails, setOrderDetails] = useState({});
    const [previousPage, setPreviousPage] = useState(null);
    const [tripCost, setTripCost] = useState(null);
    const [costSet, setCostSet] = useState(null);
    const [receiverAddress, setReceiverAddress] = useState({});
    const [startTracking, setStartTracking] = useState(false);


    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            backgroundColor: 'lightgreen',
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            backgroundColor: 'lightgreen',
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        },
        orderNumber: {
            color: 'green',
            fontStyle: 'italic',
            marginLeft: '10px',
            marginRight: '10px'
        },
        orderStatus: {
            float: 'right'
        },
        tableElement: {
            textAlign: 'right'
        }
    }

    useEffect(() => {
        let live = false;
        if (startTracking === false) {
            return;
        }
        const intervalId = setInterval(() => {
            if (!live) {
                live = true;
                GetTransporterLocation(orderId).then((resp) => {
                    live = false;
                    let { TransporterLatLocation, TransporterLongLoc } = resp.data.server_response[0];
                    if (TransporterLatLocation === null || TransporterLongLoc === null) {
                        TransporterLatLocation = 0;
                        TransporterLongLoc = 0;
                    }
                    setReceiverAddress({
                        ...receiverAddress,
                        long: TransporterLongLoc,
                        lat: TransporterLatLocation
                    });
                });
            }
        }, 4000);
        return () => clearInterval(intervalId);
    }, [startTracking])
    useEffect(() => {
        checkTripCost(orderId, isTransporter()).then((tripCostRes) => {
            if (tripCostRes && tripCostRes !== "CostNotSend") {
                setTripCost(isTransporter() ? tripCostRes.server_response[0] : tripCostRes.CostDetail);
                setCostSet(true);
            } else {
                setCostSet(false);
            }
        });

        getOrderDetails(orderId).then((orderDetailsRes) => {

            // console.log(orderDetailsRes); // temp test

            setOrderDetails(orderDetailsRes);

            setReceiverAddress({
                otherDetails: orderDetailsRes.OtherDetailsDes,
                phoneCustomer: orderDetailsRes.ReceiverAddressNum,
                long: orderDetailsRes.LongReciver,
                lat: orderDetailsRes.LatReciver
            });
        });

        setPreviousPage(location.state?.currentPage);
    }, []);

    // console.log(orderDetails); /* temp test */

    

    if (typeof orderDetails === "object" && JSON.stringify(orderDetails) !== '{}') {
        let {
            OrderStatus, /* edited (OrderStatus added) */
            DateLoad, /* edited (DateLoad added) */
            DeliveryPrice, /* edited (DeliveryPrice added) */
            CostLoad, /* edited (CostLoad added) */
            AssignToName, /* edited (AssignToName added) */
            AssignToNumber, /* edited (AssignToNumber added) */
            transporterImgURL, /* edited (transporterImgURL added) */
            customerImgURL, /* edited (customerImgURL added) */
            FullNameCustomer, /* edited (FullNameCustomer added) */
            LastNameCustomer, /* edited (LastNameCustomer added) */
            deliveryWay,
            HeightLoad,
            DetailsLoad,
            LengthLoad,
            WeightLoad,
            WidthLoad,
            OtherDetails,
            TypeLoad,
            PhoneCustomer,
            LongSender, LatSender,
            AssignStatus, AssignedMemberName,
            isAcceptDelivery,
            SenderName,
            ReceiverName
        } = orderDetails;

        let senderAddress = {
            name: SenderName,
            otherDetails: OtherDetails,
            phoneCustomer: PhoneCustomer,
            long: LongSender,
            lat: LatSender
        };

        /* edited (timeline data + dummy data to test the timeline) */

         let tempTransportationParties = [];

         if (AssignToName !== null) {
            tempTransportationParties = [
                {
                    type: 1,
                    current: false,
                    address: senderAddress,
                    customerName: FullNameCustomer + " " + LastNameCustomer,
                    name: SenderName,
                    imageURL: customerImgURL
                },
                {
                    type: 2,
                    current: false,
                    address: null,
                    name: AssignToName,
                    phone: AssignToNumber,
                    price: DeliveryPrice,
                    current: true,
                    imageURL: transporterImgURL
                },
                /*{
                    type: 2,
                    current: false,
                    address: null
                },
                {
                    type: 2,
                    current: true,
                    address: null
                },*/
                {
                    type: 3,
                    current: false,
                    address: receiverAddress,
                    name: ReceiverName
                }
            ]
         } else {
            tempTransportationParties = [
                {
                    type: 1,
                    current: false,
                    address: senderAddress,
                    customerName: FullNameCustomer + " " + LastNameCustomer,
                    name: SenderName,
                    imageURL: customerImgURL
                },
                /*{
                    type: 2,
                    current: false,
                    address: null,
                    phone: "059999999999",
                    price: "2020",
                    name: "test"
                },
                {
                    type: 2,
                    current: false,
                    address: null,
                    phone: "059999999999",
                    price: "2020",
                    name: "test"
                },
                {
                    type: 2,
                    current: true,
                    address: null,
                    phone: "059999999999",
                    price: "2020",
                    name: "test"
                },*/
                {
                    type: 3,
                    current: false,
                    address: receiverAddress,
                    name: ReceiverName
                }
            ]
         }

        function timeFormat(time) {
          // Check correct time format and split into components
          time = time.toString ().match (/^([01]\d|2[0-3])(:)([0-5]\d)(:[0-5]\d)?$/) || [time];

          if (time.length > 1) { // If time format correct
            time = time.slice (1);  // Remove full string match value
            time[5] = +time[0] < 12 ? ' AM' : ' PM'; // Set AM/PM
            time[0] = +time[0] % 12 || 12; // Adjust hours
          }
          return time.join (''); // return adjusted time or original string
        }

        // console.log("------ previousPage: " + previousPage); // temp test

        return (

            <React.Fragment>
                {orderDetails && <Container fluid>
                    <Col className="p-3">
                        <Row>

                            {/* edited (action buttons moved to top) */}

                            {previousPage === "all-orders" && isTransporter() && tripCost && <Box style={{
                                marginTop: "20px",
                                // marginBottom: "20px" edited (commented)
                            }}>

                                <Card
                                    bg={"Danger"}
                                    text={"black"}
                                >
                                    <Card.Header>{translate("ORDER_DETAILS.BID_PRICE")}</Card.Header>
                                    <Card.Body className="d-flex align-items-center">
                                        {tripCost.CostDelivery}
                                        <div className="ActionButtons ms-auto">
                                            <DeleteTripCost orderId={orderId} />
                                            <EditTripCost orderId={orderId} tripCost={tripCost.CostDelivery} />
                                        </div>
                                    </Card.Body>
                                </Card>

                            </Box>}
                            <Box style={{
                                marginTop: "20px",
                                marginBottom: "20px"
                            }}>
                                {previousPage === "all-orders" && !isTransporter() &&
                                    <div className="d-flex align-items-center">
                                        <Button
                                            style={{ width: "150px", marginLeft: "10px", marginRight: "10px" }}
                                            className="ms-auto"
                                            onClick={() => {
                                                window.open("/account/bill-for-order/" + orderId + "?print=true")
                                            }}
                                        >
                                            {translate("ORDER_DETAILS.PRINT")}
                                        </Button>
                                        <CancelOrder orderId={orderId} className="mx-1" onSuccess={() => {
                                            history.goBack();
                                        }} />
                                    </div>
                                }

                                {previousPage === "all-orders" &&
                                    !costSet && isTransporter() && <div className="d-flex justify-content-end">
                                        <BidOrder orderId={orderId} />
                                    </div>
                                }


                                {previousPage === "current-orders" && (
                                    <Box display="flex" alignItems="center" justifyContent="end">

                                        {/* edited (add print button to current-oders for both transporter and client) */}

                                        <Button
                                            variant="outline-primary"
                                            onClick={() => {
                                                // setOpenConf(false);
                                                window.open("/account/bill-for-order/" + orderId + "?print=true")
                                            }}
                                            style={{ marginRight: "5px", width: "150px" }}
                                        >
                                            {translate("ORDER_DETAILS.PRINT")}
                                            <svg style={{ display: "inline-block", marginLeft: "5px", marginRight: "5px" }} xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-printer" viewBox="0 0 16 16">
                                                <path d="M2.5 8a.5.5 0 1 0 0-1 .5.5 0 0 0 0 1z" />
                                                <path d="M5 1a2 2 0 0 0-2 2v2H2a2 2 0 0 0-2 2v3a2 2 0 0 0 2 2h1v1a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2v-1h1a2 2 0 0 0 2-2V7a2 2 0 0 0-2-2h-1V3a2 2 0 0 0-2-2H5zM4 3a1 1 0 0 1 1-1h6a1 1 0 0 1 1 1v2H4V3zm1 5a2 2 0 0 0-2 2v1H2a1 1 0 0 1-1-1V7a1 1 0 0 1 1-1h12a1 1 0 0 1 1 1v3a1 1 0 0 1-1 1h-1v-1a2 2 0 0 0-2-2H5zm7 2v3a1 1 0 0 1-1 1H5a1 1 0 0 1-1-1v-3a1 1 0 0 1 1-1h6a1 1 0 0 1 1 1z" />
                                            </svg>
                                        </Button>

                                        {isTransporterMaster() && AssignStatus !== "Assigned" &&
                                            <AssignToTransporter
                                                onSuccess={() => {
                                                    getOrderDetails(orderId).then((orderDetailsRes) => {
                                                        setOrderDetails(orderDetailsRes);
                                                    });
                                                }}
                                                orderId={orderId}
                                            />
                                        }
                                        {isTransporter() &&
                                            <AssignToMemberOnNetworkDialog
                                                onSuccess={() => {
                                                    history.goBack();
                                                }}
                                                orderId={orderId}
                                            />
                                        }

                                        {/* edited (temporarily comment FinalizeOrder) */}
                                        {/*(isTeamMember() || (isTransporter() && !isTransporterMaster())) &&
                                        <div className="ms-1">
                                            <FinalizeOrder orderId={orderId} />
                                        </div>*/
                                        }
                                    </Box>
                                )}
                            </Box>
                        </Row>

                        {/* edited (Transportation Timeline) */}
                        <Row>
                            <div className="d-flex justify-content-center">

                                <Timeline transportationParties={tempTransportationParties} />

                            </div>
                        </Row>

                        <Row className="mt-3">
                            <Col className="">
                                <Row>
                                    <Col>
                                        <Card className='shadow mt-5'>
                                            <Card.Header style={styles.cardHeaderLg}>
                                                Order
                                                <span style={styles.orderNumber}>{orderId}</span>
                                                <span style={styles.orderStatus}>
                                                    <Badge bg="danger">status</Badge>
                                                </span>
                                            </Card.Header>
                                            <Card.Body className='mt-3'>
                                                <Table>
                                                    <tbody>
                                                        <tr>
                                                            <th scope='row'>Order Type</th>
                                                            <td style={styles.tableElement}>
                                                                {deliverFormatter({ DeliveryWays: deliveryWay })}
                                                                <Badge bg="secondary ms-3">{CostLoad + " NIS"}</Badge>
                                                            </td>
                                                        </tr>
                                                        <tr>
                                                            <th scope='row'>Order Time</th>
                                                            <td style={styles.tableElement}>{/*DateLoad.split(" ")[1]*/ timeFormat(DateLoad.split(" ")[1])}  {DateLoad.split(" ")[0]}</td>
                                                        </tr>
                                                        <tr>
                                                            <th scope='row'>Notes</th>
                                                            <td style={styles.tableElement}>{DetailsLoad}</td>
                                                        </tr>
                                                    </tbody>
                                                </Table>
                                            </Card.Body>
                                        </Card>
                                    </Col>
                                </Row>
                                <Row>
                                    <Col>
                                        <Card className='shadow mt-2'>
                                            {/* <Card.Header style={styles.cardHeader}>
                                            Package Inforamtion
                                        </Card.Header> */}
                                            <Card.Body className='mt-3'>
                                                <Table>
                                                    <tbody>
                                                        <tr>
                                                            <th scope='row'>Package Type</th>
                                                            <td style={styles.tableElement}>
                                                                {packageFormatter({ PackageType: TypeLoad })}
                                                                <Badge bg="warning ms-3">
                                                                    {/*<MdOutlineFastfood />*/}
                                                                </Badge>
                                                            </td>
                                                        </tr>
                                                        <tr>
                                                            <th scope='row'>{translate("ORDER_DETAILS.LOAD_HEIGHT")}</th>
                                                            <td style={styles.tableElement}>{HeightLoad}</td>
                                                        </tr>
                                                        <tr>
                                                            <th scope='row'>{translate("ORDER_DETAILS.LOAD_WIDTH")}</th>
                                                            <td style={styles.tableElement}>{WidthLoad}</td>
                                                        </tr>
                                                        <tr>
                                                            <th scope='row'>{translate("ORDER_DETAILS.LOAD_LENGTH")}</th>
                                                            <td style={styles.tableElement}>{LengthLoad}</td>
                                                        </tr>
                                                        <tr>
                                                            <th scope='row'>{translate("ORDER_DETAILS.LOAD_WEIGHT")}</th>
                                                            <td style={styles.tableElement}>{WeightLoad}</td>
                                                        </tr>
                                                    </tbody>
                                                </Table>
                                                {/*  <Accordion defaultActiveKey="0" className="mt-3">
                                                <Accordion.Item
                                                    eventKey="1"
                                                    className="w-100"
                                                >
                                                    <Accordion.Header as="div">
                                                        Package Details
                                                    </Accordion.Header>
                                                    <Accordion.Body>
                                                        <Table>
                                                            <tbody>
                                                                <tr>
                                                                    <th scope='row'>Load Height</th>
                                                                    <td style={styles.tableElement}>10</td>
                                                                </tr>
                                                                <tr>
                                                                    <th scope='row'>Load Width</th>
                                                                    <td style={styles.tableElement}>20</td>
                                                                </tr>
                                                                <tr>
                                                                    <th scope='row'>Load Length</th>
                                                                    <td style={styles.tableElement}>50</td>
                                                                </tr>
                                                                <tr>
                                                                    <th scope='row'>Load Weight</th>
                                                                    <td style={styles.tableElement}>15</td>
                                                                </tr>
                                                            </tbody>
                                                        </Table>
                                                    </Accordion.Body>

                                                </Accordion.Item>
                                            </Accordion> */}
                                            </Card.Body>
                                        </Card>
                                    </Col>
                                </Row>
                            </Col>
                            <Col className="mt-5">
                                {/* <Row className='' style={{ height: '30px', marginBottom: '40px' }}>
                                    <div className='d-flex justify-content-end'>
                                        <Button variant="primary" className='me-1 ms-1'>Action 1</Button>
                                        <Button variant="secondary" className='me-1 ms-1'>Action 2</Button>
                                        <Button variant="success" className='me-1 ms-1'>Action 3</Button>
                                    </div>
                                </Row> */}
                                <Row className='' style={{ height: '100%' }}>
                                    <Col>
                                        <Card className='shadow h-100'>
                                            <Card.Header style={styles.cardHeaderSm}>
                                                {!isTransporter() && previousPage !== "current-orders" && previousPage !== "previous-orders" ? translate("ORDERS.AVAILABLE_OFFERS") : translate("TEMP.LOCATION")}
                                            </Card.Header>
                                            <Card.Body className='mt-3' style={{ height: '345.8px', overflowY: 'scroll' }}>
                                                {!isTransporter() && previousPage !== "current-orders" && previousPage !== "previous-orders" ? <Table>
                                                    <thead>
                                                        <tr>
                                                            <th></th>
                                                            <th>Name</th>
                                                            <th>Price</th>
                                                            <th></th>
                                                        </tr>
                                                    </thead>
                                                    {previousPage === "all-orders" && !isTransporter() && tripCost && tripCost.length !== 0 ? <tbody>
                                                            {tripCost.map((costs, index) => {
                                                                return <tr key={index}>
                                                                    <td><img style={{
                                                                        width: "60px",
                                                                        height: "60px",
                                                                        objectFit: "cover"
                                                                    }}
                                                                        className="rounded-circle align-self-center" src={`http://46.253.93.4/togo/MobileAPi/${costs.TransporterPersonalImg}`} alt="transImg" />
                                                                    </td>
                                                                    <td>{costs.TransporterName}</td>
                                                                    <td>{costs.BidCost}</td>
                                                                    <td>
                                                                        {/* edited (old accept button) */}
                                                                        {/*<Button className="btn-grad" onClick={()=>{
                                                                                    AcceptOfferReq(costs.IdTransporterBidRequist, orderId ,costs.BidCost).then((resp)=>{
                                                                                        if(resp.data.indexOf("Success") !== -1){
                                                                                            refreshPage();
                                                                                        }
                                                                                        })
                                                                                    }}>Accept Offer
                                                                                </Button>*/}
                                                                        <Button
                                                                            variant="outline-primary"
                                                                            onClick={handleShow}
                                                                            style={{

                                                                            }}
                                                                        >
                                                                            {translate("TEMP.ACCEPT_OFFER")}
                                                                        </Button>
                                                                        <Modal show={show} onHide={handleClose} centered>
                                                                            <Modal.Header closeButton>
                                                                                <Modal.Title>{translate("TEMP.ACCEPT_OFFER")}</Modal.Title>
                                                                            </Modal.Header>

                                                                            <Modal.Body>
                                                                                <div className="" style={{ fontSize: "1.5rem" }}>
                                                                                    <div className="w-100 d-flex justify-content-center">
                                                                                        <div style={{
                                                                                            background: "linear-gradient(90deg, #26a69a, #69d4a5)",//"linear-gradient(90deg, #ff9966, #ff5e62)",

                                                                                            width: "80px",
                                                                                            height: "80px",
                                                                                        }}
                                                                                            className="d-flex justify-content-center rounded-circle align-self-center">
                                                                                            <img
                                                                                                style={{
                                                                                                    width: "80%",
                                                                                                    height: "80%",
                                                                                                    objectFit: "cover"
                                                                                                }}
                                                                                                className="rounded-circle align-self-center bg-white"
                                                                                                src={`http://46.253.93.4/togo/MobileAPi/${costs.TransporterPersonalImg}`}
                                                                                                alt={"img"}

                                                                                            />
                                                                                        </div>
                                                                                    </div>
                                                                                    <div className="d-flex justify-content-between">
                                                                                        <div>Transporter:</div>
                                                                                        <div>{costs.TransporterName}</div>
                                                                                    </div>
                                                                                    <div className="d-flex justify-content-between">
                                                                                        <div>Bid Price:</div>
                                                                                        <div>{costs.BidCost}</div>
                                                                                    </div>
                                                                                    <div className="w-100 d-flex justify-content-center">
                                                                                        <Rating name="size-large" size="large" defaultValue={costs.TotalRate} precision={0.1} readOnly />
                                                                                    </div>
                                                                                </div>
                                                                            </Modal.Body>
                                                                            <Modal.Footer>
                                                                                <Button variant="secondary" onClick={handleClose}>
                                                                                    {translate("GENERAL.CANCEL")}
                                                                                </Button>
                                                                                <Button
                                                                                    variant="primary"
                                                                                    onClick={() => {
                                                                                        AcceptOfferReq(costs.IdTransporterBidRequist, orderId, costs.BidCost).then((resp) => {
                                                                                            if (resp.data.indexOf("Success") !== -1) {
                                                                                                // refreshPage();
                                                                                                history.goBack();
                                                                                            }
                                                                                        })
                                                                                    }}
                                                                                >
                                                                                    {translate("TEMP.ACCEPT")}
                                                                                </Button>
                                                                            </Modal.Footer>
                                                                        </Modal>
                                                                    </td>
                                                                </tr>
                                                            })}
                                                        </tbody> : <tbody>
                                                            <tr>
                                                                <td colSpan={4}>{translate("ORDERS.NO_OFFERS")}</td>
                                                            </tr>
                                                        </tbody>}
                                                </Table> : <Map className="" address1={senderAddress} address2={receiverAddress} />}
                                            </Card.Body>
                                        </Card>
                                    </Col>
                                </Row>
                            </Col>
                        </Row>
                        {!isTransporter() && previousPage !== "current-orders" && previousPage !== "previous-orders" && <Row className="mt-5">
                            <Col>
                                <Card className='shadow mt-2'>
                                    <Card.Header style={styles.cardHeaderSm}>
                                        {translate("TEMP.LOCATION")}
                                    </Card.Header>
                                    <Card.Body className='mt-3'>
                                        <Map className="" address1={senderAddress} address2={receiverAddress} />
                                    </Card.Body>
                                </Card>
                            </Col>
                        </Row>}
                    </Col>
                </Container>}
            </React.Fragment>
        );
    } else if (typeof orderDetails === "string") {
        return (
            <Alert status="error" w="80%" m="10px auto">
                <AlertIcon />
                <AlertTitle mr={2}>{orderId}</AlertTitle>
                <AlertDescription>{orderDetails}</AlertDescription>
            </Alert>
        );
    } else {
        return <Box height="400px"><Loader /></Box>
    }
}

export default OrderDetails;
