import React, { useEffect, useRef, useState } from "react";
import './CustomerInfo.css';
import { Container, Row, Col, Card, Table, Button, Form, Tab, Tabs, Sonnet } from 'react-bootstrap';
import { GetTransporterPersonalInfo, GetClientPersonalInfo, GetTransporterWorkingTimes, GetClientBusinessInfo } from "../../APIs/AdminPanelApis";

function TransporterPersonalInfoDisplay({ id }) {

    const [info, setInfo] = useState();
    const [fullName, setFullName] = useState();

    // const [workingTime, setWorkingTime] = useState();
    const [workingTimeList, setWorkingTimeList] = useState([]);

    const fullNameRef = useRef();
    const accountNameRef = useRef();
    const emailRef = useRef();
    const IDNumberRef = useRef();
    const licenceNumberRef = useRef();

    /* validation */
    const [beginValidation, setBeginValidation] = useState(false);
    const [fullNameCheck, setFullNameCheck] = useState(false);
    const [accountNameCheck, setAccountNameCheck] = useState(false);
    const [emailCheck, setEmailCheck] = useState(false);
    const [IDNumberCheck, setIDNumberCheck] = useState(false);
    const [licenceNumberCheck, setlicenceNumberCheck] = useState(false);

    useEffect(() => {

        GetTransporterPersonalInfo(id).then((res) => {
            // console.log(res.data);
            setInfo(res.data.server_response);
            // setFullName(res.data.server_response.fullName);
        })

        GetTransporterWorkingTimes(id).then((res) => {
            // console.log(res.data.TimeResponse);
            // setWorkingTime(res.data.TimeResponse);

            const workingTime = res.data.TimeResponse;

            console.log(workingTime)

            const {
                SatTimeStart, SatTimeFinish, SunTimeStart, SunTimeFinish, MonTimeStart,
                MonTimeFinish, TueTimeStart, TueTimeFinish, WenTimeStart, WenTimeFinish,
                ThuTimeStart, ThuTimeFinish, FriTimeStart, FriTimeFinish
            } = workingTime;

            let tempWorkingTimeList = [
                {
                    title: "Saturday",
                    id: "Sat",
                    from: formatTime(SatTimeStart),
                    to: formatTime(SatTimeFinish)
                },
                {
                    title: "Sunday",
                    id: "Sun",
                    from: formatTime(SunTimeStart),
                    to: formatTime(SunTimeFinish)
                },
                {
                    title: "Monday",
                    id: "Mon",
                    from: formatTime(MonTimeStart),
                    to: formatTime(MonTimeFinish)
                },
                {
                    title: "Tuesday",
                    id: "Tue",
                    from: formatTime(TueTimeStart),
                    to: formatTime(TueTimeFinish)
                },
                {
                    title: "Wednesday",
                    id: "Wen",
                    from: formatTime(WenTimeStart),
                    to: formatTime(WenTimeFinish)
                },
                {
                    title: "Thursday",
                    id: "Thu",
                    from: formatTime(ThuTimeStart),
                    to: formatTime(ThuTimeFinish)
                },
                {
                    title: "Friday",
                    id: "Fri",
                    from: formatTime(FriTimeStart),
                    to: formatTime(FriTimeFinish)
                }
            ];

            setWorkingTimeList(tempWorkingTimeList);

            // console.log(workingTimeList)
        })



    }, [])

    const formatTime = (value) => {
        if (!value) return null;
        let time = value.split(" ")[1].slice(0, -3);
        return convert24TimeTo12(time);
    };

    function validatePersonalInfo() {
        setBeginValidation(true);

        if (fullNameRef.current.value) {
            setFullNameCheck(true);
        } else {
            setFullNameCheck(false);
        }
        if (accountNameRef.current.value) {
            setAccountNameCheck(true);
        } else {
            setAccountNameCheck(false);
        }
        if (emailRef.current.value) {
            setEmailCheck(true);
        } else {
            setEmailCheck(false);
        }
        if (IDNumberRef.current.value) {
            setIDNumberCheck(true);
        } else {
            setIDNumberCheck(false);
        }
        if (licenceNumberRef.current.value) {
            setlicenceNumberCheck(true);
        } else {
            setlicenceNumberCheck(false);
        }
    }

    function updatePersonalInfo() {

        validatePersonalInfo();

        if (fullNameRef.current.value && accountNameRef.current.value && emailRef.current.value && IDNumberRef.current.value && licenceNumberRef.current.value) {
            console.log("updated!")
        }
    }

    function test() {
        console.log(info)
    }

    return (
        <>
            <Tabs defaultActiveKey="PersonalInformation" id="uncontrolled-tab-example" className="tabPane">
                <Tab eventKey="PersonalInformation" title="Personal Information">
                    <Card className="customCardSm shadow m-5">
                        {/* <Card.Header>
                            Personal Information
                        </Card.Header> */}
                        <Card.Body>

                            <Container fluid>
                                <Row>
                                    <Col lg={6}>
                                        <Table className="infoTable">
                                            <tbody>
                                                <tr>
                                                    <th>Full Name:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.fullName}
                                                            ref={fullNameRef}
                                                        /* onChange={(event) => {setFullName(event.target.value); console.log(event.target.value)}} */
                                                        />
                                                        {beginValidation && !fullNameCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Account Name:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.AccountName}
                                                            ref={accountNameRef}
                                                        />
                                                        {beginValidation && !accountNameCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Phone Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.phone}
                                                            ref={accountNameRef}
                                                        />
                                                        {beginValidation && !accountNameCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Email:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="email"
                                                            placeholder={info?.Email}
                                                            ref={emailRef}
                                                        />
                                                        {beginValidation && !emailCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>ID Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={info?.IDNumber}
                                                            ref={IDNumberRef}
                                                        />
                                                        {beginValidation && !IDNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                                {/* <tr>
                                                    <th>Place of Issuing Identity:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={info?.NamePlace}
                                                            ref={IDNumberRef}
                                                            onClick={test}
                                                        />
                                                        {beginValidation && !IDNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr> */}
                                                <tr>
                                                    <th>Licence Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={info?.LicenceNumber}
                                                            ref={licenceNumberRef}
                                                        />
                                                        {beginValidation && !licenceNumberCheck && <div className="required-error"><span>*</span></div>}
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </Table>
                                    </Col>
                                    <Col lg={6}>
                                        <div className="w-100 d-flex justify-content-center">
                                            <div
                                                className="rounded-circle d-flex light-turquoise-bg wh-45px "
                                                style={{ width: "200px", height: "200px" }}
                                            >
                                                <img
                                                    className="user-img-details"

                                                    src={info?.img ? "http://46.253.93.4/togo/MobileAPi/" + info.img : "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}

                                                    alt="user-profile-pic"
                                                />
                                            </div>
                                        </div>
                                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "1.5rem" }}>{/* fullName */ info?.fullName}</div>
                                    </Col>
                                </Row>
                            </Container>
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn" onClick={updatePersonalInfo}>
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
                <Tab eventKey="WorkingTimeAndBusinessLocation" title=" Working Time and Business Location">
                    <Card className="customCardSm shadow m-5">
                        {/* <Card.Header>
                            Working Time and Business Location
                        </Card.Header> */}
                        <Card.Body>
                            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn">
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
                <Tab eventKey="VehiclesInformation" title="Vehicles Information">
                    <Card className="customCardSm shadow m-5">
                        {/* <Card.Header>
                            Vehicles Information
                        </Card.Header> */}
                        <Card.Body>
                            labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn">
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
            </Tabs>
        </>
    )
}

function ClientPersonalInfoDisplay({ id }) {

    const [personalInfo, setPersonalInfo] = useState();
    const [businessInfo, setBusinessInfo] = useState();

    useEffect(() => {

        GetClientPersonalInfo(id).then((res) => {
            // console.log(res.data);
            setPersonalInfo(res.data.server_response);
            // setFullName(res.data.server_response.fullName);
        })

        GetClientBusinessInfo(id).then((res) => {
            // console.log(res.data);
            setBusinessInfo(res.data.server_response)
        })

    }, [])

    function updatePersonalInfo() {

        /* if (fullNameRef.current.value && accountNameRef.current.value && emailRef.current.value && IDNumberRef.current.value && licenceNumberRef.current.value) {
            console.log("updated!")
        } */
    }

    return (
        <>
            <Tabs defaultActiveKey="PersonalInformation" id="uncontrolled-tab-example" className="tabPane">
                <Tab eventKey="PersonalInformation" title="Personal Information">
                    <Card className="customCardSm shadow m-5">
                        {/* <Card.Header>
                            Personal Information
                        </Card.Header> */}
                        <Card.Body>

                            <Container fluid>
                                <Row>
                                    <Col lg={6}>
                                        <Table className="infoTable">
                                            <tbody>
                                                <tr>
                                                    <th>Full Name:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={personalInfo?.fullName}
                                                        /* ref={fullNameRef} */
                                                        /* onChange={(event) => {setFullName(event.target.value); console.log(event.target.value)}} */
                                                        />
                                                        {/* {beginValidation && !fullNameCheck && <div className="required-error"><span>*</span></div>} */}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Email:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="email"
                                                            placeholder={personalInfo?.Email}
                                                        /* ref={emailRef} */
                                                        />
                                                        {/* {beginValidation && !emailCheck && <div className="required-error"><span>*</span></div>} */}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Phone Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={personalInfo?.phone}
                                                        /* ref={emailRef} */
                                                        />
                                                        {/* {beginValidation && !emailCheck && <div className="required-error"><span>*</span></div>} */}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>ID Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={personalInfo?.IDNumber}
                                                        /* ref={IDNumberRef} */
                                                        />
                                                        {/* {beginValidation && !IDNumberCheck && <div className="required-error"><span>*</span></div>} */}
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </Table>
                                    </Col>
                                    <Col lg={6}>
                                        <div className="w-100 d-flex justify-content-center">
                                            <div
                                                className="rounded-circle d-flex light-turquoise-bg wh-45px "
                                                style={{ width: "200px", height: "200px" }}
                                            >
                                                <img
                                                    className="user-img-details"

                                                    src={personalInfo?.img ? "http://46.253.93.4/togo/MobileAPi/" + personalInfo.img : "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}

                                                    alt="user-profile-pic"
                                                />
                                            </div>
                                        </div>
                                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "1.5rem" }}>{/* fullName */ personalInfo?.fullName}</div>
                                    </Col>
                                </Row>
                            </Container>
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn" onClick={updatePersonalInfo}>
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
                <Tab eventKey="businessInformation" title="Business Information">
                    <Card className="customCardSm shadow m-5">
                        {/* <Card.Header>
                            Working Time and Business Location
                        </Card.Header> */}
                        <Card.Body>
                            <Container fluid>
                                <Row>
                                    <Col lg={12}>
                                        <Table className="infoTable">
                                            <tbody>
                                                <tr>
                                                    <th>Business Name:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={businessInfo?.BusinessName}
                                                        /* ref={fullNameRef} */
                                                        /* onChange={(event) => {setFullName(event.target.value); console.log(event.target.value)}} */
                                                        />
                                                        {/* {beginValidation && !fullNameCheck && <div className="required-error"><span>*</span></div>} */}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Business Location:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="email"
                                                            placeholder={businessInfo?.BusinessPlace}
                                                        /* ref={emailRef} */
                                                        />
                                                        {/* {beginValidation && !emailCheck && <div className="required-error"><span>*</span></div>} */}
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Business Type:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={businessInfo?.BusinessType === "1" ? "شركة" : "مؤسسة"}
                                                        /* ref={emailRef} */
                                                        />
                                                        {/* {beginValidation && !emailCheck && <div className="required-error"><span>*</span></div>} */}
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </Table>
                                    </Col>
                                </Row>
                            </Container>
                        </Card.Body>
                        <Card.Footer>
                            <Button className="update-btn">
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
            </Tabs>
        </>
    )
}

