import { motion } from "framer-motion";
import React, { useState, useEffect } from "react";
import { Badge, Form } from "react-bootstrap";

export default function RecordsCard(props) {

    const [records, setRecords] = useState(props.list);

    useEffect(() => {
        setRecords(props.list)
    }, [props.list])

    const filterHandler = (val) => {

        let tempRecords = props.list.filter(record => record.order_id.includes(val.toLowerCase()));

        setRecords(tempRecords)
    }

    const formatAction = (record) => {

        const tempHolder = <div style={{ width: "40%" }}>
            <div style={{ fontWeight: "bold", color: "#03A65A" }}>{record.description}</div>
            <div>{record.time_stamp.split(" ")[0] + " "}<Badge bg="primary">{record.time_stamp.split(" ")[1]}</Badge></div>
        </div>

        switch (record.action_id) {
            case "2":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " created order " + record.order_id}</div>
                </>;
            case "3":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " bid on order " + record.order_id}</div>
                </>;
            case "4":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " cancel order " + record.order_id}</div>
                </>;
            case "5":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " updated bid on order " + record.order_id}</div>
                </>;
            case "6":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " canceled bid on order " + record.order_id}</div>
                </>;
            case "7":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " accepted " + record.party_two_name + "'s bid on order " + record.order_id}</div>
                </>;
            case "8":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " picked up order " + record.order_id}</div>
                </>;
            case "9":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " finished order " + record.order_id}</div>
                </>;
            case "10":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " assigned order " + record.order_id + " to " + record.party_two_name}</div>
                </>;
            case "11":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " rejected order " + record.order_id}</div>
                </>;
            case "12":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " accepted order " + record.order_id}</div>
                </>;
            case "17":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " canceled assign on order " + record.order_id}</div>
                </>;
            case "18":
                return <>
                    {tempHolder}
                    <div style={{ width: "60%" }}>{record.party_one_name + " created and assigned order " + record.order_id + " to " + record.party_two_name}</div>
                </>;
        }
    }

    return (
        <div style={{ position: "relative" }} className="d-flex flex-column mt-3 p-3 ">

            <p className="text-black">{props.title}</p>
            <Form.Control style={{ position: "absolute", right: "1.5rem", top: "5px", height: "25px" }} className="w-25" type="text" placeholder="Order ID..." onChange={(e) => filterHandler(e.target.value)} />


            <hr />
            <div className="h-100 overflow-auto w-100" style={{ fontFamily: "system-ui" }}>
                {
                    records?.length > 0 ? records.map((record, index) => {
                        return (<>
                            <motion.div
                                key={index}
                                initial={{ backgroundColor: "#fff" }}
                                whileHover={{ backgroundColor: "#f0fff0" }}
                                whileTap={{ scale: 0.95 }}
                                className="align-items-center d-flex gap-3 mb-2 w-100"
                            >
                                {formatAction(record)}

                            </motion.div>
                            <hr style={{ marginRight: "10px" }} />
                        </>
                        )
                    }) : <div>No Records!</div>
                }
            </div>
        </div>
    )
}
