import React, { useRef, useState } from "react";
import { Form, Row, Col, Container, Button, ListGroup } from 'react-bootstrap';

export default function TestComponent() {

    const textRef = useRef();
    const [listAreas, setListAreas] = useState([
        {number: "1", name: "name1"},
        {number: "2", name: "name2"},
        {number: "3", name: "name3"},
        {number: "4", name: "name4"},
    ]);

    const cleanHandler = () => {
        // console.log(textRef.current.value);

    }

    return (
        <Container fluid>
            <Row>
                <Col className="d-flex justify-content-center"><span className="h1">This is a test component!</span></Col>
            </Row>

            <Row>
                <Col lg={5}>
                    <Form.Group className="" controlId="exampleForm.ControlTextarea1">
                        <Form.Label>Enter options to clear:</Form.Label>
                        <Form.Control style={{ height: "300px", overflowY: "scroll" }} as="textarea" rows={100} ref={textRef} />
                    </Form.Group>
                </Col>

                <Col lg={2}>
                    <Button className="mt-5" onClick={cleanHandler}>
                        clean
                    </Button>
                </Col>

                <Col lg={5}>
                    <ListGroup>
                        {
                            listAreas.map((item, index) => {
                                return <ListGroup.Item key={index}>{item.number + " - " + item.name}</ListGroup.Item>
                            })
                        }
                    </ListGroup>
                </Col>
            </Row>
        </Container>
    )
}
