import { motion } from "framer-motion";
import React, { useState, useEffect } from "react";
import Form from 'react-bootstrap/Form';
import "./UserCard.css";
import Modal from "react-bootstrap/Modal";
import { Accordion, Card, Button, Badge } from "react-bootstrap";
import translate from "../../i18n/translate";
import { useHistory } from "react-router";

export default function UsersCard(props) {

    const history = useHistory();

    const [users, setUsers] = useState(props.list);

    useEffect(() => {
        setUsers(props.list)
    }, [props.list])

    /* using search bar, filter users (transporters/clients) by their names or phone numbers */
    const filterHandler = (val) => {
        let tempUsers = props.list.filter(user => user.FullName.toLowerCase().includes(val.toLowerCase()) || user.PhoneNumber.includes(val));
        setUsers(tempUsers)
    }

    return (
        <div
            className="h-100 d-flex flex-column bg-white  p-3 rounded-22 shadow">

            <p className="text-black">{props.title}</p>

            <Form.Control className="mt-3 mb-3" type="text" placeholder="search..." onChange={(e) => filterHandler(e.target.value)} />

            <div style={{ overflowY: "auto", overflowX: "hidden", fontFamily: "system-ui" }}>
                {
                    users?.map((user, index) => {
                        return (
                            <motion.div
                                key={index}
                                onClick={() => { history.push("/customerInfo/" + user.id + "/" + (user.IsTransporter === "1" ? "1" : "0")) }}
                                variants={{
                                    mount: { opacity: 0.8 },
                                    rest: { opacity: 1 }
                                }}
                                initial='mount'
                                whileHover={{ opacity: 1 }}
                                className="d-flex gap-3 align-items-center d-flex gap-3 text-nowrap mb-2 userSelect">
                                <div
                                    className="rounded-circle d-flex light-turquoise-bg wh-45px "
                                >
                                    <img
                                        className="user-img"

                                        src={user.PersonalImgPath ? "http://46.253.93.4/togo/MobileAPi/" + user.PersonalImgPath : "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}

                                        alt="user-profile-pic"
                                    />
                                </div>
                                <div style={{ width: "40%" }}>
                                    <div>{user.FullName}</div>
                                    <div>{user.PhoneNumber}</div>
                                </div>

                            </motion.div>
                        )
                    })
                }
            </div>
        </div>
    )
}
