import React, {useCallback, useEffect, useState} from "react";
import Nav from "react-bootstrap/Nav";

import {getFunctions} from "../../APIs/OrdersAPIs";
import {OrdersTabularView} from "./OrdersTabularView";
import {Box} from "@chakra-ui/react";
import Invoice from "../Invoice/Invoice";
import {isTeamMember, isTransporter, isTransporterMaster} from "../../Util";
import Loader from "../Loader/Loader";
import {toastMessage} from "../../Actions/GeneralActions";
import translate from "../../i18n/translate";
import {useHistory} from "react-router-dom";
import PaginateComp from "../Pagination/Pagination";

import newproduct from "../../assets/images/new-product.png";
import processingOrder from "../../assets/images/processing-time.png";
import receipt from "../../assets/images/receipt.png";
import processedOrder from "../../assets/images/order-processed.png";
import voucher from "../../assets/images/voucher.png";
import think from "../../assets/images/affiliate-marketing.png";

import AdminBoard from "../AdminBoard";
import { CreateNewOrder } from "../CreateNewOrder";
import "./Main.css";
import CreateAddress from "../CreateAddress";
import { Button } from "react-bootstrap";
import Network from "../Network";
import NetworkClient from "../NetworkClient";
import { PAGE_SIZE } from "../../APIs/AuthenticationAPIs";
//import OrdersCardView from "./OrdersCardView";

import Transactions from "../FinancialTransactions/FinancialTransactions"; /* edited (import Transactions) */

import {useSelector, useDispatch} from "react-redux";
import {getPersonalInfo} from "../../Actions/ProfileActions"; /* edited (new import) */

const styles = {
    navImg: {
        width: "30px",
        height: "30px",
        display: "inline-block",

        verticalAlign: "middle",
    },
    navLal: {
        display: "inline-block",
        margin: "0 5px",
        verticalAlign: "middle",
        color: "var(--bs-dark)"
    },
    nameDisplay: {
        position: "absolute",
        top: "6.5rem",
        right: "1rem",
        fontSize: "1rem",
        color: "white"
    }
};

const getCurrentPage = (history) => {
    const pathname = history.location.pathname;
    return pathname.substr(pathname.lastIndexOf('/') + 1, pathname.length);
}

const Main = () => {

    let myDate = new Date();
    let hrs = myDate.getHours();

    let greet;

    if (hrs < 12)
        greet = 'Good Morning';
    else if (hrs >= 12 && hrs <= 17)
        greet = 'Good Afternoon';
    else if (hrs >= 17 && hrs <= 24)
        greet = 'Good Evening';

    let history = useHistory();

    const [currentPage, setCurrentPage] = useState(getCurrentPage(history) ?? (isTeamMember() ? "current-orders" : "all-orders"));
    const [orders, setOrders] = useState([]);
    const [totalNumOfRecs, setTotalNumberOfRecs] = useState(0);
    const [activePage, setActivePage] = useState(0);
    const [invoices, setInvoices] = useState([]);
    // const [transactions, setTransactions] = useState([]); /* edited (add transactions to state) */
    const [loading, setLoading] = useState(false);
    const [refresh, setRefresh] = useState(false);
    const dispatch = useDispatch();

    const cleanUp = () => {
        setOrders([]);
        setInvoices([]);
        // setTransactions([]); /* edited (setTransactions([]) added) */
    };

    useEffect(() => {
        let currentPage = getCurrentPage(history);
        if (!currentPage || currentPage === '/main') {
            currentPage = isTeamMember() ? "current-orders" : "all-orders";
            let currentPagePath = `/account/main/${currentPage}`;
            history.push(currentPagePath);
        }
        setCurrentPage(currentPage);

        let historyUnListen = history.listen(location => {
            const pathname = location.pathname;
            let currentPageTmp = pathname.substr(pathname.lastIndexOf('/') + 1, pathname.length);
            if (!currentPageTmp) {
                currentPageTmp = isTeamMember() ? "current-orders" : "all-orders";
                let currentPagePath = `account/main/${currentPageTmp}`;
                history.push(currentPagePath);
            }
            setCurrentPage(currentPageTmp);
        });

        return () => {
            historyUnListen();
            cleanUp();
        }
    }, [history]);

    const fetchData = useCallback((functionType, stateKey) => {
        setLoading(true);
        getFunctions(functionType, activePage)
            .then(resp => {
                if (resp === 'NotActiveNow') {
                    dispatch(toastMessage(translate("GENERAL.COULD_NOT_FETCH"), translate("GENERAL.ERROR")));
                } else {
                    const {data: {server_response, total_orders}} = resp;
                    switch (stateKey) {
                        case 'orders':
                            setTotalNumberOfRecs(total_orders);
                            setOrders(server_response);
                            break;
                        case 'invoices':
                            // console.log("Invoices: ");
                            // console.log(server_response);
                            setInvoices(server_response);
                            break;
                        /*case 'transactions':  edited (add transactions case) 
                            console.log("Transactions: ");
                            console.log(server_response);
                            setTransactions(server_response);*/
                        default:
                            setTotalNumberOfRecs(0);
                            break;
                    }
                }
            })
            .catch((err) => {
                dispatch(toastMessage(err));
            }).finally(() => {
                setLoading(false);
            });
    }, [dispatch, activePage]);

    useEffect(() => {
        let stateKey, functionType = null;
        cleanUp();
        switch (currentPage) {
            case "all-orders":
                stateKey = "orders";
                functionType = isTransporter() ? "TransporterGetOrder" : "ShowClientOrder";
                break;
            case "current-orders":
                stateKey = "orders";
                functionType = isTransporter() ? "TransporterOrderCurrent" : "ClientShowBidRequistsAccepted";
                break;
            case "previous-orders":
                stateKey = "orders";
                functionType = isTransporter() ? "TransporterHistoryOrder" : "ClientHistoryOrder";
                break;
            case "invoices":
                stateKey = "invoices";
                functionType = isTransporter() ? "getTransporterInvoices" : "getClientInvoices";
                break;
            case "transactions": /* edited (add transactions case) */
                stateKey = "transactions";
                functionType = isTransporter() ? "TeamMemberTransaction" : null;
                break;
            default:
                break;
        }
        if(functionType){
            fetchData(functionType, stateKey);
        }
    }, [currentPage, fetchData, refresh]);

    const dispatch1 = useDispatch();
    const {FullName} = useSelector(state => state.profile.personalInfo);
    // const [loading, setLoading] = useState(true);
    const isTransporterVal = isTransporter();

    useEffect(() => {
        dispatch(getPersonalInfo(() => {
            setLoading(false);
        }));
    }, [dispatch1]);

    const name = FullName;

    return (
        <div>
            <div className="d-flex justify-content-between" style={styles.nameDisplay}>
                        {greet === "Good Morning" ? <svg style={{ marginTop: "2px", marginRight: "5px", color: "yellow" }} xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-brightness-high" viewBox="0 0 16 16">
                          <path d="M8 11a3 3 0 1 1 0-6 3 3 0 0 1 0 6zm0 1a4 4 0 1 0 0-8 4 4 0 0 0 0 8zM8 0a.5.5 0 0 1 .5.5v2a.5.5 0 0 1-1 0v-2A.5.5 0 0 1 8 0zm0 13a.5.5 0 0 1 .5.5v2a.5.5 0 0 1-1 0v-2A.5.5 0 0 1 8 13zm8-5a.5.5 0 0 1-.5.5h-2a.5.5 0 0 1 0-1h2a.5.5 0 0 1 .5.5zM3 8a.5.5 0 0 1-.5.5h-2a.5.5 0 0 1 0-1h2A.5.5 0 0 1 3 8zm10.657-5.657a.5.5 0 0 1 0 .707l-1.414 1.415a.5.5 0 1 1-.707-.708l1.414-1.414a.5.5 0 0 1 .707 0zm-9.193 9.193a.5.5 0 0 1 0 .707L3.05 13.657a.5.5 0 0 1-.707-.707l1.414-1.414a.5.5 0 0 1 .707 0zm9.193 2.121a.5.5 0 0 1-.707 0l-1.414-1.414a.5.5 0 0 1 .707-.707l1.414 1.414a.5.5 0 0 1 0 .707zM4.464 4.465a.5.5 0 0 1-.707 0L2.343 3.05a.5.5 0 1 1 .707-.707l1.414 1.414a.5.5 0 0 1 0 .708z"/>
                        </svg> : greet === "Good Afternoon" ? <svg style={{ marginTop: "2px", marginRight: "5px", color: "orange" }} xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-brightness-alt-high" viewBox="0 0 16 16">
                          <path d="M8 3a.5.5 0 0 1 .5.5v2a.5.5 0 0 1-1 0v-2A.5.5 0 0 1 8 3zm8 8a.5.5 0 0 1-.5.5h-2a.5.5 0 0 1 0-1h2a.5.5 0 0 1 .5.5zm-13.5.5a.5.5 0 0 0 0-1h-2a.5.5 0 0 0 0 1h2zm11.157-6.157a.5.5 0 0 1 0 .707l-1.414 1.414a.5.5 0 1 1-.707-.707l1.414-1.414a.5.5 0 0 1 .707 0zm-9.9 2.121a.5.5 0 0 0 .707-.707L3.05 5.343a.5.5 0 1 0-.707.707l1.414 1.414zM8 7a4 4 0 0 0-4 4 .5.5 0 0 0 .5.5h7a.5.5 0 0 0 .5-.5 4 4 0 0 0-4-4zm0 1a3 3 0 0 1 2.959 2.5H5.04A3 3 0 0 1 8 8z"/>
                        </svg> : <svg style={{ marginTop: "2px", marginRight: "5px", color: "blue" }} xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-moon-stars" viewBox="0 0 16 16">
                          <path d="M6 .278a.768.768 0 0 1 .08.858 7.208 7.208 0 0 0-.878 3.46c0 4.021 3.278 7.277 7.318 7.277.527 0 1.04-.055 1.533-.16a.787.787 0 0 1 .81.316.733.733 0 0 1-.031.893A8.349 8.349 0 0 1 8.344 16C3.734 16 0 12.286 0 7.71 0 4.266 2.114 1.312 5.124.06A.752.752 0 0 1 6 .278zM4.858 1.311A7.269 7.269 0 0 0 1.025 7.71c0 4.02 3.279 7.276 7.319 7.276a7.316 7.316 0 0 0 5.205-2.162c-.337.042-.68.063-1.029.063-4.61 0-8.343-3.714-8.343-8.29 0-1.167.242-2.278.681-3.286z"/>
                          <path d="M10.794 3.148a.217.217 0 0 1 .412 0l.387 1.162c.173.518.579.924 1.097 1.097l1.162.387a.217.217 0 0 1 0 .412l-1.162.387a1.734 1.734 0 0 0-1.097 1.097l-.387 1.162a.217.217 0 0 1-.412 0l-.387-1.162A1.734 1.734 0 0 0 9.31 6.593l-1.162-.387a.217.217 0 0 1 0-.412l1.162-.387a1.734 1.734 0 0 0 1.097-1.097l.387-1.162zM13.863.099a.145.145 0 0 1 .274 0l.258.774c.115.346.386.617.732.732l.774.258a.145.145 0 0 1 0 .274l-.774.258a1.156 1.156 0 0 0-.732.732l-.258.774a.145.145 0 0 1-.274 0l-.258-.774a1.156 1.156 0 0 0-.732-.732l-.774-.258a.145.145 0 0 1 0-.274l.774-.258c.346-.115.617-.386.732-.732L13.863.1z"/>
                        </svg>}
                        { greet } 
                        <span style={{ fontWeight: "lighter", marginRight: "10px", marginLeft: "10px" }}>
                            {/*PersonalDetailsOut()[0].value*/}
                            {name}
                        </span>
                    </div>

            {!isTransporter() && <Box d="flex" justifyContent="right" py="2" px="3">
                <div className="m-inline-e-2">
                    <CreateAddress>
                        <Button
                            className="font-weight-bold btn-grad text-white"
                            variant=""
                            >
                            {translate("ORDERS.ADD_ADDRESS")}
                        </Button>
                    </CreateAddress>
                </div>
                <CreateNewOrder onSuccess={()=>{
                    setRefresh(!refresh);
                }}/>
            </Box>}
            {/* edited (add "CreateNewOrder") button for transporter */}
            {isTransporter() && <Box d="flex" justifyContent="right" py="2" px="3">
                <CreateNewOrder onSuccess={()=>{
                    setRefresh(!refresh);
                }}/>
            </Box>}
            <div> {/* edited (style={{marginBottom: 20, marginTop: 20}} removed) tabs hovering issue*/}
                <Nav
                    justify
                    variant="tabs"
                    defaultActiveKey={currentPage}
                    activeKey={currentPage}
                    onSelect={(eventKey) => history.push(`/account/main/${eventKey}`)}
                >
                    {!isTeamMember() && <Nav.Item>
                        <Nav.Link eventKey="all-orders">
                            <img src={newproduct} alt="new" style={styles.navImg} />
                            <div style={styles.navLal}>{translate("ORDERS.NEW_ORDERS")}</div>
                        </Nav.Link>
                    </Nav.Item>}
                    <Nav.Item>
                        <Nav.Link eventKey="current-orders">
                            <img src={processingOrder} alt="new" style={styles.navImg} />
                            <div style={styles.navLal}>{translate("ORDERS.IN_PROCESS_ORDER")}</div>
                        </Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                        <Nav.Link eventKey="previous-orders">
                            <img src={processedOrder} alt="new" style={styles.navImg} />
                            <div style={styles.navLal}>
                                {translate("ORDERS.COMPLETED_OR_CANCELED_ORDER")}
                            </div>
                        </Nav.Link>
                    </Nav.Item>

                    {/* edited (uncomment transactions tab) */}

                     <Nav.Item> 
                        <Nav.Link eventKey="transactions">
                            <img src={receipt} alt="new" style={styles.navImg} />
                            <div style={styles.navLal}>
                                {translate("ORDERS.FINANCIAL_TRANSACTIONS")}
                            </div>
                        </Nav.Link>
                    </Nav.Item> 

                    {/* edited (comment invoices tab) */}

                    {/* <Nav.Item>
                        <Nav.Link eventKey="invoices">
                            <img src={voucher} alt="new" style={styles.navImg} />
                            <div style={styles.navLal}>
                                {translate("ORDERS.INVOICES")}
                            </div>
                        </Nav.Link>
                    </Nav.Item>*/}

                    {/* edited (comment MANAGE TEAMS tab) */}

                    {/*{isTransporter() && <Nav.Item>
                        <Nav.Link eventKey="admin" disabled={!isTransporterMaster()}>
                            <img src={think} alt="new" style={styles.navImg} />
                            <div style={styles.navLal}>
                                {translate("ADMIN.MANAGE")}
                            </div>
                        </Nav.Link>
                    </Nav.Item>}*/}
                    {isTransporter() && <Nav.Item>
                        <Nav.Link eventKey="network">
                            <img src={think} alt="new" style={styles.navImg} />
                            <div style={styles.navLal}>
                                {translate("NETWORK.NETWORK_TITLE")}
                            </div>
                        </Nav.Link>
                    </Nav.Item>}

                {/* edited (add network tab for client) */}

                {/*{!isTransporter() && <Nav.Item>
                        <Nav.Link eventKey="network-client">
                            <img src={think} alt="new" style={styles.navImg} />
                            <div style={styles.navLal}>
                                {translate("NETWORK.NETWORK_TITLE")}
                            </div>
                        </Nav.Link>
                    </Nav.Item>}*/}
                </Nav>
            </div>

            <Box height="400px">
                {loading ? <Loader/> : (
                    <>
                        {!!orders && currentPage.indexOf("orders") !== -1 &&
                        <>
                            {/* <OrdersCardView orders={orders} /> */}
                            <OrdersTabularView
                                orders={orders}
                                currentPage={currentPage}
                                update={()=>{setRefresh(!refresh);}}
                            />
                            <PaginateComp
                                totalNumOfRecs={totalNumOfRecs}
                                pageSize={PAGE_SIZE}
                                activePage={activePage}
                                setActive={setActivePage}
                            />
                        </>
                        }

                        {currentPage === "admin" && isTransporter() && <AdminBoard />}

                        {currentPage === "network" && isTransporter() && <Network />}

                        {currentPage === "network-client" && !isTransporter() && <NetworkClient />}

                        {currentPage === "transactions" && <Transactions />}

                        {!!invoices.length && <Invoice invoices={invoices} /> }
                    </>
                )}
            </Box>
        </div>
    );
};

export default Main;
