import React, { useEffect, useRef, useState} from "react";
import Address from "../Address/Address";
import {Accordion, Card, Button} from "react-bootstrap";


import {FinalizeOrder} from "../OrdersManager/FinalizeOrder";
import {CancelOrder} from "../OrdersManager/CancelOrder";
import {AcceptOrder} from "../OrdersManager/AcceptOrder";
import {DeleteTripCost} from "../OrdersManager/DeleteTripCost";
import {EditTripCost} from "../OrdersManager/EditTripCost";
import Loader from "../Loader/Loader";
import "./OrderDetails.css";
import {getOrderDetails, checkTripCost} from "../APIs/OrdersAPIs";
import translate from "../../i18n/translate";

import { Alert, AlertDescription, AlertIcon, AlertTitle } from "@chakra-ui/alert";
import { Box } from "@chakra-ui/layout";
import { deliverFormatter, packageFormatter } from "../Orders/OrdersTabularView";
import { isTransporter, isTransporterMaster } from "../../Util";
import AssignToTransporter from "../AssigneToTransporter";

import { useLocation, useParams } from "react-router";
import { Link } from "react-router-dom";
var Barcode = require('react-barcode');

const OrderDetails = () => {

    const {id: orderId} = useParams();
    const location = useLocation();
    const [orderDetails, setOrderDetails] =  useState({});
    const [previousPage, setPreviousPage] = useState(null);
    const [tripCost, setTripCost] = useState(null);
    const [costSet, setCostSet] = useState(null);
    const printlink = useRef(null);
    useEffect(() => {
        checkTripCost(orderId).then((tripCostRes) => {
            if (tripCostRes && tripCostRes !== "CostNotSend") {
                setTripCost(tripCostRes.server_response[0].CostDelivery);
                setCostSet(true);
            } else {
                setCostSet(false);
            }
        });

        getOrderDetails(orderId).then((orderDetailsRes) => {
            setOrderDetails(orderDetailsRes);
        });
        
        setPreviousPage(location.state?.currentPage);
    }, []);

    
    if (typeof orderDetails === "object" && JSON.stringify(orderDetails) !== '{}') {
        let {
            deliveryWay,
            HeightLoad,
            DetailsLoad,
            LengthLoad,
            WeightLoad,
            WidthLoad,
            OtherDetails,
            TypeLoad,
            OtherDetailsDes,
            PhoneCustomer,
            ReceiverAddressNum,
            LongReciver, LongSender, LatReciver, LatSender,
            AssignStatus, AssignedMemberName
        } = orderDetails;

        let senderAddress = {
            otherDetails: OtherDetails,
            phoneCustomer: PhoneCustomer,
            long: LongSender,
            lat: LatSender
        };

        let receiverAddress = {
            otherDetails: OtherDetailsDes,
            phoneCustomer: ReceiverAddressNum,
            long: LongReciver,
            lat: LatReciver
        };

        return (
            <div style={{marginRight: "10%", marginLeft: "10%", marginTop:"5%", overflowY: "auto"}}>
                
                {orderDetails && (
                    <div>
                        <div className="d-flex">
                            <div className="w-50">
                                <div className="d-flex align-items-center">
                                    <div className="w-250px">{translate("ORDER_DETAILS.ORDER_NUMBER")}</div>
                                    <span>{orderId}</span>
                                </div>
                                <div className="d-flex align-items-center">
                                    <div className="w-250px">{translate("ORDER_DETAILS.DELIVERY_TYPE")}</div>
                                    <div>{deliverFormatter({DeliveryWays: deliveryWay})}</div>
                                </div>
                                <div className="d-flex align-items-center">
                                    <div className="w-250px">{translate("ORDER_DETAILS.PACKAGE_TYPE")}</div>
                                    <div>{packageFormatter({PackageType: TypeLoad})}</div>
                                </div>
                                {isTransporterMaster() && AssignStatus === "Assigned" && <div className="d-flex">
                                    <div className="w-250px align-items-center">{translate("ORDER_DETAILS.ASSIGNED")}</div>
                                    <div>{AssignedMemberName}</div>
                                </div>}
                                
                                {TypeLoad !== "1" && (
                                    <>
                                        <div className="d-flex align-items-center">
                                            <div className="w-250px">{translate("ORDER_DETAILS.LOAD_HEIGHT")}</div>
                                            <div>{HeightLoad || <hr style={{width: "70px"}}/>}</div>
                                        </div>
                                        <div className="d-flex align-items-center">
                                            <div className="w-250px">{translate("ORDER_DETAILS.LOAD_WIDTH")}</div>
                                            <div>{WidthLoad || <hr style={{width: "70px"}}/>}</div>
                                        </div>
                                        <div className="d-flex align-items-center">
                                            <div className="w-250px">{translate("ORDER_DETAILS.LOAD_LENGTH")}</div>
                                            <div>{LengthLoad || <hr style={{width: "70px"}}/>}</div>
                                        </div>
                                        <div className="d-flex align-items-center">
                                            <div className="w-250px">{translate("ORDER_DETAILS.LOAD_WEIGHT")}</div>
                                            <div>{WeightLoad || <hr style={{width: "70px"}}/>}</div>
                                        </div>
                                        <div className="d-flex align-items-center">
                                            <div className="w-250px">{translate("ORDER_DETAILS.LOAD_DETAILS")}</div>
                                            <div>{DetailsLoad || <hr style={{width: "70px"}}/>}</div>
                                        </div>
                                    </>
                                )}   
                            </div>
                            <div>
                                {orderDetails.BarCode && <Barcode value={orderDetails.BarCode} />}
                            </div>
                        </div> 
                        <div className="d-flex">
                            <Accordion defaultActiveKey="0" className="w-50 m-inline-e-2">
                                <Accordion.Item 
                                    eventKey="0"
                                    className="w-100"
                                >
                                    <Accordion.Header as="div">
                                        {translate("ORDER_DETAILS.SENDER_ADDRESS")}
                                    </Accordion.Header>
                                    <Accordion.Body>
                                                
                                        <Address address={senderAddress}/>
                                                    
                                    </Accordion.Body>
                                            
                                </Accordion.Item>
                            </Accordion>

                            <Accordion defaultActiveKey="0" className="w-50">
                                <Accordion.Item 
                                    eventKey="0"
                                    className="w-100"
                                >
                                    <Accordion.Header as="div">
                                        {translate("ORDER_DETAILS.RECEIVER_ADDRESS")}
                                    </Accordion.Header>
                                    <Accordion.Body>
                                                
                                        <Address address={receiverAddress} />
                                                    
                                    </Accordion.Body>
                                            
                                </Accordion.Item>
                            </Accordion>
                        </div>
                        <Box mt="10px">
                            {previousPage === "all-orders" && costSet && (
                                <div>
                                    <Card
                                        bg={"Danger"}
                                        text={"black"}
                                    >
                                        <Card.Header>{translate("ORDER_DETAILS.BID_PRICE")}</Card.Header>
                                        <Card.Body>
                                            <Card.Title>{tripCost}</Card.Title>
                                            {/* <Card.Text></Card.Text> */}
                                        </Card.Body>
                                    </Card>
                                </div>
                            )}
                            {previousPage === "all-orders" && costSet !== null && costSet && (
                                <div className="ActionButtons">
                                    <DeleteTripCost orderId={orderId}/>
                                    <EditTripCost orderId={orderId} tripCost={tripCost}/>
                                </div>
                            )}
                            {previousPage === "all-orders" && !isTransporter() && <Button onClick={()=>{
                                //printlink.current.click();
                                window.open("/account/bill-for-order/" + orderId + "?print=false")
                            }}>
                                {translate("ORDER_DETAILS.PRINT")}
                            </Button>}
                            
                            {/* {previousPage === "all-orders" && 
                                <Link 
                                    ref={printlink}
                                    target="_blank" 
                                    to={{
                                        pathname: "/account/bill-for-order/" + orderId,
                                        search: "?print=true"
                                    }}
                                >
                                Print
                            </Link>} */}

                            {/* {previousPage === "all-orders" && costSet && <EditCost />} */}

                            {previousPage === "all-orders" &&
                            costSet !== null && isTransporter() &&
                            !costSet && <AcceptOrder orderId={orderId}/>}

                            {previousPage === "current-orders" && (
                                <Box display="flex" alignItems="center" justifyContent="end">
                                    {isTransporterMaster() && 
                                    <AssignToTransporter 
                                        onSuccess={()=>{
                                            getOrderDetails(orderId).then((orderDetailsRes) => {
                                                setOrderDetails(orderDetailsRes);
                                            });
                                        }}
                                        orderId={orderId} 
                                        />
                                    }
                                    {!isTransporterMaster() && 
                                    <>
                                        <FinalizeOrder orderId={orderId} />
                                        <CancelOrder orderId={orderId} className="ml-1" />
                                    </>
                                    }
                                </Box>
                            )}
                        </Box>
                    </div>
                )}
            </div>
        );
    } else if(typeof orderDetails === "string") {
        return (
            <Alert status="error" w="80%" m="10px auto">
                <AlertIcon />
                <AlertTitle mr={2}>{orderId}</AlertTitle>
                <AlertDescription>{orderDetails}</AlertDescription>
            </Alert>
        );
    } else {
        return <Box height="400px"><Loader /></Box>
    }
}

export default OrderDetails;
