import React, { useEffect, useState} from "react";
import Address from "../Address/Address";
import Map from "../Address/Map"; /* edited (Map imported) */
import {Accordion, Card, Button, Badge } from "react-bootstrap"; /* edited (Badge imported) */


import {FinalizeOrder} from "../OrdersManager/FinalizeOrder";
import {CancelOrder} from "../OrdersManager/CancelOrder";
import {BidOrder} from "../OrdersManager/AcceptOrder";
import {DeleteTripCost} from "../OrdersManager/DeleteTripCost";
import {EditTripCost} from "../OrdersManager/EditTripCost";
import Loader from "../Loader/Loader";
import "./OrderDetails.css";
import {getOrderDetails, checkTripCost, AcceptOfferReq, GetTransporterLocation} from "../../APIs/OrdersAPIs";
import translate from "../../i18n/translate";

import { Alert, AlertDescription, AlertIcon, AlertTitle } from "@chakra-ui/alert";
import { Box } from "@chakra-ui/layout";
import { deliverFormatter, packageFormatter } from "../Orders/OrdersTabularView";
import { isTeamMember, isTransporter, isTransporterMaster } from "../../Util";
import AssignToTransporter from "../AssigneToTransporter";

import { useLocation, useParams, useHistory } from "react-router";

import AssignToMemberOnNetworkDialog from "../AssginToMemberOnNetworkDialog";
import { refreshPage } from "../../Functions/CommonFunctions";

import Timeline from "../TimeLine/TimeLine"; /* edited (TimeLine imported) */

var Barcode = require('react-barcode');

const OrderDetails = () => {

    /* edited (dummy data to test the timeline) */
    const tempTransportationParties = [
        {
            type: 1,
            current: false
        },
        {
            type: 2,
            current: false
        },
        {
            type: 2,
            current: false
        },
        {
            type: 2,
            current: true
        },
        {
            type: 3,
            current: false
        }
    ]

    const {id: orderId} = useParams();
    const location = useLocation();
    const history = useHistory();
    const [orderDetails, setOrderDetails] =  useState({});
    const [previousPage, setPreviousPage] = useState(null);
    const [tripCost, setTripCost] = useState(null);
    const [costSet, setCostSet] = useState(null);
    const [receiverAddress, setReceiverAddress] = useState({});
    const [startTracking, setStartTracking] = useState(false);
    useEffect(()=>{
        let live = false;
        if(startTracking === false){
            return;
        }
        const intervalId = setInterval(()=>{
            if(!live){
                live = true;
                GetTransporterLocation(orderId).then((resp)=>{
                    live = false;
                    let {TransporterLatLocation, TransporterLongLoc} = resp.data.server_response[0];
                    if(TransporterLatLocation === null || TransporterLongLoc === null){
                        TransporterLatLocation = 0;
                        TransporterLongLoc = 0;
                    }
                    setReceiverAddress({
                        ...receiverAddress,
                        long: TransporterLongLoc,
                        lat: TransporterLatLocation
                    });
                });
            }
        }, 4000);
        return () => clearInterval(intervalId);
    }, [startTracking])
    useEffect(() => {
        checkTripCost(orderId, isTransporter()).then((tripCostRes) => {
            if (tripCostRes && tripCostRes !== "CostNotSend") {
                setTripCost(isTransporter() ? tripCostRes.server_response[0] : tripCostRes.CostDetail);
                setCostSet(true);
            } else {
                setCostSet(false);
            }
        });

        getOrderDetails(orderId).then((orderDetailsRes) => {
            setOrderDetails(orderDetailsRes);
            setReceiverAddress({
                otherDetails: orderDetailsRes.OtherDetailsDes,
                phoneCustomer: orderDetailsRes.ReceiverAddressNum,
                long: orderDetailsRes.LongReciver,
                lat: orderDetailsRes.LatReciver
            });
        });
        
        setPreviousPage(location.state?.currentPage);
    }, []);

    
    if (typeof orderDetails === "object" && JSON.stringify(orderDetails) !== '{}') {
        let {
			OrderStatus, /* edited (OrderStatus added) */
            DateLoad, /* edited (DateLoad added) */
            deliveryWay,
            HeightLoad,
            DetailsLoad,
            LengthLoad,
            WeightLoad,
            WidthLoad,
            OtherDetails,
            TypeLoad,
            PhoneCustomer,
            LongSender, LatSender,
            AssignStatus, AssignedMemberName,
            isAcceptDelivery
        } = orderDetails;

        let senderAddress = {
            otherDetails: OtherDetails,
            phoneCustomer: PhoneCustomer,
            long: LongSender,
            lat: LatSender
        };

        return (
            <div style={{ paddingBottom: "2%", marginLeft: "5%", marginRight: "5%" }}>
                
                {/* edited (container and row classes added to container divs) */}
                {orderDetails && (
                    <div className="container">
                        <div className="row">
                            {/* edited (action buttons moved to top) */}
                            {previousPage === "all-orders" && isTransporter() && tripCost && <Box style={{
                                marginTop: "20px",
                                marginBottom: "20px"
                            }}>
                                 
                                        <Card
                                            bg={"Danger"}
                                            text={"black"}
                                        >
                                            <Card.Header>{translate("ORDER_DETAILS.BID_PRICE")}</Card.Header>
                                            <Card.Body className="d-flex align-items-center">
                                                {tripCost.CostDelivery}
                                                <div className="ActionButtons ms-auto">
                                                    <DeleteTripCost orderId={orderId}/>
                                                    <EditTripCost orderId={orderId} tripCost={tripCost.CostDelivery}/>
                                                </div>
                                            </Card.Body>
                                        </Card>
                                
                            </Box>}
                            <Box style={{
                                marginTop: "20px",
                                marginBottom: "20px"
                            }}>
                                {previousPage === "all-orders" && !isTransporter() && tripCost && tripCost.length !== 0 && (
                                    <div>
                                        <Card
                                            bg={"Danger"}
                                            text={"black"}
                                        >
                                            <Card.Header>{translate("ORDERS.AVAILABLE_OFFERS")}</Card.Header>
                                            <Card.Body>
                                                {tripCost.map((costs)=>{
                                                    return <div className="align-items-center d-flex justify-content-between">
                                                        <img className="w-25" src={`http://46.253.93.4/togo/MobileAPi/${costs.TransporterPersonalImg}`} alt="transImg" />
                                                        <div>{costs.TransporterName}</div>
                                                        <Card.Title>{costs.BidCost}</Card.Title>
                                                        <Button className="btn-grad" onClick={()=>{
                                                            AcceptOfferReq(costs.IdTransporterBidRequist, orderId ,costs.BidCost).then((resp)=>{
                                                                if(resp.data.indexOf("Success") !== -1){
                                                                    refreshPage();
                                                                }
                                                            })
                                                        }}>Accept Offer</Button>
                                                    </div>
                                                })}
                                                {/* <Card.Title>{tripCost || translate("ORDERS.NO_OFFERS")}</Card.Title> */}
                                                {/* <Card.Text></Card.Text> */}
                                            </Card.Body>
                                        </Card>
                                    </div>
                                )}
                                
                                {previousPage === "all-orders" && !isTransporter() && 
                                    <div className="d-flex align-items-center">
                                        <Button 
                                            style={{ width: "150px", marginLeft: "10px", marginRight: "10px" }}
                                            className="ms-auto" 
                                            onClick={()=>{
                                                window.open("/account/bill-for-order/" + orderId + "?print=true")
                                            }}
                                        >
                                            {translate("ORDER_DETAILS.PRINT")}
                                        </Button>
                                        <CancelOrder orderId={orderId} className="mx-1" onSuccess={()=>{
                                            history.goBack();
                                        }} />
                                    </div>
                                }

                                {previousPage === "all-orders" &&
                                !costSet && isTransporter() && <div className="d-flex justify-content-end">
                                    <BidOrder orderId={orderId}/>
                                    </div>
                                }


                                {previousPage === "current-orders" && (
                                    <Box display="flex" alignItems="center" justifyContent="end">
                                        {isTransporterMaster() && AssignStatus !== "Assigned" &&
                                            <AssignToTransporter 
                                                onSuccess={()=>{
                                                    getOrderDetails(orderId).then((orderDetailsRes) => {
                                                        setOrderDetails(orderDetailsRes);
                                                    });
                                                }}
                                                orderId={orderId} 
                                            />
                                        }
                                        {isTransporter() &&
                                            <AssignToMemberOnNetworkDialog 
                                                onSuccess={()=>{
                                                   history.goBack();
                                                }}
                                                orderId={orderId} 
                                            />
                                        }
                                        
                                        {(isTeamMember() || (isTransporter() && !isTransporterMaster())) && 
                                            <div className="ms-1">
                                                <FinalizeOrder orderId={orderId} />
                                            </div>
                                        }
                                    </Box>
                                )}
                            </Box>
                        </div>

                        {/* edited (Transportation Timeline) */}
                        <div className="row">
                            <div className="d-flex justify-content-center">

                                <Timeline transportationParties={tempTransportationParties} />

                            </div>
                        </div>

						{/* edited (card container added to display order & load details) (barcode removed) */}
                        <Card
                            bg={"Danger"}
                            text={"black"}
                            className="mt-3"
                        >
                            <Card.Header className="d-flex justify-content-start" style={{ background: "linear-gradient(90deg, #26a69a, #69d4a5)", color: "white" }}>
                                <svg style={{ marginRight: "10px", marginLeft: "10px" }} xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-info-circle" viewBox="0 0 16 16">
                                    <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14zm0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16z" />
                                    <path d="m8.93 6.588-2.29.287-.082.38.45.083c.294.07.352.176.288.469l-.738 3.468c-.194.897.105 1.319.808 1.319.545 0 1.178-.252 1.465-.598l.088-.416c-.2.176-.492.246-.686.246-.275 0-.375-.193-.304-.533L8.93 6.588zM9 4.5a1 1 0 1 1-2 0 1 1 0 0 1 2 0z" />
                                </svg>
                                {translate("ORDER_DETAILS.ORDER_DETAILS")}
                            </Card.Header>
                            <Card.Body className="d-flex align-items-center">
                                <div className="container">
                                    <div className="row">
                                        <div className="col-lg-6">
                                            <div style={{}} className="d-flex align-items-center">
                                                <div style={{}} className="w-250px p-1">{translate("ORDER_DETAILS.ORDER_NUMBER")}</div>
                                                <span style={{ color: "#53c5a1" }}>{orderId}</span>
                                            </div>

                                            <hr className="defaultLine" />

                                            <div style={{}} className="d-flex align-items-center">
                                                <div style={{}} className="w-250px p-1">{translate("ORDER_DETAILS.DELIVERY_TYPE")}</div>
                                                <div style={{ color: "#53c5a1" }}>{deliverFormatter({ DeliveryWays: deliveryWay })}</div>
                                            </div>

                                            <hr className="defaultLine" />

                                            <div style={{}} className="d-flex align-items-center">
                                                <div style={{}} className="w-250px p-1">{translate("ORDER_DETAILS.PACKAGE_TYPE")}</div>
                                                <div style={{ color: "#53c5a1" }}>{packageFormatter({ PackageType: TypeLoad })}</div>
                                            </div>

                                            {/* to be edited */}

                                            {isTransporterMaster() && AssignStatus === "Assigned" && <div className="d-flex">
                                                <div style={{}} className="w-250px align-items-center">{translate("ORDER_DETAILS.ASSIGNED")}</div>
                                                <div>{AssignedMemberName}</div>
                                            </div>}
                                        </div>

                                        <hr className="showLine" style={{}} />

                                        <div className="col-lg-6">
                                            <div style={{}} className="d-flex align-items-center">
                                                <div style={{}} className="w-250px p-1">{translate("ORDERS.ORDER_STATUS")}</div>
                                                <Badge style={{ fontSize: '1em' }} bg="secondary">{OrderStatus}</Badge>
                                            </div>

                                            <hr className="defaultLine" />

                                            <div style={{}} className="d-flex align-items-center">
                                                <div style={{}} className="w-250px p-1">{translate("ORDERS.ORDER_DATE")}</div>
                                                <span style={{ color: "#53c5a1" }}>{DateLoad.split(" ")[0]}</span>
                                            </div>

                                            <hr className="defaultLine" />

                                            <div style={{}} className="d-flex align-items-center">
                                                <div style={{}} className="w-250px p-1">{translate("ORDERS.ORDER_TIME")}</div>
                                                <span style={{ color: "#53c5a1" }}>{DateLoad.split(" ")[1]}</span>
                                            </div>
                                        </div>

                                    </div>

                                    {TypeLoad !== "1" && (
                                        <>
                                            <hr className="mt-3" />
                                            <div className="row mt-4 d-flex justify-content-center">
                                                <table className="table table-striped">
                                                    <thead>
                                                        <tr>
                                                            <th scope="col">{translate("ORDER_DETAILS.LOAD_HEIGHT")}</th>
                                                            <th scope="col">{translate("ORDER_DETAILS.LOAD_WIDTH")}</th>
                                                            <th scope="col">{translate("ORDER_DETAILS.LOAD_LENGTH")}</th>
                                                            <th scope="col">{translate("ORDER_DETAILS.LOAD_WEIGHT")}</th>
                                                            <th scope="col">{translate("ORDER_DETAILS.LOAD_DETAILS")}</th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>
                                                        <tr>
                                                            <td>{HeightLoad}</td>
                                                            <td>{WidthLoad}</td>
                                                            <td>{LengthLoad}</td>
                                                            <td>{WeightLoad}</td>
                                                            <td>{DetailsLoad}</td>
                                                        </tr>
                                                    </tbody>
                                                </table>
                                            </div>
                                        </>
                                    )}
                                </div>
                            </Card.Body>
                        </Card>

                        {/* edited (Accordion with one map added) */}
                        <Accordion defaultActiveKey="0" className="mt-3">
                                    <Accordion.Item
                                        eventKey="0"
                                        className="w-100"
                                    >
                                        <Accordion.Header as="div">
                                            <svg style={{ marginRight: "10px", marginLeft: "10px" }} xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" className="bi bi-geo-alt" viewBox="0 0 16 16">
                                                <path d="M12.166 8.94c-.524 1.062-1.234 2.12-1.96 3.07A31.493 31.493 0 0 1 8 14.58a31.481 31.481 0 0 1-2.206-2.57c-.726-.95-1.436-2.008-1.96-3.07C3.304 7.867 3 6.862 3 6a5 5 0 0 1 10 0c0 .862-.305 1.867-.834 2.94zM8 16s6-5.686 6-10A6 6 0 0 0 2 6c0 4.314 6 10 6 10z" />
                                                <path d="M8 8a2 2 0 1 1 0-4 2 2 0 0 1 0 4zm0 1a3 3 0 1 0 0-6 3 3 0 0 0 0 6z" />
                                            </svg>
                                            {translate("TEMP.LOCATION")}
                                        </Accordion.Header>
                                        <Accordion.Body>

                                            <Map className="" address1={senderAddress} address2={receiverAddress} />

                                        </Accordion.Body>

                                    </Accordion.Item>
                                </Accordion>


						{/* edited (two maps were commented to be be replaced by one map) */}
                        {/*<div className="d-flex">
                                                    <Accordion defaultActiveKey="0" className="w-50 m-inline-e-2">
                                                        <Accordion.Item 
                                                            eventKey="0"
                                                            className="w-100"
                                                        >
                                                            <Accordion.Header as="div">
                                                                {translate("ORDER_DETAILS.SENDER_ADDRESS")}
                                                            </Accordion.Header>
                                                            <Accordion.Body>
                                                                        
                                                                <Address address={senderAddress}/>
                                                                            
                                                            </Accordion.Body>
                                                                    
                                                        </Accordion.Item>
                                                    </Accordion>
                        
                                                    <Accordion defaultActiveKey="0" className="w-50">
                                                        <Accordion.Item 
                                                            eventKey="0"
                                                            className="w-100"
                                                        >
                                                            <Accordion.Header as="div">
                                                                <div className="flex-grow-1">
