import React, { useEffect, useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import { GetNetworkInvitations } from "./APIs/OrdersAPIs";
import { InviteRow } from "./Network";

export function NetworkInvitesListDialog() {
    const [open, setOpen] = useState(false);
    const [invites, setInvites] = useState([]);
    const handleCloseInvites = () => {
        setOpen(false);
    }
    useEffect(()=>{
        if(open){
            GetNetworkInvitations().then((res)=>{
                console.log(res);
            })
        }
    }, [open]);
    return (
        <>
            <Button
                className="border me-2 rounded-22 togo-button"
                onClick={()=>{
                    setOpen(true);
                }}
            >
                Join a network
            </Button>
            <Modal show={open} onHide={handleCloseInvites} size="lg">
                {/* <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header> */}
                <Modal.Body>
                    {invites?.length === 0 && <p>There is no network invitations</p>}
                    <div>
                        <div className="align-items-center d-flex" style={{height: "200px"}}>
                            <div className={"w-250px me-2"}>
                                <img
                                    style={{maxHeight: "200px"}} 
                                    //src={`${imgBaseUrl}${member.PersonalImgPath}`} alt={member.PhoneNumber}  
                                />
                            </div>
                            <div className="d-flex flex-column flex-grow-1 h-100 justify-content-evenly">
                                <InviteRow 
                                    label={"Name"} 
                                    value={"iy"} 
                                    secLabel={"Load cost"}
                                    secValue={
                                        <Form.Control
                                            className="border-2 togo-border-full p-1"
                                            type="text"
                                        />
                                    }
                                />
                                <InviteRow
                                    label={"Phone number"} 
                                    value={"05965"} 
                                    secLabel={"Activation Code"}
                                    secValue={
                                        <Form.Control
                                            className="border-2 togo-border-full p-1"
                                            type="text"
                                        />
                                    }
                                />
                                <div className="d-flex justify-content-end mt-1">
                                    <Button className="border rounded-22 togo-button">
                                        Accept
                                    </Button>
                                </div>
                            </div>
                        </div>
                    </div>
                </Modal.Body>
            </Modal>
        </>
        
    )
}
