import React from 'react';
import Table from "react-bootstrap/Table";
import translate from '../../i18n/translate';

export default function DynamicTable({columns, data, onRowSelect, selected}) {

    // console.log(data); // temp test

    return (
        <Table hover size="sm" responsive className="ms-4 me-4 mt-2">
            <thead>
                <tr>
                    {columns.map(({label, key}, idx) => <th key={key+"-"+idx}>{label}</th>)}
                </tr>
            </thead>
            <tbody>
            {data.map((item, index) =>
                <tr 
                    key={index + "-tr"} 
                    onClick={onRowSelect ? ()=>{onRowSelect(item, index)} : undefined} 
                    className={`${selected === index ? "highlight-row": ""}`}>
                    {columns.map(({key, format}, idx) =>
                        <td key={key + "-td" + idx}>
                            {item[key] === "Waiting for bid" ? <span style={{ color: "red" }}>{!!format ? format(item) : item[key]}</span> : <span>{!!format ? format(item) : item[key]}</span>}
                        </td>
                    )}
                </tr>
            )}
            {!data.length && <tr><td colSpan={columns.length}>{translate("ORDERS.NO_ORDERS_FOUND")}</td></tr>}
            </tbody>
        </Table>
    );
};
