        //     <script>
        //         supersetEmbeddedSdk.embedDashboard(config);
        //     </script>
        // </Container>

        
        // <Container>
        //     <script>
        //         embedDashboard(config)
        //     </script>
        // </Container>


        // https://superset.zone.ps/superset/dashboard/6/?standalone=true
        // https://superset.zone.ps/login/?next=http%3A%2F%2Fsuperset.zone.ps%2Fsuperset%2Fdashboard%2F5%2F%3Fstandalone%3Dtrue
        <iframe width="100%" height="1000" frameBorder="0" src="https://superset.zone.ps/login/?next=https%3A%2F%2Fsuperset.zone.ps%2Fsuperset%2Fdashboard%2F6%2F%3Fstandalone%3Dtrue"> </iframe>
    )
} */

export default function SupersetComponent() {

    // loading variable, to show loading spinner until api call is done
    const [loading, setLoading] = useState(false);

    // to call and load superset api once
    useEffect(() => {

        // set loading as true, to show loading spinner
        setLoading(true);

        // get guest-token api
        fetchSupersetGusetToken().then(res => {

            // temp test
            console.log(res.data.token);

            // store fetched token in a variable
            const guestTokent = res.data.token;

            // set loading as false, to hide loading spinner
            setLoading(false);

            // loading superset stuff...
            embedDashboard({
                id: "63e35e3d-df1b-4c89-b9ce-bdc0a691e2a6", // given by the Superset embedding UI
                supersetDomain: "https://superset.zone.ps",
                mountPoint: document.getElementById("my-superset-container"), // any html element that can contain an iframe
                fetchGuestToken: () => { return guestTokent },
                dashboardUiConfig: { hideTitle: true } // dashboard UI config: hideTitle, hideTab, hideChartControls (optional)
            });

        })

    }, []);
    // guestTokent = fetchSupersetGusetToken().then(res => res.data.token)
    console.log(guestTokent)
    return (
        // !loading ? /* superset container */
        //     <div width="100%" height="1000" frameBorder="0" id="my-superset-container"></div> :
        //     <div className="d-flex mt-5 justify-content-center"><Spinner animation="border" size="lg" /></div>
        // <iframe width="100%" height="1000" frameBorder="0" src="https://superset.zone.ps/login/?next=https%3A%2F%2Fsuperset.zone.ps%2Fsuperset%2Fdashboard%2F6%2F%3Fstandalone%3Dtrue"> </iframe>
        
        embedDashboard({
            id: "63e35e3d-df1b-4c89-b9ce-bdc0a691e2a6", // given by the Superset embedding UI
            supersetDomain: "https://superset.zone.ps",
            mountPoint: document.getElementById("my-superset-container"), // any html element that can contain an iframe
            fetchGuestToken: () => guestTokent ,
            dashboardUiConfig: { hideTitle: true } // dashboard UI config: hideTitle, hideTab, hideChartControls (optional)
        })
    )
}
