// Import the functions you need from the SDKs you need
import { initializeApp } from "firebase/app";
import { getMessaging, getToken, onMessage } from 'firebase/messaging';
// TODO: Add SDKs for Firebase products that you want to use
// https://firebase.google.com/docs/web/setup#available-libraries

// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
    apiKey: "AIzaSyB21PuWroOOIdEYUlmJelSSD1DbAeN67gA",
    authDomain: "togo2-e12ef.firebaseapp.com",
    projectId: "togo2-e12ef",
    storageBucket: "togo2-e12ef.appspot.com",
    messagingSenderId: "5515795953",
    appId: "1:5515795953:web:5e28afa58d2df527bd7850",
    measurementId: "G-Q34XC6JC0M"
};

// Initialize Firebase
const firebaseApp = initializeApp(firebaseConfig);
const messaging = getMessaging(firebaseApp);
const { REACT_APP_VAPID_KEY } = process.env;
const publicKey = "BIgIuQxgc1B9V1P731jMcUlUVp9m4KHiD1or8I44NrKJh2udSZYII6S6XMYIUm-UpdA1vJkW4HgvXRUByY8oI3c";

// console.log(publicKey);

export const getUserToken = async (/* setTokenFound */) => {
    let currentToken = '';
    // console.log(messaging)
    try {
        currentToken = await getToken(messaging, { vapidKey: publicKey });
        /* if (currentToken) {
            setTokenFound(true);
        } else {
            setTokenFound(false);
        } */
    } catch (error) {
        console.log('An error occurred while retrieving token.', error);
    }
    return currentToken;
};

export const onMessageListener = () =>
    new Promise((resolve) => {
        onMessage(messaging, (payload) => {
            // console.log(payload);
            // console.log(messaging)
            resolve(payload);
        });
    });
