import React from 'react';
import {Grid, Box, Icon, Divider, Flex, GridItem} from "@chakra-ui/react";
import {FiClock} from 'react-icons/fi';
import {RiHandCoinFill, RiUserShared2Line} from 'react-icons/ri';
import translate from "../../i18n/translate";
import './Invoice.css';

const styles = {
    invoiceContainer: {
        w: 400,
        boxShadow: "md",
        m: 5,
        bg: "white",
        borderRadius: "md",
        p: "2rem",
        gridTemplateColumns: "repeat(3, 1fr)",
        gap: 1
    },
    name: {
        gridColumn: "span 2",
        textAlign: "start"
    },
    title: {
        fontWeight: "semibold",
        mb: "0.5rem",
        color: "#4abfa0",
        textAlign: "start"
    },
    orderId: {
        fontSize: "0.8rem",
        textAlign: "end",
        opacity: 0.5
    },
    dateTime: {
        gridColumn: "span 3",
        display: "flex",
        alignItems: "center",
        opacity: 0.7,
        fontSize: "12px",
        marginTop: "-3px"
    },
    divider: {
        gridColumn: "span 3",
        ml: "10px",
        width: "calc(100% - 10px)",
        border: "1px",
        mb: "1rem"
    },
    calculations: {
        direction: "column",
        gridColumn: "span 3",
    },
    value: {
        justifyContent: "flex-start",
        bg: "#4abfa0",
        p: "0.5rem",
        w: "fit-content",
        borderRadius: "md",
        color: "white"
    },
    details: {
        mt: 4,
        opacity: 0.6,
        gridTemplateColumns: "auto auto",
        //width: "60%",
        //alignSelf: "flex-end"
    },
    detailsValues:{
        textAlign: "end",
    }
};

export default function Invoice({invoice: {ClientName, ToGoDiscount, amount, invoiceDate, invoiceTime, tax, orderId}}) {

    const getTotal = () => {
        return amount - ToGoDiscount - tax;
    };

    return (
        <Grid {...styles.invoiceContainer}>
            <Box {...styles.name}>
                <Box {...styles.title} as="h4"><Icon as={RiUserShared2Line} mt="-5px"
                />{translate("INVOICES.SENDER")}</Box>
                {ClientName}
            </Box>
            <Box {...styles.orderId}>
                <Box>{translate("INVOICES.ORDER_ID")}</Box>
                {orderId}
            </Box>
            <Box {...styles.dateTime}>
                <Icon as={FiClock} color="#4abfa0"/>
                {`${invoiceDate} - ${invoiceTime}`}
            </Box>
            <Divider {...styles.divider}/>
            <Flex {...styles.calculations}>
                <Box as="span" {...styles.value}>
                    <Box as="span" {...styles.title} color="white"><
                        Icon as={RiHandCoinFill}
                             mr={2}/>
                        {translate("INVOICES.TRANS_VAL")}
                    </Box>
                    <Box as="span"> {amount}</Box>
                </Box>
                <Grid {...styles.details}>
                    <GridItem as="span" {...styles.title}>- {translate("INVOICES.TOGO_COMM")}</GridItem>
                    <GridItem {...styles.detailsValues}>{ToGoDiscount}</GridItem>
                    <GridItem as="span" {...styles.title}>- {translate("INVOICES.TAX")}</GridItem>
                    <GridItem {...styles.detailsValues}>{tax}</GridItem>
                    <GridItem h="2px" colSpan={2}>
                        <Divider {...styles.divider} w="100%" float="right" mb={0}/>
                    </GridItem>
                    <GridItem as="span" {...styles.title} style={{marginInlineStart: "0.6rem"}}>{translate("INVOICES.TOTAL")}</GridItem>
                    <GridItem {...styles.detailsValues}>{getTotal()}</GridItem>
                </Grid>
            </Flex>
        </Grid>
    );
};
