import React, { useRef, useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import { AddTransporterToNetwork } from "../APIs/OrdersAPIs";

import translate from "../i18n/translate";

export default function AddTransporterToNetworkDialog() {
    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1rem",
            height: "3rem"
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1rem"
        },
    }

    const [show, setShow] = useState(false);

    const [beginValidation, setBeginValidation] = useState(false);
    const [phoneCheck, setPhoneCheck] = useState(false);

    const phonenumRef = useRef();
    const handleClose = () => {
        setShow(false);
    }

    return (
        <>
            <Button className="border mt-3 me-3 rounded-22 btn-grad" onClick={() => {
                setShow(true);
            }}>
                {translate("TEMP.ADD_TRANSPORTER")}
            </Button>

            <Modal show={show} centered onHide={handleClose}>
                <Modal.Header style={styles.cardHeaderLg}>
                    {translate("TEMP.ADD_TRANSPORTER")}
                </Modal.Header>

                <Modal.Body className="mt-4">
                    <p>{translate("TEMP.PLZ_ENTER_MOBILE")}</p>
                    <Form.Control
                        readOnly={false}
                        className="input-inner-sha"
                        placeholder="+972500000000"
                        type="tel"
                        ref={phonenumRef}
                    />
                    {beginValidation && !phoneCheck && <span style={{ color: "red" }}>required!</span>}
                </Modal.Body>

                <hr />

                <div className="m-3 d-flex justify-content-center">
                    <Button variant="outline-secondary" className="w-25" onClick={handleClose}>
                        {translate("GENERAL.CLOSE")}
                    </Button>
                    <Button variant="primary" className="grad-button ms-3 w-25"
                        onClick={() => {
                            setBeginValidation(true);

                            if (phonenumRef.current.value) {
                                setPhoneCheck(true)

                                AddTransporterToNetwork(phonenumRef.current.value).then(() => {
                                    handleClose();
                                })
                            } else {
                                setPhoneCheck(false)
                            }
                        }}>
                        {translate("GENERAL.PROCEED")}
                    </Button>
                </div>

                {/* <Modal.Footer className="justify-content-center border-0 pt-0">
                    <Button variant="outline-secondary" className="" onClick={handleClose}>
                        {translate("GENERAL.CLOSE")}
                    </Button>
                    <Button variant="primary" className="grad-button" 
                        onClick={()=>{
                            setBeginValidation(true);

                            if (phonenumRef.current.value) {
                                setPhoneCheck(true)

                                AddTransporterToNetwork(phonenumRef.current.value).then(()=>{
                                    handleClose();
                                })
                            } else {
                                setPhoneCheck(false)
                            }
                        }}>
                        {translate("GENERAL.PROCEED")}
                    </Button>
                </Modal.Footer> */}
            </Modal>
        </>
    )
}
