                                                    <th scope="row">Customer ID</th>
                                                    <td>{id}</td>
                                                </tr>
                                                <tr>
                                                    <th scope="row">Odoo ID</th>
                                                    <td>{info?.OdooId}</td>
                                                </tr>
                                                <tr>
                                                    <th scope="row">Balance</th>
                                                    <td>{info?.balance} NIS</td>
                                                </tr>
                                                <tr>
                                                    <th scope="row">Loan Balance</th>
                                                    <td>
                                                        {tempBalance} NIS
                                                        <Button variant="secondary" className="ms-3" onClick={() => { setOpenTempBalanceModal(true) }}>Manage Loan Balance</Button>

                                                        <Modal centered show={openTempBalanceModal} onHide={() => { setOpenTempBalanceModal(false) }}>
