                                                                <Button className="w-25" variant="success" onClick={collectHandler}>
                                                                    Collect
                                                                </Button>
                                                                <Button className="w-25" variant="danger" onClick={lendHandler}>
                                                                    Lend
                                                                </Button>
                                                                <Button className="w-25" variant="secondary" onClick={() => { setOpenTempBalanceModal(false) }}>
                                                                    Cancel
                                                                </Button>
                                                            </Modal.Footer>
                                                        </Modal>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th scope="row">Total Orders</th>
                                                    <td>{transporterTotalOrdersNum}</td>
