import React, { useRef, useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";
import { AddTransporterToNetwork } from "../APIs/OrdersAPIs";

export default function AddTransporterToNetworkDialog() {
    const [show, setShow] = useState(false);
    
    const phonenumRef = useRef();
    const handleClose = () => {
        setShow(false);
    }

    return (
        <>
            <Button className="border mt-3 me-3 rounded-22 togo-button" onClick={()=>{
                setShow(true);
            }}>
                Add Transporter
            </Button>

            <Modal show={show} onHide={handleClose} contentClassName="togo-button">
                
                <Modal.Body>
                    <p>Please Enter Mobile Number for Transporter you want to add</p>
                    <Form.Control
                        className="shadow-none bg-transparent border-0 border-2 border-bottom form-control rounded-0 text-white"
                        type="text"
                        ref={phonenumRef}
                    />
                </Modal.Body>

                <Modal.Footer className="justify-content-center border-0 pt-0">
                    <Button variant="secondary" className="border rounded-22 togo-button" onClick={handleClose}>
                        Close
                    </Button>
                    <Button variant="primary" className="border rounded-22 togo-button" 
                        onClick={()=>{
                            AddTransporterToNetwork(phonenumRef.current.value).then(()=>{
                                handleClose();
                            })
                            // console.log("To add Transporter action...");
                        }}>
                        Submit
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    )
}
