import React, {Component} from "react";
import "leaflet/dist/leaflet.css";
import "./Address.css";
import PhoneIcon from "../../assets/images/phone-call.png";
import AddressIcon from "../../assets/images/address.png"; 
import translate from "../../i18n/translate";
import { MapContainer, Marker, Popup, TileLayer } from "react-leaflet";
import L from 'leaflet';
import icon from 'leaflet/dist/images/marker-icon.png';
import iconShadow from 'leaflet/dist/images/marker-shadow.png';

let DefaultIcon = L.icon({
    iconUrl: icon,
    shadowUrl: iconShadow
});

L.Marker.prototype.options.icon = DefaultIcon;
class Address extends Component {
    state = {
        isDisplayed: true
    };

    render() {
        return (
            <div className="Address">
                {/* edited (old adress info commented) */}

                {/*{this.props.address.lat !== undefined && 
                    <MapContainer 
                    key={JSON.stringify([this.props.address.lat, this.props.address.long])}
                    center={[this.props.address.lat, this.props.address.long]} 
                    zoom={15} 
                    style={{height: "300px"}}
                    >
                        <TileLayer
                            attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                        />
                        <Marker position={L.latLng(this.props.address.lat, this.props.address.long)}>
                            <Popup>
                                {this.props.address.otherDetails}
                            </Popup>
                        </Marker>
                        
                </MapContainer>}
                <div className="">
                    <img src={PhoneIcon} className="m-inline-e-2" alt="phone"/>
                    <div className="flex-grow-1">{translate("ORDER_DETAILS.PHONE_NUMBER")}</div>
                    <div>{this.props.address.phoneCustomer}</div>
                </div>
                <div>
                    <img src={AddressIcon} className="m-inline-e-2" alt="address" />
                    <div className="flex-grow-1">{translate("ORDER_DETAILS.OTHER_DETAILS")}</div>
                    {this.props.address.otherDetails}
                </div>*/}

                {/*edited (new adress info added)*/}

                <div className="">
                    <div className="col-lg-3 d-flex justify-content-center">
                        <div style={{
                            background: "linear-gradient(90deg, #26a69a, #69d4a5)",//"linear-gradient(90deg, #ff9966, #ff5e62)",

                            width: "90px",
                            height: "90px",
                        }}
                            className="d-flex justify-content-center rounded-circle align-self-center">
                            {/* <img
                                                        style={{
                                                            width: "80px",
                                                            height: "80px",
                                                        }}
                                                        className="rounded-circle align-self-center bg-white"
                                                        src={``}
                                                        alt={""}

                                                    /> */}
                            <svg xmlns="http://www.w3.org/2000/svg" width="90%" height="90%" fill="currentColor" className="bi bi-person-fill" viewBox="0 0 16 16">
                                <path d="M3 14s-1 0-1-1 1-4 6-4 6 3 6 4-1 1-1 1H3zm5-6a3 3 0 1 0 0-6 3 3 0 0 0 0 6z" />
                            </svg>
                        </div>
                    </div>
                    <div className="flex-grow-1">{translate("TEMP.NAME")}</div>
                    <div>{ this.props.name }</div>
                </div>
                <div className="">
                    <img src={PhoneIcon} className="m-inline-e-2" alt="phone"/>
                    <div className="flex-grow-1">{translate("ORDER_DETAILS.PHONE_NUMBER")}</div>
                    <div>{this.props.address.phoneCustomer}</div>
                </div>
                <div>
                    <img src={AddressIcon} className="m-inline-e-2" alt="address" />
                    <div className="flex-grow-1">{translate("ORDER_DETAILS.OTHER_DETAILS")}</div>
                    {this.props.address.otherDetails}
                </div>

            </div>
        );
    }
}

export default Address;
