import React, { useState } from 'react'
import "./ManageCustomers.css"
import { Container, Row, Col, Table } from 'react-bootstrap';
import { imgBaseUrl } from "../../Constants/GeneralCont";
import { FiEdit2 } from 'react-icons/fi';
/* import {

} from "../../APIs/OrdersAPIs"; */

function ManageCustomers() {

    const [clients, setClients] = useState([
        { client_id: 1, client_name: "client 1", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 2, client_name: "client 2", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 3, client_name: "client 3", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 4, client_name: "client 4", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 5, client_name: "client 5", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 6, client_name: "client 6", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
    ])

    return (
        <div>
            <div className='clients-container'>
                {
                    clients?.map((client, index) => {
                        return <div key={index} className='client-div'>
                            <div className='client-image'>
                                <img style={{
                                    width: "50px",
                                    height: "50px",
                                    objectFit: "cover"
                                }}
                                    className="rounded-circle align-self-center" src={`${imgBaseUrl}${client.client_logo_path}`} alt="transImg" />
                            </div>

                            <div className='client-name'>
                                {client.client_name}
                            </div>

                            <div className='edit-client'>
                                <FiEdit2 />
                            </div>
                        </div>
                    })
                }
            </div>
        </div>

    )
}

export default ManageCustomers
