import React, { useState } from 'react'
import "./ManageCustomers.css"
import { Modal, Button } from 'react-bootstrap';
import { imgBaseUrl } from "../../Constants/GeneralCont";
import { FiEdit2 } from 'react-icons/fi';
/* import {

} from "../../APIs/OrdersAPIs"; */

function ManageCustomers() {

    const [clients, setClients] = useState([
        { client_id: 1, client_name: "client 1", client_phone: "0599999999", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 2, client_name: "client 2", client_phone: "0599999999", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 3, client_name: "client 3", client_phone: "0599999999", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 4, client_name: "client 4", client_phone: "0599999999", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 5, client_name: "client 5", client_phone: "0599999999", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
        { client_id: 6, client_name: "client 6", client_phone: "0599999999", client_logo_path: "img/BusinessLogo/BlankProfile.png" },
    ])
    const [showEditModal, setShowEditModal] = useState(false);
    const [selectedClient, setSelectedClient] = useState({});

    const handleShowEditModal = () => { setShowEditModal(true) }
    const handleCloseEditModal = () => { setShowEditModal(false) }

    const editClient = (client) => {
        handleShowEditModal()
        setSelectedClient(client)
    }

    return (
        <div>
            <div className='clients-container'>
                {
                    clients?.map((client, index) => {
                        return <div key={index} className='client-div'>
                            <div className='client-image'>
                                <img style={{
                                    width: "50px",
                                    height: "50px",
                                    objectFit: "cover"
                                }}
                                    className="rounded-circle align-self-center" src={`${imgBaseUrl}${client.client_logo_path}`} alt="transImg"
                                />
                            </div>

                            <div className='client-name'>
                                {client.client_name}
                            </div>

                            <div className='edit-client' onClick={() => editClient(client)}>
                                <FiEdit2 />
                            </div>
                        </div>
                    })
                }
            </div>

            <Modal
                show={showEditModal}
                onHide={handleCloseEditModal}
                size="md"
                aria-labelledby="contained-modal-title-vcenter"
                centered
            >
                <Modal.Header closeButton>
                    <Modal.Title id="contained-modal-title-vcenter">
                        Edit Client
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <div className='w-100 d-flex justify-content-center'>
                        <img style={{
                            width: "100px",
                            height: "100px",
                            objectFit: "cover"
                        }}
                            className="rounded-circle align-self-center" src={`${imgBaseUrl}${selectedClient.client_logo_path}`} alt="transImg"
                        />
                    </div>
                    <h3 className='w-100 h3 text-center mt-3'>{selectedClient.client_name}</h3>
                    <h4 className='w-100 h4 text-center mt-2'>{selectedClient.client_phone}</h4>
                </Modal.Body>
                <Modal.Footer>
                    <Button onClick={handleCloseEditModal}>Close</Button>
                </Modal.Footer>
            </Modal>
        </div>
    )
}

export default ManageCustomers
