import React, { useEffect, useRef, useState } from "react";
import './styles.css';
import { Container, Row, Col, Card, Table, Button, Form, Tab, Tabs } from 'react-bootstrap';
import { GetClientPersonalInfo, GetClientBusinessInfo, updateClientPersonalInfo } from "../../../APIs/AdminPanelApis";
import { useDispatch } from "react-redux";
import { toastNotification } from "../../../Actions/GeneralActions";

export default function ClientPersonalInfoDisplay({ id }) {

    let dispatch = useDispatch();

    const [personalInfo, setPersonalInfo] = useState();
    const [businessInfo, setBusinessInfo] = useState();
    const [viewImg, setViewImg] = useState(false);
    const [tempImgPath, setTempImgPath] = useState("");

    const firstNameRef = useRef();
    const lastNameRef = useRef();
    const emailRef = useRef();
    const phoneRef = useRef();
    const idNumberRef = useRef();

    useEffect(() => {

        GetClientPersonalInfo(id).then((res) => {
            setPersonalInfo(res.data.server_response);
        })

        GetClientBusinessInfo(id).then((res) => {
            setBusinessInfo(res.data.server_response)
        })

    }, [])

    const handleClose = () => {
        setViewImg(false);
    }

    function updatePersonalInfo() {

        let firstName = "";
        let lastName = "";
        let email = "";
        let idNumber = "";
        let phone = "";

        firstNameRef.current.value == "" ? firstName = firstNameRef.current.attributes.placeholder.value : firstName = firstNameRef.current.value;
        lastNameRef.current.value == "" ? lastName = lastNameRef.current.attributes.placeholder.value : lastName = lastNameRef.current.value;
        emailRef.current.value == "" ? email = emailRef.current.attributes.placeholder.value : email = emailRef.current.value;
        idNumberRef.current.value == "" ? idNumber = idNumberRef.current.attributes.placeholder.value : idNumber = idNumberRef.current.value;
        phoneRef.current.value == "" ? phone = phoneRef.current.attributes.placeholder.value : phone = phoneRef.current.value;

        const tempArr = [id, firstName, lastName, email, idNumber, phone];

        updateClientPersonalInfo(tempArr).then((res) => {
            setRefreshPersonalInfo(!refreshPersonalInfo);

            if (res.data == "Something wnet wrong!") {
                dispatch(toastNotification("Error!", res.data, "warning"));
            } else {
                dispatch(toastNotification("Updated!", res.data, "success"));
            }
        })
    }

    const fileInput = useRef();
    const selectFile = () => {
        fileInput.current.click();
    }

    return (
        <>
            <Tabs defaultActiveKey="PersonalInformation" id="uncontrolled-tab-example" className="tabPane">
                <Tab eventKey="PersonalInformation" title="Personal Information">
                    <Card className="customCardSm shadow m-5">
                        <Card.Body>

                            <Container fluid>
                                <Row>
                                    <Col lg={6}>
                                        <Table className="infoTable">
                                            <tbody>
                                                <tr>
                                                    <th>Full Name:</th>
                                                    <td className="d-flex justify-content-between">
                                                        <Form.Control
                                                            ref={firstNameRef}
                                                            placeholder={personalInfo?.FirstName}
                                                            className="w-50"
                                                        />
                                                        <Form.Control
                                                            ref={lastNameRef}
                                                            placeholder={personalInfo?.LastName}
                                                            className="w-50"
                                                        />
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Email:</th>
                                                    <td>
                                                        <Form.Control
                                                            ref={emailRef}
                                                            type="email"
                                                            placeholder={personalInfo?.Email}
                                                        />
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Phone Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            ref={phoneRef}
                                                            type="number"
                                                            placeholder={personalInfo?.phone}
                                                        />
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>ID Number:</th>
                                                    <td>
                                                        <Form.Control
                                                            ref={idNumberRef}
                                                            type="number"
                                                            placeholder={personalInfo?.IDNumber}
                                                        />
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </Table>
                                    </Col>
                                    <Col lg={6}>
                                        <div className="w-100 d-flex justify-content-center">
                                            <div
                                                className="rounded-circle d-flex light-turquoise-bg wh-45px "
                                                style={{ width: "200px", height: "200px", position: "relative" }}
                                            >
                                                <img
                                                    className="user-img-details"
                                                    onClick={() => { setViewImg(true); setTempImgPath("https://togo.ps/togo/MobileAPi/" + info?.img) }}
                                                    style={{ cursor: "pointer" }}
                                                    src={personalInfo?.img ? "http://46.253.93.4/togo/MobileAPi/" + personalInfo.img : "https://freepikpsd.com/file/2019/10/avatar-icon-png-5-Images-PNG-Transparent.png"}

                                                    alt="user-profile-pic"
                                                />

                                                <input type="file" style={{ "display": "none" }} ref={fileInput} />
                                                <button onClick={selectFile} className='btn btn-primary edit-pencil btn-grad-circle' >
                                                    <svg xmlns="http://www.w3.org/2000/svg" width="25" height="25" fill="currentColor" className="bi bi-pencil-fill" viewBox="0 0 16 16">
                                                        <path d="M12.854.146a.5.5 0 0 0-.707 0L10.5 1.793 14.207 5.5l1.647-1.646a.5.5 0 0 0 0-.708l-3-3zm.646 6.061L9.793 2.5 3.293 9H3.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.207l6.5-6.5zm-7.468 7.468A.5.5 0 0 1 6 13.5V13h-.5a.5.5 0 0 1-.5-.5V12h-.5a.5.5 0 0 1-.5-.5V11h-.5a.5.5 0 0 1-.5-.5V10h-.5a.499.499 0 0 1-.175-.032l-.179.178a.5.5 0 0 0-.11.168l-2 5a.5.5 0 0 0 .65.65l5-2a.5.5 0 0 0 .168-.11l.178-.178z" />
                                                    </svg>
                                                </button>
                                            </div>
                                        </div>
                                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "1.5rem" }}>{personalInfo?.fullName}</div>
                                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "0.8rem", color: "gray" }}>Customer ID: {id}</div>
                                        <div className="mt-3 ms-3 d-flex justify-content-center" style={{ fontSize: "0.8rem", color: "gray" }}>Odoo ID: {personalInfo?.OdooId}</div>
                                    </Col>
                                </Row>
                            </Container>
                        </Card.Body>
                        <Card.Footer>
                            <Button /* disabled */ className="btn-grad update-btn" onClick={updatePersonalInfo}>
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
                <Tab eventKey="businessInformation" title="Business Information">
                    <Card className="customCardSm shadow m-5">
                        <Card.Body>
                            <Container fluid>
                                <Row>
                                    <Col lg={12}>
                                        <Table className="infoTable">
                                            <tbody>
                                                <tr>
                                                    <th>Business Name:</th>
                                                    <td>
                                                        <Form.Control
                                                            placeholder={businessInfo?.BusinessName}
                                                        />
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Business Location:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="email"
                                                            placeholder={businessInfo?.BusinessPlace}
                                                        />
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <th>Business Type:</th>
                                                    <td>
                                                        <Form.Control
                                                            type="number"
                                                            placeholder={businessInfo?.BusinessType === "1" ? "شركة" : "مؤسسة"}
                                                        />
                                                    </td>
                                                </tr>
                                            </tbody>
                                        </Table>
                                    </Col>
                                </Row>
                            </Container>
                        </Card.Body>
                        <Card.Footer>
                            <Button disabled className="btn-grad update-btn">
                                UPDATE
                            </Button>
                        </Card.Footer>
                    </Card>
                </Tab>
            </Tabs>
            <Modal fullscreen={true} show={viewImg} onHide={handleClose}>
                <Modal.Header closeButton>
                </Modal.Header>
                <Modal.Body className="d-flex justify-content-center">
                    <img
                        src={tempImgPath && tempImgPath}
                        style={{ objectFit: "cover" }}
                        alt="No image!"
                    />
                </Modal.Body>
            </Modal>
        </>
    )
}
