import React, { useEffect, useRef, useState } from "react";
import './CustomerInfo.css';
import { Container, Row, Col, Card, Table, Button, Form } from 'react-bootstrap';
import { GetTransporterPersonalInfo } from "../../APIs/AdminPanelApis";

function TransporterPersonalInfoDisplay({ id }) {

    const [initialInfo, setIinitialInfo] = useState();
    const [info, setInfo] = useState(initialInfo);
    const [fullName, setFullName] = useState();

    const fullNameRef = useRef();
    const accountNameRef = useRef();
    const emailRef = useRef();
    const IDNumberRef = useRef();
    const licenceNumberRef = useRef();

    /* validation */
    const [beginValidation, setBeginValidation] = useState(false);
    const [fullNameCheck, setFullNameCheck] = useState(false);
    const [accountNameCheck, setAccountNameCheck] = useState(false);
    const [emailCheck, setEmailCheck] = useState(false);
    const [IDNumberCheck, setIDNumberCheck] = useState(false);
    const [licenceNumberCheck, setlicenceNumberCheck] = useState(false);

    useEffect(() => {

        GetTransporterPersonalInfo(id).then((res) => {
            // console.log(res.data);
            setIinitialInfo(res.data.server_response);
            // setFullName(res.data.server_response.fullName);
        })

    }, [])

    function validatePersonalInfo() {
        setBeginValidation(true);

        if (fullNameRef.current.value) {
            setFullNameCheck(true);
        } else {
            setFullNameCheck(false);
        }
        if (accountNameRef.current.value) {
            setAccountNameCheck(true);
        } else {
            setAccountNameCheck(false);
        }
        if (emailRef.current.value) {
            setEmailCheck(true);
        } else {
            setEmailCheck(false);
        }
        if (IDNumberRef.current.value) {
            setIDNumberCheck(true);
        } else {
            setIDNumberCheck(false);
        }
        if (licenceNumberRef.current.value) {
            setlicenceNumberCheck(true);
        } else {
            setlicenceNumberCheck(false);
        }
    }

    function updatePersonalInfo() {

        validatePersonalInfo();

        if (fullNameRef.current.value && accountNameRef.current.value && emailRef.current.value && IDNumberRef.current.value && licenceNumberRef.current.value) {
            console.log("updated!")
        }
    }

    const handleChange = (e) => {
        // create the new state and set it

        console.log(e.target.value)

        setInfo(prev => ({ ...prev, [e.target?.name]: e.target?.value }))

        console.log(info);

        e.preventDefault()
    }

    return (
        <>
            <Card className="customCardSm shadow">
                <Card.Header>
                    Personal Information
                </Card.Header>
                <Card.Body>

                    <Container fluid>
                        <Row>
                            <Col lg={6}>
                                <Table className="infoTable">
                                    <tbody>
                                        <tr>
                                            <th>Full Name:</th>
                                            <td>
                                                <Form.Control
                                                    name="fullName"
                                                    className=""
                                                    value={info?.fullName}
                                                    
                                                    onChange={(e) => handleChange(e)}
                                                /* onChange={(event) => {setFullName(event.target.value); console.log(event.target.value)}} */
                                                />
                                                {beginValidation && !fullNameCheck && <div className="required-error"><span>*</span></div>}
                                            </td>
                                        </tr>
                                        <tr>
                                            <th>Account Name:</th>
                                            <td>
                                                <Form.Control
                                                    name="AccountName"
                                                    className=""
