import React, { useEffect, useLayoutEffect, useState } from "react";
import "./App.css";
import {BrowserRouter as Router, Switch, Route} from "react-router-dom";
import "react-loader-spinner/dist/loader/css/react-spinner-loader.css";
import {useSelector} from "react-redux";
import {I18nProvider} from '../../i18n';
import {LOCALES_TYPES} from "../../i18n/locales";
import Account from "../../Container/Account/Account";
import Home from "../../Container/Home/Home";
import Toast from "../Toast";
import Privacy from "../Privacy";
import Login from "../Login";
import ActivationCodeForm from "../ActivationCodeForm";
import SelectLanguage from "../SelectLanguage";
import PersonalInfoForm from "../PersonalInfoForm";
import BusinessInfoForm from "../BusinessInfoForm";
import ViewOrder from "../Order/ViewOrder";
import Dashboard from "../Dashboard";
import AdminPanel from "../AdminPanel/AdminPanel";
import TransporterPersonalInfo from "../TransporterPersonalInfo";
import VehicleDetails from "../VechicleDetails";
import WorkInfo from "../WorkInfo";

// import { Helmet } from 'react-helmet' /* edited (import react-helmet) */
import { isTransporter } from "../../Util"; /* edited (import isTransporter) */

//import { getUserToken, onMessageListener } from "../../firebase";

const App = () => {

    useEffect(() => {
       if (localStorage.getItem("UserType") === "client") {
        document.title = "TOGO Client";
        } else if (localStorage.getItem("UserType") === "transporter") {
            document.title = "TOGO Transporter";
        } else {
            document.title = "TOGO";
    }
    }, [language]);

    /*for (var i = 0; i < localStorage.length; i++){
        console.log(localStorage.key(i) + " --> " + localStorage.getItem(localStorage.key(i)));
    }*/
    
    const language = useSelector(state => state.general.language);
    // const [isTokenFound, setTokenFound] = useState(false);

    // console.log("Token found", isTokenFound);

    // // To load once
    // useEffect(() => {
    //     let data;

    //     async function tokenFunc() {
    //         data = await getUserToken(setTokenFound);
    //         if (data) {
    //             console.log("Token is", data);
    //         }
    //         return data;
    //     }

    //     tokenFunc();
    // }, [setTokenFound]);

    // const [notification, setNotification] = useState({ title: "", body: "" });

    // console.log(notification);
  
    // onMessageListener().then((payload) => {
    //     setNotification({
    //       title: payload.notification.title,
    //       body: payload.notification.body,
    //     });
    //     console.log(payload);
    // }).catch((err) => console.log("failed: ", err));

    useLayoutEffect(()=>{
        document.dir = language === LOCALES_TYPES.ENGLISH ? "ltr" : "rtl";
        document.documentElement.lang = language;
        if(language === LOCALES_TYPES.ENGLISH){
            document.getElementById("bootStyle").href = "https://cdn.jsdelivr.net/npm/bootstrap@5.1.3/dist/css/bootstrap.min.css";
        }else{
            document.getElementById("bootStyle").href = "https://cdn.jsdelivr.net/npm/bootstrap@5.1.3/dist/css/bootstrap.rtl.min.css"
        }
        
    }, [language])
    return (<>
            {/*<Helmet>
              <title>{ "TITLE" }</title>
            </Helmet>*/}
            <I18nProvider locale={language}>
                <Router>
                    <Switch>
                        <Route path="/my-network" component={Account}/> {/* edited (add my-network route) */}
                        <Route path="/account" component={Account}/>
                        <Route path="/privacy" component={Privacy}/>
                        <Route path="/login" component={Login}/>
                        <Route path="/activate" component={ActivationCodeForm}/>
                        
                        <Route path="/lang" component={SelectLanguage} />
                        <Route path="/personal-info" component={PersonalInfoForm} />
                        <Route path="/trans-personal-info" component={TransporterPersonalInfo} />
                        <Route path="/vehicle-info" component={VehicleDetails} />
                        <Route path="/work-info" component={WorkInfo} />
                        <Route path="/business-info" component={BusinessInfoForm} />
                        <Route path="/vieworder/:id" component={ViewOrder} />

                        <Route path="/dashboard" component={Dashboard} />
                        <Route path="/adminapp" component={AdminPanel} />
                        <Route path="/" component={Home} exact/>
                    </Switch> 
                </Router>
                <Toast />
            </I18nProvider>
        </>
    );
};

export default App;
