import React from "react";
import togoLogo from "../../assets/logo.png"
import { FaHome, FaEnvelope, FaMoneyCheckAlt, FaSignOutAlt } from "react-icons/fa"
import "./AdminPanel.css"
import { GetAllActiveOrders, GetAllClients, GetAllFinishedOrders, GetAllNewOrders, GetAllTransporters, getRecordsActions, getTotalBalance, getLastOrderId } from "../../APIs/AdminPanelApis";
import { useEffect, useState } from "react";
import { ResponsivePie } from '@nivo/pie';
import OrdersCard from "./OrdersCard";
import UsersCard from "./UserCard";
import RecordsCard from "./RecordsCard";
import SignIn from "./SignIn";
import { refreshPage } from "../../Functions/CommonFunctions";

//https://dribbble.com/shots/15254181-CRM-Dashboard-Managency
//https://dribbble.com/shots/16485287-Trustland-Logistic-Management-App

export default function AdminPanel() {

    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        }
    }

    const [orderStat, setOrderStat] = useState([]);
    const [userStat, setUserStat] = useState([]);

    const [newOrders, setNewOrders] = useState([]);
    const [activeOrders, setActiveOrders] = useState([]);
    const [finishedOrders, setFinishedOrders] = useState([]);
    const [newOrdersNum, setNewOrdersNum] = useState(0);
    const [activeOrdersNum, setActiveOrdersNum] = useState(0);
    const [finishedOrdersNum, setFinishedOrdersNum] = useState(0);

    const [records, setRecords] = useState([]); // edited (records variable added)

    const [creditAmount, setCreditAmount] = useState(0); // edited (creditAmount variable added)
    const [totalEscrowBalance, setTotalEscrowBalance] = useState(0); // edited (totalEscrowBalance variable added)
    const [balanceStat, setBalanceStat] = useState([]); // edited (balanceStat variable added)

    const [allTransporters, setAllTransporters] = useState([]);
    const [allClients, setAllClients] = useState([]);

    const [lastorderId, setLastorderId] = useState();

    useEffect(() => {

        /* edited (get lat order id) */
        getLastOrderId().then((res) => {
            setLastorderId(res.data);
        })

        /* edited (get all actions records) */
        getRecordsActions().then((res) => {
            setRecords(res.data.records_list);
        });

        /* edited (get escrow total balance) */
        getTotalBalance().then((res) => {

            setTotalEscrowBalance(res.data.server_response.data.result.differ_balance);
            setCreditAmount(res.data.server_response.data.result.credit_amount);

            setBalanceStat((prev) => {
                return [...prev, {
                    "id": "credit",
                    "label": "Credit",
                    "value": res.data.server_response.data.result.credit_amount,
                }]
            });

            setBalanceStat((prev) => {
                return [...prev, {
                    "id": "debit",
                    "label": "Debit",
                    "value": res.data.server_response.data.result.debit_amount,
                }]
            });
        });

        GetAllNewOrders().then((res) => {
            setNewOrders(res.data.orders_list);
            setNewOrdersNum(res.data.NumberOfOrders);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "new",
                    "label": "New",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });
        GetAllActiveOrders().then((res) => {
            setActiveOrders(res.data.orders_list);
            setActiveOrdersNum(res.data.NumberOfOrders);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "active",
                    "label": "Active",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });
        GetAllFinishedOrders().then((res) => {
            setFinishedOrders(res.data.orders_list);
            setFinishedOrdersNum(res.data.NumberOfOrders);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "finished",
                    "label": "Finished",
                    "value": res.data.NumberOfOrders,
                }]
            });

        });

        GetAllTransporters().then((res) => {
            setAllTransporters(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "transporter",
                    "label": "Transporters",
                    "value": res.data.NumberOfTransporters,
                }]
            });
        });
        GetAllClients().then((res) => {
            setAllClients(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "client",
                    "label": "Clients",
                    "value": res.data.NumberOfClients,
                }]
            });
        });
    }, []);

    if (localStorage.getItem("signedin") !== "true") {
        return <SignIn />
    }

    return (
        <div className="d-flex h-100 w-100 panel-wrapper" style={{ backgroundColor: "#eaeced" }}>
            <div
                className="bg-white mt-3 mb-3 ms-3 rounded-22 shadow admin-left-menu"
            >
                {/* <div className="p-3">
