import React from "react";
import togoLogo from "../../assets/logo.png"
import { FaHome, FaEnvelope, FaMoneyCheckAlt, FaSignOutAlt } from "react-icons/fa"
import "./AdminPanel.css"
import { GetAllActiveOrders, GetAllClients, GetAllFinishedOrders, GetAllNewOrders, GetAllTransporters, /*transactionTest,*/ getRecordsActions } from "../../APIs/AdminPanelApis";
import { useEffect, useState } from "react";
import { ResponsivePie } from '@nivo/pie';
import OrdersCard from "./OrdersCard";
import UsersCard from "./UserCard";
import RecordsCard from "./RecordsCard";
import SignIn from "./SignIn";
import { refreshPage } from "../../Functions/CommonFunctions";
//https://dribbble.com/shots/15254181-CRM-Dashboard-Managency
//https://dribbble.com/shots/16485287-Trustland-Logistic-Management-App

export default function AdminPanel() {

    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        }
    }

    const [orderStat, setOrderStat] = useState([]);
    const [userStat, setUserStat] = useState([]);
    const [newOrders, setNewOrders] = useState([]);
    const [activeOrders, setActiveOrders] = useState([]);
    const [finishedOrders, setFinishedOrders] = useState([]);
    const [records, setRecords] = useState([]); // edited (records variable added)

    const [allTransporters, setAllTransporters] = useState([]);
    const [allClients, setAllClients] = useState([]);

    useEffect(() => {
        /* edited (get all actions records) */
        getRecordsActions().then((res) => {
            console.log(res.data.records_list);
            setRecords(res.data.records_list);
        });

        /*transactionTest(108).then((res)=>{  // edited (test transactions) 
            console.log(res);
        });*/
        GetAllNewOrders().then((res) => {
            // console.log(res.data);
            setNewOrders(res.data);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "new",
                    "label": "New",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });
        GetAllActiveOrders().then((res) => {
            setActiveOrders(res.data);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "active",
                    "label": "Active",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });
        GetAllFinishedOrders().then((res) => {
            setFinishedOrders(res.data);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "finished",
                    "label": "Finished",
                    "value": res.data.NumberOfOrders,
                }]
            });

        });

        GetAllTransporters().then((res) => {
            // console.log(res.data);
            setAllTransporters(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "transporter",
                    "label": "Transporters",
                    "value": res.data.NumberOfTransporters,
                }]
            });
        });
        GetAllClients().then((res) => {
            // console.log(res.data);
            setAllClients(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "client",
                    "label": "Clients",
                    "value": res.data.NumberOfClients,
                }]
            });
        });
    }, []);

    if (localStorage.getItem("signedin") !== "true") {
        return <SignIn />
    }

    return (
        <div className="d-flex h-100 w-100 panel-wrapper" style={{ backgroundColor: "#eaeced" }}>
            
            <div className="w-100 ps-3"/* d-flex flex-wrap  */ style={{ overflowX: "auto" }}>
                <div className="w-100 d-flex">
                    <div
                        className="bg-white me-3 mt-3 rounded-22 shadow togo-chart"
                    >
                        <ResponsivePie
                            data={orderStat}
                            margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
                            innerRadius={0.5}
                            padAngle={0.7}
                            cornerRadius={3}
                            activeOuterRadiusOffset={8}
                            borderWidth={1}
                            borderColor={{
                                from: 'color',
                                modifiers: [
                                    [
                                        'darker',
                                        0.2
                                    ]
                                ]
                            }}
                            arcLinkLabelsSkipAngle={10}
                            arcLinkLabelsTextColor="#333333"
                            arcLinkLabelsThickness={2}
                            arcLinkLabelsColor={{ from: 'color' }}
                            arcLabelsSkipAngle={10}
                            arcLabelsTextColor={{
                                from: 'color',
                                modifiers: [
                                    [
                                        'darker',
                                        2
                                    ]
                                ]
                            }}
                            defs={[
                                {
                                    id: 'dots',
                                    type: 'patternDots',
                                    background: 'inherit',
                                    color: 'rgba(255, 255, 255, 0.3)',
                                    size: 4,
                                    padding: 1,
                                    stagger: true
                                },
                                {
                                    id: 'lines',
                                    type: 'patternLines',
                                    background: 'inherit',
                                    color: 'rgba(255, 255, 255, 0.3)',
                                    rotation: -45,
                                    lineWidth: 6,
                                    spacing: 10
                                }
                            ]}
                            fill={[

                            ]}
                            legends={[
                                {
                                    anchor: 'bottom',
                                    direction: 'row',
                                    justify: false,
                                    translateX: 0,
                                    translateY: 56,
                                    itemsSpacing: 0,
                                    itemWidth: 100,
                                    itemHeight: 18,
                                    itemTextColor: '#999',
                                    itemDirection: 'left-to-right',
                                    itemOpacity: 1,
                                    symbolSize: 18,
                                    symbolShape: 'circle',
                                    effects: [
                                        {
                                            on: 'hover',
                                            style: {
                                                itemTextColor: '#000'
                                            }
                                        }
                                    ]
                                }
                            ]}
                        />
                    </div>
                    <div

                        className="bg-white me-3 mt-3 rounded-22 shadow togo-chart"
                    >
                        <ResponsivePie
                            margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
                            data={userStat}
                            animate={true}
                            colors={{
                                scheme: 'set2'//datum: 'data.color' 
                            }}
                            activeOuterRadiusOffset={8}
                            innerRadius={0.6}
                            padAngle={0.5}
                            cornerRadius={5}
                            arcLinkLabelsColor={{
                                from: 'color',
                            }}
                            arcLinkLabelsThickness={3}
                            arcLinkLabelsTextColor={{
                                from: 'color',
                                modifiers: [['darker', 1.2]],
                            }}
                            legends={[
                                {
                                    anchor: 'bottom',
                                    direction: 'row',
                                    justify: false,
                                    translateX: 0,
                                    translateY: 56,
                                    itemsSpacing: 0,
                                    itemWidth: 100,
                                    itemHeight: 18,
                                    itemTextColor: '#999',
                                    itemDirection: 'left-to-right',
                                    itemOpacity: 1,
                                    symbolSize: 18,
                                    symbolShape: 'circle',
                                    effects: [
                                        {
                                            on: 'hover',
                                            style: {
                                                itemTextColor: '#000'
                                            }
                                        }
                                    ]
                                }
                            ]}
                        />
                    </div>
                </div>
                {/* <div className="w-100 d-flex h-250px">
                    <RecordsCard title="Records Log Table" list={records} />

                </div> */}

                <div className="w-100 d-flex h-250px">
                    <OrdersCard title="New Orders" list={newOrders.orders_list} />

                </div>

                <div className="w-100 d-flex h-250px">
                    <OrdersCard title="Active Orders" list={activeOrders.orders_list} />
                </div>

                <div className="w-100 d-flex h-250px">
                    <OrdersCard title="Finished Orders" list={finishedOrders.orders_list} />
                </div>
            </div>
            <div className="m-3 w-50">

                <div className="h-250px">
                    <UsersCard title="All Clients" list={allClients.clients_list} />
                </div>

                <div className="mt-3 h-250px">
                    <UsersCard title="All Transporters" list={allTransporters.transporters_list} />
                </div>
            </div>
        </div>
    )
}
