import React, { useCallback } from "react";
import togoLogo from "../../assets/logo.png"
import { FaHome, FaEnvelope, FaMoneyCheckAlt, FaSignOutAlt } from "react-icons/fa"
import "./AdminPanel.css"
import { GetAllActiveOrders, GetAllClients, GetAllFinishedOrders, GetAllNewOrders, GetAllTransporters, getRecordsActions, getTotalBalance, getLastOrderId } from "../../APIs/AdminPanelApis";
import { useEffect, useState } from "react";
import { ResponsivePie } from '@nivo/pie';
import OrdersCard from "./OrdersCard";
import UsersCard from "./UserCard";
import RecordsCard from "./RecordsCard";
import SignIn from "./SignIn";
import { Button } from "react-bootstrap";

//https://dribbble.com/shots/15254181-CRM-Dashboard-Managency
//https://dribbble.com/shots/16485287-Trustland-Logistic-Management-App

export default function AdminPanel() {

    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        }
    }

    const [orderStat, setOrderStat] = useState([]);
    const [userStat, setUserStat] = useState([]);

    const [newOrders, setNewOrders] = useState([]);
    const [activeOrders, setActiveOrders] = useState([]);
    const [finishedOrders, setFinishedOrders] = useState([]);
    const [activeOrdersNum, setActiveOrdersNum] = useState(0);

    const [records, setRecords] = useState([]);

    const [creditAmount, setCreditAmount] = useState(0);
    const [totalEscrowBalance, setTotalEscrowBalance] = useState(0);

    const [allTransporters, setAllTransporters] = useState([]);
    const [allClients, setAllClients] = useState([]);

    const [lastorderId, setLastorderId] = useState();

    const fetchData = useCallback(() => {
        getLastOrderId().then((res) => {
            setLastorderId(res.data);
        })

        getRecordsActions().then((res) => {
            setRecords(res.data.records_list);
        });

        getTotalBalance().then((res) => {

            setTotalEscrowBalance(res.data.server_response.data.result.differ_balance);
            setCreditAmount(res.data.server_response.data.result.credit_amount);
        });

        GetAllNewOrders().then((res) => {
            setNewOrders(res.data.orders_list);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "new",
                    "label": "New",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });

        GetAllActiveOrders().then((res) => {
            setActiveOrders(res.data.orders_list);
            setActiveOrdersNum(res.data.NumberOfOrders);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "active",
                    "label": "Active",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });

        GetAllFinishedOrders().then((res) => {
            setFinishedOrders(res.data.orders_list);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "finished",
                    "label": "Finished",
                    "value": res.data.NumberOfOrders,
                }]
            });

        });

        GetAllTransporters().then((res) => {
            setAllTransporters(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "transporter",
                    "label": "Transporters",
                    "value": res.data.NumberOfTransporters,
                }]
            });
        });

        GetAllClients().then((res) => {
            setAllClients(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "client",
                    "label": "Clients",
                    "value": res.data.NumberOfClients,
                }]
            });
        });
    })

    useEffect(() => {
        fetchData();
    }, []);

    if (localStorage.getItem("signedin") !== "true") {
        return <SignIn />
    }

    return (
        <>
            <nav class="navbar navbar-light" style={{ backgroudColor: "white" }}>
                <div class="container-fluid">
                    <a class="navbar-brand d-flex justify-content-between" style={{ cursor: "pointer" }}>
                        <img src={togoLogo} style={{ width: "100px" }} alt="logo" />
                        <span className="forest-color mt-3 ms-3">Admin Panel</span>
                    </a>
                    <Button variant="outline-secondary" className="d-flex justify-content-between" onClick={() => {
                        localStorage.setItem("signedin", false);
                        refreshPage()
                    }}>
                        <FaSignOutAlt className="menu-icon" />
                        Logout
                    </Button>
                </div>
            </nav>

            <div className="d-flex w-100 panel-wrapper" style={{ backgroundColor: "#eaeced" }}>
                <div
                    className="bg-white mt-3 mb-3 ms-3 rounded-22 shadow admin-left-menu"
                >

                    <div className="d-flex h-100">
                        <RecordsCard title="Records Log Table" list={records} />

                    </div>

                </div>
                <div className="w-100 ps-3" style={{ overflowX: "auto" }}>
                    <div className="w-100 d-flex">
                        <div
                            className="bg-white me-3 mt-3 rounded-22 shadow togo-chart"
                        >
                            <ResponsivePie
                                data={orderStat}
                                margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
                                innerRadius={0.5}
                                padAngle={0.7}
                                cornerRadius={3}
                                activeOuterRadiusOffset={8}
                                borderWidth={1}
                                borderColor={{
                                    from: 'color',
                                    modifiers: [
                                        [
                                            'darker',
                                            0.2
                                        ]
                                    ]
                                }}
                                arcLinkLabelsSkipAngle={10}
                                arcLinkLabelsTextColor="#333333"
                                arcLinkLabelsThickness={2}
                                arcLinkLabelsColor={{ from: 'color' }}
                                arcLabelsSkipAngle={10}
                                arcLabelsTextColor={{
                                    from: 'color',
                                    modifiers: [
                                        [
                                            'darker',
                                            2
                                        ]
                                    ]
                                }}
                                defs={[
                                    {
                                        id: 'dots',
                                        type: 'patternDots',
                                        background: 'inherit',
                                        color: 'rgba(255, 255, 255, 0.3)',
                                        size: 4,
                                        padding: 1,
                                        stagger: true
                                    },
                                    {
                                        id: 'lines',
                                        type: 'patternLines',
                                        background: 'inherit',
                                        color: 'rgba(255, 255, 255, 0.3)',
                                        rotation: -45,
                                        lineWidth: 6,
                                        spacing: 10
                                    }
                                ]}
                                fill={[

                                ]}
                                legends={[
                                    {
                                        anchor: 'bottom',
                                        direction: 'row',
                                        justify: false,
                                        translateX: 0,
                                        translateY: 56,
                                        itemsSpacing: 0,
                                        itemWidth: 100,
                                        itemHeight: 18,
                                        itemTextColor: '#999',
                                        itemDirection: 'left-to-right',
                                        itemOpacity: 1,
                                        symbolSize: 18,
                                        symbolShape: 'circle',
                                        effects: [
                                            {
                                                on: 'hover',
                                                style: {
                                                    itemTextColor: '#000'
                                                }
                                            }
                                        ]
                                    }
                                ]}
                            />
                        </div>
                        <div

                            className="bg-white me-3 mt-3 rounded-22 shadow togo-chart"
                        >
                            <ResponsivePie
                                margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
                                data={userStat}
                                animate={true}
                                colors={{
                                    scheme: 'set2'//datum: 'data.color' 
                                }}
                                activeOuterRadiusOffset={8}
                                innerRadius={0.6}
                                padAngle={0.5}
                                cornerRadius={5}
                                arcLinkLabelsColor={{
                                    from: 'color',
                                }}
                                arcLinkLabelsThickness={3}
                                arcLinkLabelsTextColor={{
                                    from: 'color',
                                    modifiers: [['darker', 1.2]],
                                }}
                                legends={[
                                    {
                                        anchor: 'bottom',
                                        direction: 'row',
                                        justify: false,
                                        translateX: 0,
                                        translateY: 56,
                                        itemsSpacing: 0,
                                        itemWidth: 100,
                                        itemHeight: 18,
                                        itemTextColor: '#999',
                                        itemDirection: 'left-to-right',
                                        itemOpacity: 1,
                                        symbolSize: 18,
                                        symbolShape: 'circle',
                                        effects: [
                                            {
                                                on: 'hover',
                                                style: {
                                                    itemTextColor: '#000'
                                                }
                                            }
                                        ]
                                    }
                                ]}
                            />
                        </div>
                        <div
                            style={{ position: "relative" }}
                            className="bg-white me-3 mt-3 rounded-22 shadow togo-chart p-3"
                        >
                            <div className="row">
                                <div style={{ fontSize: "1.3rem" }}>Total in Escrow: <span style={{ color: "#35b09d" }}>{totalEscrowBalance}</span></div>
                                <div style={{ fontSize: "1.3rem" }}>Total Escrow: <span style={{ color: "#35b09d" }}>{creditAmount}</span></div>
                            </div>

                            <hr style={{ marginTop: "20px", marginBottom: "20px" }} />

                            <div className="row">
                                <div style={{ fontSize: "1.3rem" }}>Active Packages: <span style={{ color: "#35b09d" }}>{activeOrdersNum}</span></div>
                                <div style={{ fontSize: "1.3rem" }}>Total Packages: <span style={{ color: "#35b09d" }}>{lastorderId}</span></div>
                            </div>
                        </div>
                    </div>

                    <div className="w-100 d-flex h-25">
                        <OrdersCard title="New Orders" list={newOrders} />

                    </div>

                    <div className="w-100 d-flex h-25">
                        <OrdersCard title="Active Orders" list={activeOrders} />
                    </div>

                    <div className="w-100 d-flex h-25">
                        <OrdersCard title="Finished Orders" list={finishedOrders} />
                    </div>
                </div>
                <div className="m-3 w-50">

                    <div className="" style={{ height: "49%" }}>
                        <UsersCard title="All Clients" list={allClients.clients_list} />
                    </div>

                    <div className="mt-3" style={{ height: "49%" }}>
                        <UsersCard title="All Transporters" list={allTransporters.transporters_list} />
                    </div>
                </div>
            </div>
        </>
    )
}
