import React from "react";
import togoLogo from "../assets/logo.png"
import { FaHome, FaEnvelope, FaMoneyCheckAlt } from "react-icons/fa"
import "./AdminPanel.css"
import { GetAllActiveOrders, GetAllClients, GetAllFinishedOrders, GetAllNewOrders, GetAllTransporters } from "./AdminPanelApis";
import { useEffect, useState } from "react";
import { ResponsivePie } from '@nivo/pie';
import translate from "../i18n/translate";
import { PackageTypes } from "./Orders/OrdersTabularView";
import { PackageTypesIcons } from "./CreateNewOrder";
import OrdersCard from "./OrdersCard";
import UsersCard from "./UserCard";
//https://dribbble.com/shots/15254181-CRM-Dashboard-Managency
//https://dribbble.com/shots/16485287-Trustland-Logistic-Management-App

export default function AdminPanel() {

    const [orderStat, setOrderStat] = useState([]);
    const [userStat, setUserStat] = useState([]);
    const [newOrders, setNewOrders] = useState([]);
    const [activeOrders, setActiveOrders] = useState([]);
    const [finishedOrders, setFinishedOrders] = useState([]);

    const [allTransporters, setAllTransporters] = useState([]);
    const [allClients, setAllClients] = useState([]);

    useEffect(() => {
        GetAllNewOrders().then((res)=>{
            setNewOrders(res.data);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "new",
                    "label": "New",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });
        GetAllActiveOrders().then((res)=>{
            setActiveOrders(res.data);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "active",
                    "label": "Active",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });
        GetAllFinishedOrders().then((res)=>{
            setFinishedOrders(res.data);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "finished",
                    "label": "Finished",
                    "value": res.data.NumberOfOrders,
                }]
            });
            
        });

        GetAllTransporters().then((res)=>{
            setAllTransporters(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "transporter",
                    "label": "Transporters",
                    "value": res.data.NumberOfTransporters,
                }]
            });
        });
        GetAllClients().then((res)=>{
            setAllClients(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "client",
                    "label": "Clients",
                    "value": res.data.NumberOfClients,
                }]
            });
        });
    }, []);

    return (
        <div className="d-flex h-100 w-100" style={{
            backgroundColor: "#CEF2DB",//"#ABEDD0",
            color: "#a9b0b4",
            fontFamily: "PT Sans"
        }}>
            <div
                className="bg-white mt-3 mb-3 ms-3 rounded-22 shadow" 
                style={{
// #03A65A
// #A0F2CC
// #CEF2DB
// #F2695C
// #F2F2F2


// #CFF7E0
// #F7F7F4
// #97EACB
                    //backgroundColor: "#F4F4F7",
                    flexBasis: "38%",
                }}
            >
                <div className="p-3">
                    <div className="align-items-center d-flex fs-5 justify-content-center text-black">
                        <img src={togoLogo} style={{width: "100px"}}/>
                        <span style={{color: "#0C835B"}}>Managment System</span>
                    </div>
                    <div className="border-bottom border-top fs-6 fw-bold mt-5 py-3 text-black">
                        Welcome Abu Hussam!
                    </div>
                </div>
                <div>
                    <div 
                        style={{
                            color: "#03A65A",
                            borderLeft: "2px solid",
                        }}
                        className="align-items-center d-flex mt-2 py-2 menu-item ps-3"
                    >
                        <FaHome className="menu-icon" />
                        Dashboard
                    </div>
                    
                    <div className="align-items-center d-flex mt-2 py-2 menu-item ps-3">
                        <FaEnvelope className="menu-icon" />
                        Messages
                    </div>
                    
                    <div className="align-items-center d-flex mt-2 py-2 menu-item ps-3">
                        <FaMoneyCheckAlt className="menu-icon" />
                        Payments
                    </div>
                </div>
            </div>
            <div className="w-100 ps-3"/* d-flex flex-wrap  */ style={{overflowX: "auto"}}>
                <div className="w-100 d-flex">
                    <div 
                        style={{height: "250px", width: "375px"}}
                        className="bg-white me-3 mt-3 rounded-22 shadow"
                    >
                        <ResponsivePie
                            data={orderStat}
                            margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
                            innerRadius={0.5}
                            padAngle={0.7}
                            cornerRadius={3}
                            activeOuterRadiusOffset={8}
                            borderWidth={1}
                            borderColor={{
                                from: 'color',
                                modifiers: [
                                    [
                                        'darker',
                                        0.2
                                    ]
                                ]
                            }}
                            arcLinkLabelsSkipAngle={10}
                            arcLinkLabelsTextColor="#333333"
                            arcLinkLabelsThickness={2}
                            arcLinkLabelsColor={{ from: 'color' }}
                            arcLabelsSkipAngle={10}
                            arcLabelsTextColor={{
                                from: 'color',
                                modifiers: [
                                    [
                                        'darker',
                                        2
                                    ]
                                ]
                            }}
                            defs={[
                                {
                                    id: 'dots',
                                    type: 'patternDots',
                                    background: 'inherit',
                                    color: 'rgba(255, 255, 255, 0.3)',
                                    size: 4,
                                    padding: 1,
                                    stagger: true
                                },
                                {
                                    id: 'lines',
                                    type: 'patternLines',
                                    background: 'inherit',
                                    color: 'rgba(255, 255, 255, 0.3)',
                                    rotation: -45,
                                    lineWidth: 6,
                                    spacing: 10
                                }
                            ]}
                            fill={[
                                
                            ]}
                            legends={[
                                {
                                    anchor: 'bottom',
                                    direction: 'row',
                                    justify: false,
                                    translateX: 0,
                                    translateY: 56,
                                    itemsSpacing: 0,
                                    itemWidth: 100,
                                    itemHeight: 18,
                                    itemTextColor: '#999',
                                    itemDirection: 'left-to-right',
                                    itemOpacity: 1,
                                    symbolSize: 18,
                                    symbolShape: 'circle',
                                    effects: [
                                        {
                                            on: 'hover',
                                            style: {
                                                itemTextColor: '#000'
                                            }
                                        }
                                    ]
                                }
                            ]}
                        />
                    </div>
                    <div 
                        style={{height: "250px", width: "375px"}}
                        className="bg-white me-3 mt-3 rounded-22 shadow"
                    >
                        <ResponsivePie  
                            margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
                            data={userStat}
                            animate={true}
                            colors={{ scheme: 'set2'//datum: 'data.color' 
                        }} 
                            activeOuterRadiusOffset={8}
                            innerRadius={0.6}
                            padAngle={0.5}
                            cornerRadius={5}
                            arcLinkLabelsColor={{
                                from: 'color',
                            }}
                            arcLinkLabelsThickness={3}
                            arcLinkLabelsTextColor={{
                                from: 'color',
                                modifiers: [['darker', 1.2]],
                            }}
                            legends={[
                                {
                                    anchor: 'bottom',
                                    direction: 'row',
                                    justify: false,
                                    translateX: 0,
                                    translateY: 56,
                                    itemsSpacing: 0,
                                    itemWidth: 100,
                                    itemHeight: 18,
                                    itemTextColor: '#999',
                                    itemDirection: 'left-to-right',
                                    itemOpacity: 1,
                                    symbolSize: 18,
                                    symbolShape: 'circle',
                                    effects: [
                                        {
                                            on: 'hover',
                                            style: {
                                                itemTextColor: '#000'
                                            }
                                        }
                                    ]
                                }
                            ]}
                        />
                    </div>
                </div>
                <div style={{height: "250px"}} className="w-100 d-flex">
                    <OrdersCard title="New Orders" list={newOrders.orders_list} />
                    
                </div>

                <div style={{height: "250px"}} className="w-100 d-flex">
                    <OrdersCard title="Active Orders" list={activeOrders.orders_list} />
                </div>
                    
                <div style={{height: "250px"}} className="w-100 d-flex">
                    <OrdersCard title="Finished Orders" list={finishedOrders.orders_list} />
                </div>
            </div>
            <div className="m-3">
                <div style={{height:"250px"}}>
                    <UsersCard title="All Clients" list={allClients.clients_list} />
                </div>
                <div style={{height:"250px"}} className="mt-3">
                    <UsersCard title="All Transporters" list={allTransporters.transporters_list} />
                </div>
            </div>
        </div>
    )
}
