import React, { useRef, useState } from "react";
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import Modal from 'react-bootstrap/Modal';
import Spinner from 'react-bootstrap/Spinner';
import { sendVerificationCode } from "../../APIs/LoginAPIs";
import { useDispatch } from "react-redux";
import { toastNotification } from "../../Actions/GeneralActions";

export default function LoginByPhoneNumber() {
    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1.5rem"
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold',
            fontSize: "1.2rem"
        },
    }

    let dispatch = useDispatch();

    const [show, setShow] = useState(false);
    const [shadowError, setShowError] = useState(false);
    const [startValidation, setStartValidation] = useState(false);
    const [loadingSend, setLoadingSend] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const numberRef = useRef();

    function sendCode() {
        setStartValidation(true);
        if (isValidNumber(numberRef.current.value)) {
            setShowError(false);
            setLoadingSend(true);

            sendVerificationCode(numberRef.current.value).then((res) => {

                if (res.data === "MobileNotFound") {
                    dispatch(toastNotification("Error", "Mobile not found!", "error"));
                    setLoadingSend(false);
                } else if (res.data === "NotUpdated") {
                    dispatch(toastNotification("Error", "Something went wrong!", "error"));
                    setLoadingSend(false);
                } else {
                    console.log(res.data);
                    setLoadingSend(false);
                    setShow(true);
                }
            })

        } else {
            setShowError(true);
        }
    }

    function isValidNumber(inputtxt) {
        let phoneno = /^\d{10}$/;
        if (inputtxt.match(phoneno)) {
            return true;
        }
        else {
            return false;
        }
    }

    return (
        <>
            <div className="container-fluid mt-5">
                <div className="row d-flex justify-content-center">
                    <div className="col-6">
                        <Card className='shadow h-100 rounded-22'>
                            <Card.Header style={styles.cardHeaderLg} className="">
                                Sign in By Phone Number
                            </Card.Header>

                            <Card.Body className="mt-5">
                                <Form>
                                    <Form.Group className="mb-3" controlId="formBasicEmail">
                                        <Form.Label>Please enter your phone number (start with 0)</Form.Label>
                                        <Form.Control type="number" placeholder="Phone number..." ref={numberRef} />
                                        {shadowError && startValidation && <Form.Text className="text-danger">
                                            Please enter a valid phone number!
                                        </Form.Text>}
                                    </Form.Group>

                                    <Button className="btn-grad" onClick={sendCode}>
                                        {loadingSend && <Spinner animation="border" size="sm" />}
                                        Send
                                    </Button>
                                </Form>
                            </Card.Body>
                        </Card>
                    </div>
                </div>
            </div>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header>
                    Sign In
                </Modal.Header>

                <Modal.Body>
                    <Form>
                        <Form.Group className="mb-3" controlId="formBasicEmail">
                            <Form.Label>Enter the code</Form.Label>
                            <Form.Control type="number" placeholder="####" />
                            {/* <Form.Text className="text-muted">
                                            We'll never share your email with anyone else.
                                        </Form.Text> */}
                        </Form.Group>

                        <Button className="btn-grad" onClick={handleClose}>
                            Login
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </>
    )
}
