import React, { useCallback } from "react";
import togoLogo from "../../assets/logo.png"
import { FaHome, FaEnvelope, FaMoneyCheckAlt, FaSignOutAlt } from "react-icons/fa"
import "./AdminPanel.css"
import { GetAllActiveOrders, GetAllClients, GetAllFinishedOrders, GetAllNewOrders, GetAllTransporters, getRecordsActions, getTotalBalance, getLastOrderId } from "../../APIs/AdminPanelApis";
import { useEffect, useState } from "react";
import { ResponsivePie } from '@nivo/pie';
import OrdersCard from "./OrdersCard";
import UsersCard from "./UserCard";
import RecordsCard from "./RecordsCard";
import SignIn from "./SignIn";
import { Button } from "react-bootstrap";
import { refreshPage } from "../../Functions/CommonFunctions";

import { io } from "socket.io-client";

import { useDispatch } from "react-redux";
import { toastNotification } from "../../Actions/GeneralActions";

//https://dribbble.com/shots/15254181-CRM-Dashboard-Managency
//https://dribbble.com/shots/16485287-Trustland-Logistic-Management-App

export default function AdminPanel() {

    let dispatch = useDispatch();

    /* useEffect(() => {
        const initialValue = document.body.style.zoom;

        // Change zoom level on mount
        document.body.style.zoom = "150%";

        return () => {
            // Restore default value
            document.body.style.zoom = initialValue;
        };
    }, []); */

    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        }
    }

    const [orderStat, setOrderStat] = useState([]);
    const [userStat, setUserStat] = useState([]);
    const [newOrders, setNewOrders] = useState([]);
    const [activeOrders, setActiveOrders] = useState([]);
    const [finishedOrders, setFinishedOrders] = useState([]);
    const [activeOrdersNum, setActiveOrdersNum] = useState(0);
    const [records, setRecords] = useState([]);
    const [creditAmount, setCreditAmount] = useState(0);
    const [totalEscrowBalance, setTotalEscrowBalance] = useState(0);
    const [allTransporters, setAllTransporters] = useState([]);
    const [allClients, setAllClients] = useState([]);
    const [lastorderId, setLastorderId] = useState();

    const [socket, setSocket] = useState(null);
    const [onlineUsers, setOnlineUsers] = useState([]);
    // const [initiate, setInitiate] = useState(false);

    useEffect(() => {
        setSocket(io("http://46.253.93.4:5000"));
        // console.log(socket)
        // setInitiate(true)

        socket?.on("getOnlineUsers", (onlineUsers) => {
            setOnlineUsers(onlineUsers)
            console.log(onlineUsers);
        });
    }, [])

    useEffect(() => {
        // if (initiate)
        socket?.on("userStatus", (title, msg, type, onlineUsers) => {
            setOnlineUsers(onlineUsers)
            console.log(onlineUsers);
            dispatch(toastNotification(title, msg, type));
        });
    }, [socket])

    /* get all data */
    const fetchData = useCallback(() => {
        getLastOrderId().then((res) => {
            setLastorderId(res.data);
        })

        getRecordsActions().then((res) => {
            setRecords(res.data.records_list);
        });

        getTotalBalance().then((res) => {

            setTotalEscrowBalance(res.data.server_response.data.result.differ_balance);
            setCreditAmount(res.data.server_response.data.result.credit_amount);
        });

        GetAllNewOrders().then((res) => {
            setNewOrders(res.data.orders_list);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "new",
                    "label": "New",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });

        GetAllActiveOrders().then((res) => {
            setActiveOrders(res.data.orders_list);
            setActiveOrdersNum(res.data.NumberOfOrders);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "active",
                    "label": "Active",
                    "value": res.data.NumberOfOrders,
                }]
            });
        });

        GetAllFinishedOrders().then((res) => {
            setFinishedOrders(res.data.orders_list);
            setOrderStat((prev) => {
                return [...prev, {
                    "id": "finished",
                    "label": "Finished",
                    "value": res.data.NumberOfOrders,
                }]
            });

        });

        GetAllTransporters().then((res) => {
            setAllTransporters(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "transporter",
                    "label": "Transporters",
                    "value": res.data.NumberOfTransporters,
                }]
            });
        });

        GetAllClients().then((res) => {
            setAllClients(res.data);
            setUserStat((prev) => {
                return [...prev, {
                    "id": "client",
                    "label": "Clients",
                    "value": res.data.NumberOfClients,
                }]
            });
        });
    })

    const cleanUp = () => {
        setOrderStat([]);
        setUserStat([]);
        setNewOrders([]);
        setActiveOrders([]);
        setFinishedOrders([]);
        setActiveOrdersNum(0);
        setRecords([]);
        setCreditAmount(0);
        setTotalEscrowBalance(0);
        setAllTransporters([]);
        setAllClients([]);
        setLastorderId();
    }

    /* useEffect(() => {
        console.log("users status updated")
    }, [onlineUsers]) */

    /* get all the data when the admin is logged in */
    /* useEffect(() => {
        if (localStorage.getItem("signedin") === "true" && localStorage.getItem('Adminid') != undefined) {
            cleanUp();
            fetchData();
        }
    }, []); */

    useEffect(() => {
        if (localStorage.getItem("signedin") === "true" && localStorage.getItem('Adminid') != undefined) {
            getLastOrderId().then((res) => {
                setLastorderId(res.data);
            })
        }
    }, []);

    useEffect(() => {
        if (localStorage.getItem("signedin") === "true" && localStorage.getItem('Adminid') != undefined) {
            getRecordsActions().then((res) => {
                setRecords(res.data.records_list);
            });
        }
    }, []);

    useEffect(() => {
        if (localStorage.getItem("signedin") === "true" && localStorage.getItem('Adminid') != undefined) {
            getTotalBalance().then((res) => {

                setTotalEscrowBalance(res.data.server_response.data.result.differ_balance);
                setCreditAmount(res.data.server_response.data.result.credit_amount);
            });
        }
    }, []);

    useEffect(() => {
        if (localStorage.getItem("signedin") === "true" && localStorage.getItem('Adminid') != undefined) {

            setOrderStat([]);

            GetAllNewOrders().then((res) => {
                setNewOrders(res.data.orders_list);
                setOrderStat((prev) => {
                    return [...prev, {
                        "id": "new",
                        "label": "New",
                        "value": res.data.NumberOfOrders,
                    }]
                });
            });

            GetAllActiveOrders().then((res) => {
                setActiveOrders(res.data.orders_list);
                setActiveOrdersNum(res.data.NumberOfOrders);
                setOrderStat((prev) => {
                    return [...prev, {
                        "id": "active",
                        "label": "Active",
                        "value": res.data.NumberOfOrders,
                    }]
                });
            });

            GetAllFinishedOrders().then((res) => {
                setFinishedOrders(res.data.orders_list);
                setOrderStat((prev) => {
                    return [...prev, {
                        "id": "finished",
                        "label": "Finished",
                        "value": res.data.NumberOfOrders,
                    }]
                });

            });
        }
    }, []);

    useEffect(() => {
        if (localStorage.getItem("signedin") === "true" && localStorage.getItem('Adminid') != undefined) {

            setUserStat([]);

            GetAllTransporters().then((res) => {

                let tempArr = res.data.transporters_list;

                for (let i = 0; i < tempArr.length; i++) {
                    if (onlineUsers.find(user => user.userId == tempArr[i].id)) {
                        tempArr[i].isActive = true;
                    } else {
                        tempArr[i].isActive = false;
                    }
                    // tempArr[i].isActive = true;
                }

                setAllTransporters(tempArr);
                setUserStat((prev) => {
                    return [...prev, {
                        "id": "transporter",
                        "label": "Transporters",
                        "value": res.data.NumberOfTransporters,
                    }]
                });
            });

            GetAllClients().then((res) => {

                let tempArr = res.data.clients_list;

                for (let i = 0; i < tempArr.length; i++) {
                    if (onlineUsers.find(user => user.userId == tempArr[i].id)) {
                        tempArr[i].isActive = true;
                    } else {
                        tempArr[i].isActive = false;
                    }
                    // tempArr[i].isActive = true;
                }

                setAllClients(tempArr);
                setUserStat((prev) => {
                    return [...prev, {
                        "id": "client",
                        "label": "Clients",
                        "value": res.data.NumberOfClients,
                    }]
                });
            });
        }
    }, [onlineUsers]);

    /* get current time to greet user */
    let myDate = new Date();
    let hrs = myDate.getHours();

    let greet;

    if (hrs < 12)
        greet = 'Good Morning';
    else if (hrs >= 12 && hrs <= 17)
        greet = 'Good Afternoon';
    else if (hrs >= 17 && hrs <= 24)
        greet = 'Good Evening';

    /* display login page when admin is not logged in */
    if (localStorage.getItem("signedin") !== "true" && localStorage.getItem('Adminid') == undefined) {
        return <SignIn onSuccess={() => {

            // const socket = io("http://46.253.93.4:5000");
            /* socket?.emit("login", localStorage.getItem('Adminid'), "admin");

            setTimeout(() => {
                refreshPage()
            }, 500); */

            refreshPage();
        }} />
    }

    return (
        <>
            <nav className="navbar navbar-light shadow" style={{ backgroundColor: "white" }}>
                <div className="container-fluid">
                    <a className="navbar-brand d-flex justify-content-between" style={{ cursor: "pointer" }}>
                        <img src={togoLogo} style={{ width: "100px" }} alt="logo" />
                        <span className="forest-color mt-3 ms-3">Admin Panel</span>
                    </a>
                    <div className="d-flex justify-content-between">
                        <span className="forest-color mt-1 me-3 d-flex justify-content-between" style={{ fontSize: "1.2rem" }}>
                            {greet === "Good Morning" ? <svg style={{ marginTop: "2px", marginRight: "5px", color: "yellow" }} xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-brightness-high" viewBox="0 0 16 16">
                                <path d="M8 11a3 3 0 1 1 0-6 3 3 0 0 1 0 6zm0 1a4 4 0 1 0 0-8 4 4 0 0 0 0 8zM8 0a.5.5 0 0 1 .5.5v2a.5.5 0 0 1-1 0v-2A.5.5 0 0 1 8 0zm0 13a.5.5 0 0 1 .5.5v2a.5.5 0 0 1-1 0v-2A.5.5 0 0 1 8 13zm8-5a.5.5 0 0 1-.5.5h-2a.5.5 0 0 1 0-1h2a.5.5 0 0 1 .5.5zM3 8a.5.5 0 0 1-.5.5h-2a.5.5 0 0 1 0-1h2A.5.5 0 0 1 3 8zm10.657-5.657a.5.5 0 0 1 0 .707l-1.414 1.415a.5.5 0 1 1-.707-.708l1.414-1.414a.5.5 0 0 1 .707 0zm-9.193 9.193a.5.5 0 0 1 0 .707L3.05 13.657a.5.5 0 0 1-.707-.707l1.414-1.414a.5.5 0 0 1 .707 0zm9.193 2.121a.5.5 0 0 1-.707 0l-1.414-1.414a.5.5 0 0 1 .707-.707l1.414 1.414a.5.5 0 0 1 0 .707zM4.464 4.465a.5.5 0 0 1-.707 0L2.343 3.05a.5.5 0 1 1 .707-.707l1.414 1.414a.5.5 0 0 1 0 .708z" />
                            </svg> : greet === "Good Afternoon" ? <svg style={{ marginTop: "2px", marginRight: "5px", color: "orange" }} xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-brightness-alt-high" viewBox="0 0 16 16">
                                <path d="M8 3a.5.5 0 0 1 .5.5v2a.5.5 0 0 1-1 0v-2A.5.5 0 0 1 8 3zm8 8a.5.5 0 0 1-.5.5h-2a.5.5 0 0 1 0-1h2a.5.5 0 0 1 .5.5zm-13.5.5a.5.5 0 0 0 0-1h-2a.5.5 0 0 0 0 1h2zm11.157-6.157a.5.5 0 0 1 0 .707l-1.414 1.414a.5.5 0 1 1-.707-.707l1.414-1.414a.5.5 0 0 1 .707 0zm-9.9 2.121a.5.5 0 0 0 .707-.707L3.05 5.343a.5.5 0 1 0-.707.707l1.414 1.414zM8 7a4 4 0 0 0-4 4 .5.5 0 0 0 .5.5h7a.5.5 0 0 0 .5-.5 4 4 0 0 0-4-4zm0 1a3 3 0 0 1 2.959 2.5H5.04A3 3 0 0 1 8 8z" />
                            </svg> : <svg style={{ marginTop: "2px", marginRight: "5px", color: "blue" }} xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="currentColor" className="bi bi-moon-stars" viewBox="0 0 16 16">
                                <path d="M6 .278a.768.768 0 0 1 .08.858 7.208 7.208 0 0 0-.878 3.46c0 4.021 3.278 7.277 7.318 7.277.527 0 1.04-.055 1.533-.16a.787.787 0 0 1 .81.316.733.733 0 0 1-.031.893A8.349 8.349 0 0 1 8.344 16C3.734 16 0 12.286 0 7.71 0 4.266 2.114 1.312 5.124.06A.752.752 0 0 1 6 .278zM4.858 1.311A7.269 7.269 0 0 0 1.025 7.71c0 4.02 3.279 7.276 7.319 7.276a7.316 7.316 0 0 0 5.205-2.162c-.337.042-.68.063-1.029.063-4.61 0-8.343-3.714-8.343-8.29 0-1.167.242-2.278.681-3.286z" />
