import React, { useRef, useState } from 'react';
import ImageUploading from 'react-images-uploading';
import AvatarEditor from 'react-avatar-editor';
import { Modal, Button, Form } from 'react-bootstrap';


export default function UploadAndEditImage({ currentImage, setImage, setImageToUpload, imageBorderRadius, imageHeight, imageWidth }) {

    // console.log(currentImage)

    const [images, setImages] = useState([]);
    const maxNumber = 1;
    const [show, setShow] = useState(false);
    const [rotate, setRotate] = useState(0);
    const [size, setSize] = useState(1.0);
    const [borderRadius, setBorderRadius] = useState(0);
    const editor = useRef(null);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const onChange = (image) => {
        setImages(image);
    };

    const handleRotate = (event) => {
        setRotate(parseInt(event.target.value));
    };

    const handleSize = (event) => {
        setSize(parseFloat(event.target.value));
    };

    const handleBorderRadius = (event) => {
        setBorderRadius(parseInt(event.target.value));
    };

    const getImageUrl = async () => {
        const dataUrl = editor.current.getImage().toDataURL()
        const result = await fetch(dataUrl);
        const blob = await result.blob();

        const urlCreator = window.URL || window.webkitURL;
        const imageUrl = urlCreator.createObjectURL(blob);

        return imageUrl;
    }

    const getImageUrlToUpload = async () => {
        const dataUrl = editor.current.getImage().toDataURL()
        const result = await fetch(dataUrl);
        const blob = await result.blob();

        let data = new FormData();
        data.append('image', blob, 'image.png');

        for (var key of data.entries()) {
            console.log(key[0] + ', ' + key[1]);
        }

        // console.log(data.entries())

        return data;
    }

    return (
        <>
            <Button variant="primary" onClick={() => { handleShow(); setImages([{ "data_url": currentImage }]) }} className="btn-grad-circle" style={{
                position: "absolute",
                right: 0,
                bottom: 0,
                height: "50px",
                width: "50px",
            }}>
                <i className="bi bi-pencil-fill h4"></i>
            </Button>

            <Modal show={show} onHide={handleClose} style={{ backgroundColor: "rgba(0, 0, 0, 0.7)" }}>
                <Modal.Header closeButton>
                    <Modal.Title>Upload Image</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <ImageUploading
                        multiple
                        value={images}
                        onChange={onChange}
                        maxNumber={maxNumber}
                        dataURLKey="data_url"
                    >
                        {({
                            imageList,
                            onImageUpload,
                            // onImageRemoveAll,
                            onImageUpdate,
                            onImageRemove,
                            isDragging,
                            dragProps,
                        }) => (
                            // write your building UI
                            <div className="upload__image-wrapper">
                                {images.length == 0 && <Button
                                    className='w-100'
                                    style={{ height: "300px" }}
                                    variant={isDragging ? "secondary" : "outline-secondary"}
                                    onClick={onImageUpload}
                                    {...dragProps}
                                >
                                    Click or Drop here <i className="bi bi-card-image ms-2"></i>
                                </Button>}
                                {imageList.map((image, index) => (
                                    <div key={index} className="image-item">
                                        <div className="image-item__btn-wrapper w-100 d-flex justify-content-center mb-2">
                                            <Button variant="outline-primary" className='w-25 me-1 rounded-pill' onClick={() => onImageUpdate(index)}>Update</Button>
                                            <Button variant="outline-danger" className='w-25 rounded-pill' onClick={() => onImageRemove(index)}>Remove</Button>
                                        </div>
                                        <div>
                                            <AvatarEditor
                                                ref={editor}
                                                image={image['data_url']}
                                                width={imageWidth}
                                                height={imageHeight}
                                                border={50}
                                                borderRadius={imageBorderRadius}
                                                scale={size}
                                                rotate={rotate}
                                            />
                                            <Form.Label className="mt-2">Rotate: {rotate}Â°</Form.Label>
                                            <Form.Range value={rotate} min="-180" max="180" step="1" onChange={handleRotate} />
                                            <Form.Label>Size: {size}</Form.Label>
                                            <Form.Range value={size} min="1" max="10" step="0.1" onChange={handleSize} />
                                            {/* <Form.Label>Border Radius: {borderRadius}</Form.Label>
                                            <Form.Range value={borderRadius} min="0" max="250" step="1" onChange={handleBorderRadius} /> */}
                                            <Button onClick={() => { setRotate(0); setSize(1); setBorderRadius(0) }}>Reset</Button>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </ImageUploading>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="outline-secondary" onClick={handleClose}>
                        Cancel <i className="bi bi-x"></i>
                    </Button>
                    <Button variant="outline-success" onClick={async () => {
                        handleClose();
                        if (editor) {
                            // This returns a HTMLCanvasElement, it can be made into a data URL or a blob,
                            // drawn on another canvas, or added to the DOM.

                            // const canvas = editor.current.getImage()
                            // console.log(editor.current.state.image.resource.currentSrc)

                            // console.log("------------------------")

                            // setImage(editor.current.state.image.resource.currentSrc);
                            const imageURL = await getImageUrl();
                            setImage(imageURL);

                            const imageToUpload = await getImageUrlToUpload();
                            setImageToUpload(imageToUpload);

                            // console.log(getImageUrl());

                            // If you want the image resized to the canvas size (also a HTMLCanvasElement)

                            // const canvasScaled = editor.current.getImageScaledToCanvas()
                            // console.log(canvasScaled)
                        }
                    }}>
                        Save <i className="bi bi-check-all"></i>
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
