/* ###################################################################################################################### */

import React, { useRef, useState } from 'react';
import ImageUploading from 'react-images-uploading';
import AvatarEditor from 'react-avatar-editor';
import { Modal, Button, Form } from 'react-bootstrap';


export default function App() {
    const [images, setImages] = React.useState([]);
    const maxNumber = 1;
    const [show, setShow] = useState(false);
    const [rotate, setRotate] = useState(0);
    const [size, setSize] = useState(0);
    const editor = useRef(null);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const onChange = (image) => {
        // data for submit
        // console.log(imageList, addUpdateIndex);
        setImages(image);
    };

    const handleRotate = (event) => {
        setRotate(parseInt(event.target.value));
    };

    const handleSize = (event) => {
        setSize(parseInt(event.target.value));
    };

    return (
        <>
            <Button variant="primary" onClick={handleShow}>
                Launch demo modal
            </Button>

            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <ImageUploading
                        multiple
                        value={images}
                        onChange={onChange}
                        maxNumber={maxNumber}
                        dataURLKey="data_url"
                    >
                        {({
                            imageList,
                            onImageUpload,
                            // onImageRemoveAll,
                            onImageUpdate,
                            onImageRemove,
                            isDragging,
                            dragProps,
                        }) => (
                            // write your building UI
                            <div className="upload__image-wrapper">
                                {images.length == 0 && <Button
                                    className='w-100'
                                    style={{ height: "300px" }}
                                    variant={isDragging ? "secondary" : "outline-secondary"}
                                    // style={isDragging ? { color: 'lightgray' } : undefined}
                                    onClick={onImageUpload}
                                    {...dragProps}
                                >
                                    Click or Drop here <i className="bi bi-card-image ms-2"></i>
                                </Button>}
                                {/* <button onClick={onImageRemoveAll}>Remove all images</button> */}
                                {imageList.map((image, index) => (
                                    <div key={index} className="image-item">
                                        {/* <img src={image['data_url']} alt="" width="100" />
                                <div className="image-item__btn-wrapper">
                                    <button onClick={() => onImageUpdate(index)}>Update</button>
                                    <button onClick={() => onImageRemove(index)}>Remove</button>
                                </div> */}
                                        <div className="image-item__btn-wrapper w-100 d-flex justify-content-center mb-2">
                                            <Button variant="outline-primary" className='w-25 me-1 rounded-pill' onClick={() => onImageUpdate(index)}>Update</Button>
                                            <Button variant="outline-danger" className='w-25 rounded-pill' onClick={() => onImageRemove(index)}>Remove</Button>
                                        </div>
                                        <div>
                                            <AvatarEditor
                                                ref={editor}
                                                image={image['data_url']}
                                                width={250}
                                                height={250}
                                                border={50}
                                                scale={size}
                                                rotate={rotate}
                                            />
                                            <Form.Label>Rotate: {rotate}Â°</Form.Label>
                                            <Form.Range value={rotate} min="-180" max="180" onChange={handleRotate} />
                                            <Form.Label>Size: {size}</Form.Label>
                                            <Form.Range value={size} min="0" max="100" onChange={handleSize} />
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </ImageUploading>
                </Modal.Body>
                <Modal.Footer>
                    <Button variant="outline-secondary" onClick={handleClose}>
                        Cancel <i className="bi bi-x"></i>
                    </Button>
                    <Button variant="outline-success" onClick={() => {
                        handleClose();
                        if (editor) {
                            // This returns a HTMLCanvasElement, it can be made into a data URL or a blob,
                            // drawn on another canvas, or added to the DOM.
                            const canvas = editor.current.getImage()
                            console.log(canvas)

                            console.log("------------------------")

                            // If you want the image resized to the canvas size (also a HTMLCanvasElement)
                            const canvasScaled = editor.current.getImageScaledToCanvas()
                            console.log(canvasScaled)
                        }
                    }}>
                        Save <i className="bi bi-check-all"></i>
                    </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
