import translate from "../i18n/translate";
import React, { useEffect, useState } from "react";
import { Button, Col, Form, Modal, Row, FloatingLabel, Spinner } from "react-bootstrap";
import { CreateAddressReq, GetCityRegion } from "./APIs/OrdersAPIs";

export default function CreateAddress({onSuccess, children}){

    const [show, setShow] = useState(false);
    const [cities, setCities] = useState([]);

    useEffect(() => {
        //if(show === true) {
            GetCityRegion().then((res) => {
                setCities(res.data.server_response);
            });
        //}
    }, []);
    
    const [loading, setLoading] = useState(false);
    const [validated, setValidated] = useState(false);

    return (
        <div>
            <span onClick={()=>{
                setShow(true);
            }}>
            
                {children}
            </span>
            <Modal show={show} onHide={()=>{setShow(false)}} centered animation={false} backdrop="static" fullscreen>
                <Modal.Header closeButton>
                    <Modal.Title>{translate("ORDERS.ADD_ADDRESS")}</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                    <Form id="addressForm" validated={validated} noValidate /* ref={formRef} */ onSubmit={(event)=>{
                        event.preventDefault();
                        event.stopPropagation();
                        const formData = new FormData(event.target),
                        formDataObj = Object.fromEntries(formData.entries())
                        const form = event.currentTarget;
                        if (form.checkValidity() === true) {
                            setLoading(true);
                            CreateAddressReq(formDataObj).then(()=>{
                                setLoading(false);
                                setShow(false);
                                onSuccess && onSuccess();
                            });
                        }
                        setValidated(true);
                        console.log(formDataObj);
                        
                    }}>
                        
                        <FloatingLabel className="mb-3" controlId="formBasicEmail" label={translate("ORDERS.ADDRESS_NAME")}>
                            <Form.Control type="text" placeholder="..." name="placename" required />
                            <Form.Control.Feedback type="invalid">
                                Please add place name
                            </Form.Control.Feedback>
                        </FloatingLabel>
                        
                        <FloatingLabel className="mb-3" controlId="userTel" label={translate("ORDERS.ADDRESS_PHONE")}>
                            <Form.Control type="tel" placeholder="..." name="phone" pattern="^05[0-9]{8}?$" required/>
                            <Form.Control.Feedback type="invalid">
                                Please enter a valid phone number example 0568000000
                            </Form.Control.Feedback>
                        </FloatingLabel>
                        
                        <FloatingLabel className="mb-3" controlId="AddressCity" label={translate("ORDERS.ADDRESS_CITY")}>
                            <Form.Select name="city">
                                {cities?.map?.((city)=>{
                                    return <option key={city.IdCity} value={city.IdCity}>{city.Name}</option>
                                })}
                            </Form.Select>
                        </FloatingLabel>

                        <FloatingLabel className="mb-3" controlId="userAddress" label={translate("ORDERS.ADDRESS")}>
                            <Form.Control type="text" placeholder="..." name="address" />
                        </FloatingLabel>

                        <FloatingLabel className="mb-3" controlId="addressInfo" label={translate("ORDERS.ADDRESS_INFO")}>
                            <Form.Control type="text" placeholder="..." name="addressinfo" />
                        </FloatingLabel>
                        <Row>
                            <Col md>
                            <FloatingLabel className="mb-3" controlId="addCountry" label={translate("ORDERS.ADDRESS_COUNTRY")}>
                                <Form.Control type="text" placeholder="..." name="country" />
                            </FloatingLabel>
                            </Col>
                            <Col>
                            <FloatingLabel className="mb-3" controlId="addressCode" label={translate("ORDERS.ADDRESS_CODE")}>
                                <Form.Control type="text" placeholder="..." name="zipcode" />
                            </FloatingLabel>
                            </Col>
                        </Row>
                        
                        <Form.Check 
                            type="switch"
                            id="custom-switch"
                            label={translate("ORDERS.ADDRESS_SHARED")}
                            name="isShared"
                        />

                        <Button variant="primary" type="submit">
                            {loading && <Spinner size="sm" className="me-1" animation="border" variant="light" />}

                            {translate("GENERAL.PROCEED")}
                        </Button>
                    </Form>
                </Modal.Body>
            </Modal>
        </div>
    )
}
