import React, { useEffect, useState } from "react";
import './CustomerInfo.css';
import { Container, Row, Col, Card, Table, Button } from 'react-bootstrap';
import { GetTransporterPersonalInfo } from "../../APIs/AdminPanelApis";

function TransporterPersonalInfoDisplay(props) {

    console.log(props)

    return (
        <>
            
        </>
    )
}

function ClientPersonalInfoDisplay() {
    return (
        <>
            Client
        </>
    )
}

export default function CustomerInfo(props) {

    // console.log(props);

    const [transporterPersonalInfo, setTransporterPersonalInfo] = useState();

    useEffect(() => {

        if (props.isTransporter) {
            GetTransporterPersonalInfo(props.customerId).then((res) => {
                console.log(res.data);
                setTransporterPersonalInfo(res.data);
            })
        }

    }, [])

    return (
        <>
            <div onClick={props.handleClose} className="d-flex justify-content-start backBtn">
                <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="currentColor" className="bi bi-caret-left-fill" viewBox="0 0 16 16" >
                    <path d="m3.86 8.753 5.482 4.796c.646.566 1.658.106 1.658-.753V3.204a1 1 0 0 0-1.659-.753l-5.48 4.796a1 1 0 0 0 0 1.506z" />
                </svg>
                <span>Back</span>
            </div>

            {/* upper background */}
            <div className="upperBackground">
            </div>

            <div className="mainContainer">
                {props.isTransporter ? <TransporterPersonalInfoDisplay info={transporterPersonalInfo} /> : <ClientPersonalInfoDisplay />}
            </div>
        </>
    )
}
