import React from "react";
import "./AdminPanel.css"
import { GetAllActiveOrders, GetAllClients, GetAllFinishedOrders, GetAllReturnedOrders, GetAllNewOrders, GetAllTransporters, getRecordsActions, getTotalBalance, getLastOrderId, getLastRecord, updateAdminWebNotificationToken, getAllBalanceChargeActions/* , testchargeBalance */, GetAllClientsNum, GetAllTransportersNum } from "../../APIs/AdminPanelApis";
import { useEffect, useState } from "react";
import { ResponsivePie } from '@nivo/pie';
import OrdersCard from "./OrdersCard";
import UsersCard from "./UserCard";
import RecordsCard from "./RecordsCard";
import TransfersCard from "./TransfersCard";
import { useDispatch } from "react-redux";
import { useHistory } from "react-router";
import 'bootstrap-icons/font/bootstrap-icons.css';
import { Button } from 'react-bootstrap';

//https://dribbble.com/shots/15254181-CRM-Dashboard-Managency
//https://dribbble.com/shots/16485287-Trustland-Logistic-Management-App

export default function AdminPanel() {

    const history = useHistory();

    let dispatch = useDispatch();

    const styles = {
        cardHeaderLg: {
            position: 'absolute',
            left: '20px',
            right: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        },
        cardHeaderSm: {
            position: 'absolute',
            left: '20px',
            top: '-20px',
            background: "linear-gradient(90deg, #26a69a, #69d4a5)",
            color: 'white',
            borderRadius: '10px',
            fontWeight: 'bold'
        }
    }

    const [orderStat, setOrderStat] = useState([]); // for orders stat. 
    const [userStat, setUserStat] = useState([]); // for users stat.
    const [newOrders, setNewOrders] = useState([]); // all newly created orders
    const [activeOrders, setActiveOrders] = useState([]); // all in process orders
    const [finishedOrders, setFinishedOrders] = useState([]); // all finished/deleted orders
    const [exceptionOrders, setExceptionOrders] = useState([]); // all stuck/returned orders
    const [activeOrdersNum, setActiveOrdersNum] = useState(0); // number of active orders
    const [records, setRecords] = useState([]); // all system actions
    const [rechargeBalaneRecords, setRechargeBalaneRecords] = useState([]); // all balance recharge actions
    const [creditAmount, setCreditAmount] = useState(0); // total escrow, all system balance
    const [totalEscrowBalance, setTotalEscrowBalance] = useState(0); // total in escrow, active money
    /* const [allTransporters, setAllTransporters] = useState([]); // all transporters
    const [allClients, setAllClients] = useState([]); // all clients */
    const [lastorderId, setLastorderId] = useState(); // last action record id, from system actions
    const [totalClients, setTotalClinets] = useState(); // last action record id, from system actions
    const [totalTransporters, setTotalTransporters] = useState(); // last action record id, from system actions
    
    const [returnedOrdersActiveClass, setReturnedOrdersActiveClass] = useState("active-tab");
    const [finishedOrdersActiveClass, setfinishedOrdersActiveClass] = useState("");

    /* get last order id as number of total orders */
    useEffect(() => {
        getLastOrderId().then((res) => {
            setLastorderId(res.data);
        })
    }, []);

    /* get system actions */
    useEffect(() => {
        getRecordsActions().then((res) => {
            setRecords(res.data.records_list);
        });
    }, []);

    /* get total balance */
    useEffect(() => {
        getTotalBalance().then((res) => {

            setTotalEscrowBalance(res.data.server_response.data?.result.differ_balance);
            setCreditAmount(res.data.server_response.data?.result.credit_amount);
        });
    }, []);

    useEffect(() => {
        getAllBalanceChargeActions().then((res) => {
            console.log(res.data.recharges_list);
            setRechargeBalaneRecords(res.data.recharges_list);
        })
    }, [])

    /* get all orders */
    useEffect(() => {
        // get all new orders
        GetAllNewOrders().then((res) => {
            setNewOrders(res.data.orders_list);
        });

        // get all in-process orders
        GetAllActiveOrders().then((res) => {
            setActiveOrders(res.data.orders_list);
            setActiveOrdersNum(res.data.NumberOfOrders);
        });

        // get all stuck/returned orders
        GetAllReturnedOrders().then((res) => {
            setExceptionOrders(res.data.orders_list);
        });
    }, []);

    useEffect(() => {
        // get all transporters count
        GetAllTransportersNum().then((res) => {
            setTotalTransporters(res.data.NumberOfTransporters);
        });

        // get all clients count
        GetAllClientsNum().then((res) => {
            setTotalClinets(res.data.NumberOfClients);
        });
    }, []);

    const toggleTabes = () => {
        if (finishedOrdersActiveClass == "active-tab") {
            setfinishedOrdersActiveClass("");
            setReturnedOrdersActiveClass("active-tab");
        } else {
            setfinishedOrdersActiveClass("active-tab");
            setReturnedOrdersActiveClass("");
        }
    }

    return (
        <>
            <div className="statDiv">
                <div className="container-fluid h-100">
                    <div className='row h-100 mt-1'>
                        <div className='col rightBorder'>
                            <div className='row h-50'>
                                <div className='col'>Active Packages:</div>
                                <div className='col togo-dark-green'>{activeOrdersNum}</div>
                            </div>
                            <div className='row h-50'>
                                <div className='col'>Total Packages:</div>
                                <div className='col togo-dark-green'>{lastorderId}</div>
                            </div>
                        </div>
                        <div className='col rightBorder'>
                            <div className='row h-50'>
                                <div className='col'>Total Clients:</div>
                                <div className='col togo-dark-green'>{totalClients}</div>
                            </div>
                            <div className='row h-50'>
                                <div className='col'>Total Transporters:</div>
                                <div className='col togo-dark-green'>{totalTransporters}</div>
                            </div>
                        </div>
                        <div className='col'>
                            <div className='row h-50'>
                                <div className='col'>Total in Escrow:</div>
                                <div className='col togo-dark-green'>{totalEscrowBalance}{" NIS"}</div>
                            </div>
                            <div className='row h-50'>
                                <div className='col'>Total Escrow:</div>
                                <div className='col togo-dark-green'>{creditAmount}{" NIS"}</div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            {/* <button onClick={() => {testchargeBalance(40, 50)}}>test</button> */}
            <div className="d-flex w-100 panel-wrapper" style={{ backgroundColor: "#eaeced" }}>
                {/* <div
                    className="bg-white mt-3 mb-3 ms-3 rounded-22 shadow admin-left-menu"
                >

                    <div className=" h-100">
                        <RecordsCard title="Records Log Table" list={records} />
                    </div>

                </div> */}
                {/* <div className="w-100 ps-3" style={{ overflowX: "auto" }}>
                    <div className="w-100 d-flex">
                        <div
                            className="bg-white me-3 mt-3 rounded-22 shadow togo-chart"
                        >
                            <ResponsivePie
                                data={orderStat}
                                margin={{ top: 40, right: 80, bottom: 80, left: 80 }}
                                innerRadius={0.5}
                                padAngle={0.7}
                                cornerRadius={3}
                                activeOuterRadiusOffset={8}
                                borderWidth={1}
                                borderColor={{
                                    from: 'color',
                                    modifiers: [
                                        [
                                            'darker',
                                            0.2
                                        ]
                                    ]
                                }}
                                arcLinkLabelsSkipAngle={10}
                                arcLinkLabelsTextColor="#333333"
                                arcLinkLabelsThickness={2}
                                arcLinkLabelsColor={{ from: 'color' }}
                                arcLabelsSkipAngle={10}
                                arcLabelsTextColor={{
