import React from "react";
import { motion } from "framer-motion";
import { Badge } from "react-bootstrap";

export default function OrderActions({ actions }) {

    const formatAction = (record) => {

        const tempHolder = <div style={{ width: "40%" }}>
            <div style={{ fontWeight: "bold", color: "#03A65A" }}>{record.description}</div>
            <div>{record.time_stamp.split(" ")[0] + " "}<Badge bg="primary">{record.time_stamp.split(" ")[1]}</Badge></div>
        </div>

        switch (record.action_id) {
            case "2":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}><span style={{ fontStyle: "italic" }}>{record.party_one_name}</span>{" created order " + record.order_id}</div>
                </div>;
            case "3":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Transporter " + record.party_one_name + " offered " + record.price + "NIS to order " + record.order_id}</div>
                </div>;
            case "4":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Client " + record.party_one_name + " cancel order " + record.order_id}</div>
                </div>;
            case "5":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Trasnporter " + record.party_one_name + " updated order " + record.order_id + " to " + record.price + "NIS"}</div>
                </div>;
            case "6":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Transporter " + record.party_one_name + " canceled bid on order " + record.order_id}</div>
                </div>;
            case "7":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Client " + record.party_one_name + " accepted " + record.party_two_name + "'s bid on order " + record.order_id}</div>
                </div>;
            case "8":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Transporter " + record.party_one_name + " picked up order " + record.order_id}</div>
                </div>;
            case "9":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Transporter " + record.party_one_name + " finished order " + record.order_id}</div>
                </div>;
            case "10":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " assigned order " + record.order_id + " to " + record.party_two_name}</div>
                </div>;
            case "11":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Transporter " + record.party_one_name + " rejected order " + record.order_id}</div>
                </div>;
            case "12":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Transporter " + record.party_one_name + " accepted order " + record.order_id}</div>
                </div>;
            case "17":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{record.party_one_name + " canceled assign on order " + record.order_id}</div>
                </div>;
            case "18":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Client " + record.party_one_name + " created and assigned order " + record.order_id + " to " + record.party_two_name}</div>
                </div>;
            case "19":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Transporter " + record.party_one_name + " marked order " + record.order_id + " as stuck"}</div>
                </div>;
            case "20":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Transporter " + record.party_one_name + " marked order " + record.order_id + " as returned"}</div>
                </div>;
            case "21":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Client " + record.party_one_name + " accepted returned order " + record.order_id}</div>
                </div>;
            case "22":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Client " + record.party_one_name + " rejected returned order " + record.order_id}</div>
                </div>;
            case "23":
                return <div className="d-flex justify-content-between">
                    {tempHolder}
                    <div style={{ width: "70%" }}>{"Admin " + record.party_one_name + " canceled order " + record.order_id}</div>
                </div>;
        }
    }

    return (
        <>
            {actions?.map((record, index) => {
                return (<div key={index}>
                    <motion.div
                        key={index}
                        initial={{ backgroundColor: "#fff" }}
                        whileHover={{ backgroundColor: "#f0fff0" }}
                        whileTap={{ scale: 0.95 }}
                        className="align-items-center d-flex gap-3 mb-2 w-100"
                    >
                        {formatAction(record)}

                    </motion.div>
                    <hr style={{ marginRight: "10px" }} />
                </div>
                )
            })}
        </>
    )
}
