// dynamically generate navbar tabs given a list of tab names
function SupersetComponent() {
    tabs = [
        {
            eventKey: 'home',
            title: 'Home',
        },
        {
            eventKey: 'profile',
            title: 'Profile',
        },
        {
            eventKey: 'contact',
            title: 'Contact',
            disabled: true,
        }
    ]
    return (
        <div>
            <Tabs defaultActiveKey="home" id="uncontrolled-tab-example">
                {tabs.map((tab, index) => (
                    <Tab eventKey={index} key={index} title={tab.title} />
                ))}
            </Tabs>
        </div>
    )
}



export default SupersetComponent;
